/*********************************************************
 * Global
 *********************************************************/

a:link {
    color: #30a4e0;
}


a:visited {
    color: #8f30e0;
}
html, body {
	margin: 0;
	padding: 0;
}
body {background:url(http://i.imgur.com/wlHm7B4.png);color: white;font-size: 11pt;font-family: Joy, Verdana, Helvetica, Arial, sans-serif;
}.optlabel {font-size: 9pt;display: block;
}.textbox {border: 1px solid #AAAAAA;border-radius: 3px;padding: 2px 3px;font-family: Joy, Verdana, Helvetica, Arial, sans-serif;font-size: 9pt;box-shadow: inset 0px 1px 2px #CCCCCC, 1px 1px 0 rgba(255,255,255,.6);background: #F8FBFD;color: black;
}.textbox:hover {border-color: #474747;box-shadow: inset 0px 1px 2px #D2D2D2, 1px 1px 0 rgba(255,255,255,.6);background: #FFFFFF;
}.textbox:focus {outline: 0 none;border: 1px solid #004488;box-shadow: inset 0px 1px 2px #D2D2D2, 0px 0px 5px #2266AA;background: #FFFFFF;
}a.subtle {text-decoration: none;color: black;
}a.subtle:hover {text-decoration: underline;color: #6688CC;
}.buttonbar {margin-top: 1em;text-align: center;
}

/*********************************************************
 * Header
 *********************************************************/

i.fa.fa-user {display:none;}

.header {position: relative;height: 50px;background: url(https://dl.dropbox.com/s/1xzlgc7i5wou3cb/TRY%20DAT%20AGAIN.png?dl=0);
    
}.header:hover {position: relative;height: 50px;background: url(https://dl.dropbox.com/s/rhq0eni32njk9ri/test%20hover.png?dl=0);-webkit-transition-delay: 0.3s;-webkit-transition-duration: 0.7s;-webkit-box-shadow: -1px -1px 29px 0px rgba(0,43,255,1);
}.userbar .username {color: black;text-shadow: none;
}.userbar button.icon i {margin: 0 -5px;
}.tabbar {position: absolute;bottom: 0px;left: 0px;right: 0;display: block;list-style: none;margin: 0;padding: 0 0 0 0;height: 35px;text-align: left;color: white;text-shadow: 0 1px 0 black;font-size: 10pt;line-height: 100%;border-top-right-radius: 30px;border-top-left-radius: 30px;
}.maintabbarbottom {content: "";display: block;position: absolute;left: 0;right: 0;bottom: -2px;height: 2px;background: #004ce7;border: solid 0px #004ce7;border-left: 0;margin: -1px 0 0 0;-webkit-box-shadow: 0 1px 2px rgba(0,0,0,.2);-moz-box-shadow: 0 1px 2px rgba(0,0,0,.2);box-shadow: 0 1px 2px rgba(0,0,0,.2);border-top-right-radius: 30px;border-top-left-radius: 30px;
}
.tabbar.maintabbar {margin-left: 250px;margin-right: 165px;overflow: hidden;
}.tabbar li,
.tabbar ul {display: block;float: left;list-style: none;margin: 0;padding: 0;  
}
.tabbar a.button {outline: none;display: inline-block;height: 32px;min-width: 60px;cursor: pointer;text-align: center;text-decoration: none;text-shadow: 0 1px 0 rgba(255,255,255,.4);border-radius: 0px;-webkit-box-shadow: 0 1px 2px rgba(0,0,0,.2), inset 0 -1px 2px rgba(255,255,255,1);-moz-box-shadow: 0 1px 2px rgba(0,0,0,.2), inset 0 -1px 2px rgba(255,255,255,1);box-shadow: 0 1px 2px rgba(0,0,0,.2), inset 0 -1px 2px rgba(255,255,255,1);white-space: nowrap;
	/* default colors */
	color: #707070;text-shadow: 0 0px 0 #707070;border: solid 1px #004ce7;border-left-width: 3px;border-right-width: 3px;
background: #000000;position: relative;padding: 2px 2px 10px 5px;margin: -1px 10px 20px 0;top: 1px;border-radius: 0;box-shadow: none;font-size: 20px;font-family: Joy,  Verdana, Helvetica, Arial, sans-serif;
}
.tabbar a.button i {display: block;text-align: center;margin: 3px auto 0 auto;font-style: normal;font-size: 15px;height: 14px;
 
 
}
.tabbar a.button span {display: block;overflow: hidden;text-align: center;
}
.tabbar a.button i.text {font-size: 12px;font-family: Joy;margin-right: -90px;padding-left: 10;overflow: hidden;font-weight: bold;-webkit-text-shadow: -2px -2px 16px 0px rgba(255,255,255,1);right: 10px;
}
.tabbar a.button i.text + span {margin-right: -10px;
}
.tabbar a.button:hover{color:#f8f8f8;background: #000000;border-color: #004ce7;-webkit-transition-delay: 0.1s;-webkit-transition-duration: 0.1s;-webkit-box-shadow: -1px -1px 29px 0px rgba(0,43,255,1);-webkit-text-shadow: -2px -2px 16px 0px rgba(255,255,255,1);border-top-right-radius: 30px;border-top-left-radius: 30px;
}
.tabbar a.button:active {
  	color:#f8f8f8;
	background: -webkit-gradient(left top, left bottom, color-stop(0%, rgba(20,103,255,1)), color-stop(49%, rgba(20,103,255,1)), color-stop(51%, rgba(0,86,245,1)), color-stop(59%, rgba(0,66,210,1)), color-stop(71%, rgba(0,37,158,1)), color-stop(100%, rgba(0,9,26,1)));border-color: #004ce7;border-top-right-radius: 30px;border-top-left-radius: 30px;

}
.tabbar a.button.notifying {border-color: #004ce7;background: url("https://s.graphiq.com/sites/default/files/2307/media/images/t2/Royal_Blue_Web_429598_i0.png");
}
.tabbar a.button.notifying:hover {border-color: #004ce7;background: url("http://i.imgur.com/t8yebRQ.png");border-top-right-radius: 30px;
    border-top-left-radius: 30px;
}
.tabbar a.button.cur,
.tabbar a.button.cur:hover {color: #f8f8f8;text-shadow: 0 1px 0 #707070;background: #000000;box-shadow: none;border-color: #004ce7;border-bottom: 0;cursor: default;padding-top: 5px;padding-bottom: 5px;top: 0px;border-top-right-radius: 30px;border-top-left-radius: 30px;
}.tabbar li:first-child a.button {border-top-left-radius: 30px;margin-left: 12px;
}.tabbar li:last-child a.button {border-top-left-radius: 30px;
}
.tabbar a.closable {
	padding-right: 50px;
}
.tabbar .closebutton {display: inline-block;margin: 0 0 0 -18px;width: 10px;position: relative;z-index:11;top: 3px;vertical-align: top;
color:#707070;
}
.tabbar a.cur + .closebutton {
	top: 6px;
}
.closebutton,
.minimizebutton {text-decoration: none;font-size: 20px;color: #ffffff;border: 1;padding: 10;margin: 0;background: none;cursor: pointer;left:-20px;bottom:80px;
}.closebutton:hover,
.minimizebutton:hover {color: #ffffff;border:none !important;background:none !important;-webkit-transition-delay: 0.1s;-webkit-transition-duration: 0.1s;-webkit-box-shadow: -1px -1px 29px 0px rgba(255,255,255,1);
}
.closebutton:active,
.minimizebutton:active {
	color: #ffffff;
    border:none !important;
    background:none !important;
}


/*********************************************************
 * Rooms
 *********************************************************/

.ps-room {position: absolute;display: block;top: 52px;left: 0;right: 0;bottom: 0;border-left: 2px solid #004ce7;margin-left: -2px;
}.scrollable {overflow: auto;
}.ps-room.ps-room-light {background: url("https://static.wixstatic.com/media/10af1d_2f930ebe26d044e280c842ccc74f8b76.png?dn=sdsdsd.png");
    color: #aaa;-webkit-transition-delay: 0.3s;-webkit-transition-duration: 0.4s;-webkit-box-shadow: -1px -1px 29px 0px rgba(0,43,255,1);
} 
.ps-room.ps-room-light:hover {background: url("https://dl.dropbox.com/s/nrnu5d1fnycj9o1/BLAST%20BACKGROUND.png?dl=0");color: #aaa;-webkit-transition-delay: 0.5s;-webkit-transition-duration: 0.4s;-webkit-box-shadow: -1px -1px 29px 0px rgba(0,43,255,1);
}
.ps-room.ps-room-opaque {background: url(https://static.wixstatic.com/media/10af1d_5f93797bf24241da9fd223800c56df6e.png?dn=aaaaaaaaaaaaaaaaaaaa.png);font-size:18px;color: #CCCCCC;font-family:"bebas neue" !important;
}
.ps-overlay {position: absolute;top: 0;left: 0;right: 0;bottom: 0;background: #000000;background: rgba(0,0,0, .5);border-radius: 25px;
overflow: auto;z-index: 20;
}
.ps-popup {position: absolute;top: auto; right: auto; left: auto; bottom: auto;text-align: left;background: #000000;border-radius: 25px;
    font-family: Joy, Verdana, sans-serif, Arial, Helvetica;font-size: 30px;width: 450px;color: #aaa;border: 1px solid #0041ff;
    padding: 2px 10px;box-shadow: none;z-index: 20;
}
.ps-popup a:link {
    color: #146dc8;
}
.ps-popup a:visited {
    color: #7c1cce;
}
.ps-overlay .ps-popup {position: static;margin: 80px auto 20px auto;max-width: 320px;border-radius: 0px;
}
.ps-popup p,
.ps-popup h3 {
	margin: 7px 0;
    border-radius: 0px;
}
.ps-popup h3 {
	font-size: 12pt;
}
p.or {font-size: 12pt;font-style: italic;font-weight: bold;color: #444444;margin: 20px 0;text-align: center;
}
p.or:before,
p.or:after {content: "";font-size: 10px;padding: 0 0 0 85px;margin: 0 9px 0 0;border-bottom: 1px solid #777777;position: relative;top: -7px;
}
p.or:after {
	margin: 0 0 0 9px;
}
.ps-popup p.error {
	font-weight: bold;
}.popupmenu, popupmenu li {margin: -2px 0 -1px 0;padding: 0;list-style: none;font-size: 10pt;
}.popupmenu h3 {margin: 5px 0 0 -2px;padding: 1px 0;font-size: 8pt;color: #fff;
}
.popupmenu li:first-child h3 {margin-top: 0;
}
.popupmenu button {display: block;cursor: pointer;font-size: 8pt;font-family: Joy, Verdana, sans-serif, Arial,  Helvetica;margin: 0 0 0 6px;
padding: 0 4px;border: 0;border-radius: 0px;background: transparent;color: #aaa;width: 204px;height: 19px;text-align: left;box-sizing: border-box;
}@media (max-height:590px) {
	.popupmenu button {
		height: 17px;
	}
	.popupmenu h3 {
		margin-top: 2px;
	}
	.popupmenu, popupmenu li {
		margin: -3px 0 -2px 0;
	}
}
@media (max-height:518px) {
	.popupmenu button {
		height: 16px;
	}
	.popupmenu h3 {
		margin-top: 1px;
	}
}

.popupmenu button.sel {padding: 0 3px;border: 1px solid #004ce7;
}
.popupmenu button:hover,
.popupmenu button.sel:hover {border-radius: 0px;padding: 0 3px;border: 1px solid #004ce7;background: rgba(0,0,0,0.3);
}
.select {cursor: pointer;box-sizing: border-box;width: 230px;height: 20px;padding: 2px 10px;font-size: 9pt;text-align: left;
    font-family: Joy, Verdana, Helvetica, Arial, sans-serif;border-radius: 25px;border: 1px solid #004ce7;box-shadow: inset 0 1px 0 #FFFFFF;
	background: #000000;color: #aaa;-webkit-transition-delay: 0.3s;-webkit-transition-duration: 0.3s;
    -webkit-box-shadow: -1px -1px 29px 0px rgba(0,43,255,1);
}
.select:hover {color:#fff;border-radius: 24px;border: 1px solid #004ce7;background: #000000;-webkit-transition-delay: 0.3s;
    -webkit-transition-duration: 0.3s;-webkit-box-shadow: -1px -1px 29px 0px rgba(0,43,255,1);
    }
.select:active {border: 2px solid #282828;border-radius: 0px;outline:pink;background: url("http://i.imgur.com/Q5an6Ul.png");box-shadow: inset 0 1px 0 #FFFFFF;
}
.select:after {float: right;margin-top: 1px;font-size: 14px;content: "\f0d7";font-family: FontAwesome;-webkit-font-smoothing: antialiased;
  color: #777777;
}
.select:disabled {border-color: #004ce7;background: rgba(220,220,220,.3);color: #555555;box-shadow: none;
}
.select:disabled:after {
	content: "";
}
.select.preselected,
.select:disabled.preselected {
	color: black;
}
/*********************************************************
 * Main menu
 *********************************************************/

.leftmenu {padding: 40px 0 0 0;width: 300px;
}
.rightmenu {position: absolute;top: 40px;right: 35px;
}
.activitymenu {position: absolute;left: 325px;top: 40px;
}
.mainmenu {padding: 0 0 40px 0;
}

.battleform {width: 230px;text-align: left;margin: 0 auto;
}
.battleform .buttonbar {text-align: center;
}

.mainmenuwrapper {position: relative;min-height: 100%;
}
.mainmenufooter {position: absolute;bottom: 15px;left: 20px;color: #BBBBBB;text-shadow: 0 0 1px black, 0 0 1px black, 0 0 1px black;
}
.mainmenufooter blockquote {margin: 0 0 1em 0;padding: 0;
}
.mainmenufooter small {font-size: 8pt;
}
.mainmenufooter a {text-decoration: underline;color: #FFFFFF;
}
.mainmenufooter a:hover {text-decoration: underline;color: #99BBFF;
}

.pmbox {width: 270px;margin: 0 auto;min-height: 2px;
}
.pm-window {margin-bottom: 12px;
}
.pm-window h3 {background: #000000;color: #ffffff;margin: 0;padding: 7px 10px;font-size: 8pt;border: 2px solid #004ce7;
    border-top-left-radius: 20px;border-top-right-radius: 20px;cursor: pointer;
}
.pm-window h3:hover {color: #000000;
}
.pm-window h3.pm-minimized {border-radius: 0px;
}
.pm-window h3.pm-notifying {border-color: #004ce7;background: #000000;
}
.pm-window h3.pm-notifying:hover {border-color: #604020;background: #000000;
}
.pm-window h3 .closebutton,
.pm-window h3 .minimizebutton {
	float: right;
	padding-right: 30px;
}
.pm-window h3 small {color: #000000;font-weight: normal;vertical-align: 10px;
}
.pm-log {min-height: 100px;max-height: 300px;background: #000000;color: #aaa;word-wrap: break-word;border: 2px solid #004ce7;border-top: 0;
	border-bottom: 0;overflow: auto;-webkit-overflow-scrolling: touch;overflow-scrolling: touch;
}
.pm-log-add {border: 2px solid #004ce7;border-bottom-left-radius:20px;border-bottom-right-radius: 20px;background: #000000;
}
.pm-window.collapsed .pm-log,
.pm-window.collapsed .pm-log-add {display: none;
}
.pm-window.focused h3,
.pm-window.focused .pm-log,
.pm-window.focused .pm-log-add {border-color: #004ce7;
}
.pm-window.focused h3,
.pm-window.focused h3:hover {background: #bebebe;color: #222222;cursor: default;
}
.challenge {margin-top: -1px;background: url("https://static.wixstatic.com/media/10af1d_9c5d75e111e24843917e33787320ac06.png?dn=FIAL+LONE.png");
	border: 1px solid #004ce7;color: #fff;padding: 1px 5px;
}
.challenge p {margin: 1px 0;
}

.tiny-layout .leftmenu,
.tiny-layout .rightmenu,
.tiny-layout .activitymenu {position: static;top: 0;right: 0;left: 0;width: auto;margin: 0;padding: 0;
}
.tiny-layout .leftmenu {padding-top: 30px;
}
.tiny-layout .activitymenu {padding-bottom: 8px;
}

.mainmessage {display: block;padding: 10px 15px;margin: 30px 40px;max-width: 400px;border-radius: 20px;background: rgba(201, 207, 219, .8);
	color: black;
}
.menugroup {display: block;padding: 10px 0;margin: 0 27px 30px 27px;max-width: 400px;border-radius: 30px;color: white;
    text-shadow: 0 1px 0 black;
}
.menugroup {background: #000000;text-align: center;
}

.mainmessage p,
.menugroup p {
	margin: 10px 0;
}
.mainmenu .menugroup:nth-child(2) p:nth-child(1) .button, .mainmenu .menugroup:nth-child(2) p:nth-child(2) .button, .mainmenu .menugroup:nth-child(2) p:nth-child(3) .button, .mainmenu .menugroup:nth-child(3) p:nth-child(1) .button, .mainmenu .menugroup:nth-child(3) p:nth-child(2) .button {
	box-sizing: border-box;font-size: 14pt;font-family: Joystick, Verdana, Helvetica, Arial, sans-serif;display: inline-block;
	padding: 5px 10px 8px 7px;width: 200px;box-shadow:none;outline: none;cursor: pointer;text-align: center;text-decoration: none;
	text-shadow: 0 -1px 0 hsl(210,40%,10%);border-radius: 5px;

	/* default colors */
	color: #aaa;border: solid 1px #0044ef;background: #779EC5;
    background: url("https://static.wixstatic.com/media/10af1d_842b016bb66949bfb00c28a86eabeba8.png?dn=sssssssssss.png");
	box-shadow:none;outline: none;cursor: pointer;text-align: center;text-decoration: none;text-shadow: 0 -1px 0 hsl(210,40%,10%);
	border-radius: 20px;
}
 .mainmenu .menugroup:nth-child(1) .button {width: 230px;padding: 6px 7px 6px 7px;font-size: 14pt;color: #aaa;border: solid 1px #0044ef;
	background: #779EC5;
	background: url("https://static.wixstatic.com/media/10af1d_842b016bb66949bfb00c28a86eabeba8.png?dn=sssssssssss.png");
box-shadow:none;outline: none;cursor: pointer;text-align: center;text-decoration: none;text-shadow: 0 -1px 0 hsl(210,40%,10%);
	border-radius: 20px;
}
  .mainmenu .menugroup:nth-child(1) .button:hover, .mainmenu .menugroup:nth-child(2) p:nth-child(1) .button:hover, .mainmenu .menugroup:nth-child(2) p:nth-child(2) .button:hover, .mainmenu .menugroup:nth-child(2) p:nth-child(3) .button:hover, .mainmenu .menugroup:nth-child(3) p:nth-child(1) .button:hover, .mainmenu .menugroup:nth-child(3) p:nth-child(2) .button:hover {
	background: url("https://static.wixstatic.com/media/10af1d_1330e18dc4584ac5ad239f0c21725425.png?dn=HOVER.png");border-color: #0044ef;
   box-shadow: -1px 1px 40px 0px rgba(0,34,255,1);-webkit-transition-delay: 0.1s;-webkit-transition-duration: 0.2s;
    }
 .mainmenu .menugroup:nth-child(1) .button:active,.mainmenu .menugroup:nth-child(2) p:nth-child(1) .button:active,.mainmenu .menugroup:nth-child(2) p:nth-child(2) .button:active,.mainmenu .menugroup:nth-child(2) p:nth-child(3) .button:active,.mainmenu .menugroup:nth-child(3) p:nth-child(1) .button:active,.mainmenu .menugroup:nth-child(3) p:nth-child(2) .button:active {
	box-shadow: none;background: #0044ef !important;
	border-color: #bb2222;-webkit-box-shadow: -2px -2px 75px 0px rgba(255,255,255,1);
}
.menugroup .button.disabled,
.menugroup .button.disabled:hover,
.menugroup .button.disabled:active {cursor: default;background: #EEEEEE;border: 1px solid #CCCCCC;color: #999999;text-shadow: none;
	-webkit-box-shadow: -2px -2px 75px 0px rgba(255,255,255,1);
}

.rightmenu {width: 294px;
}
.rightmenu .menugroup .button {width: 180px;
}
@media (max-width:895px) {
	.mainmenu {padding-bottom: 1px;
	}
	.rightmenu {position: static;top: 0;right: 0;padding-bottom: 30px;
	}
	.rightmenu {
		width: 324px;
	}
	.rightmenu .menugroup .button {
		width: 200px;
	}
}

/*********************************************************
 * Ladder
 *********************************************************/

.ladder table, .ladder td, .ladder th {border-collapse: collapse;border: 1px solid #004ce7;background-color: #000000;
}
.ladder td, .ladder th {padding: 3px 5px;
}
.ladder th {text-align: left;font-size: 9pt;background: #EEEEEE;
}
.ladder span {color: white;
}

.roomcounters button {display: block;border: 2px solid #004ce7;
	background: url("https://static.wixstatic.com/media/10af1d_9c5d75e111e24843917e33787320ac06.png?dn=FIAL+LONE.png");
	border-radius: 4px;text-decoration: none;color: #ffffff;text-shadow: none;box-shadow: none;cursor: pointer;
	font-size: 5pt;
}

.roomcounters button:hover {border: 2px solid #004ce7;background: #000000;color: #c4c4c4;text-decoration: none;
}

.roomcounters button:active {box-shadow: none;
	background: url("https://static.wixstatic.com/media/10af1d_9c5d75e111e24843917e33787320ac06.png?dn=FIAL+LONE.png") !important;
	border-color: #004ce7;
}

.roomlist {text-align: left;background: transparent;
border-color: #004ce7

}
