css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("content.netdevgroup.com") {

html {
	height: 100%;
}

body {
	height:100%;
  padding-top:51px;/*to offset fixed navbar*/
}

.height-full {
  height: 100%;
}

.nav-tabs>li>a:hover {
  background-color:#555;
  border-color:#ddd;
}

h1, .h1, h2, .h2, h3, .h3 {
  margin-top:0;/*Remove default top margin to align elements*/
}

.tab-content-panel {
  height:calc(100% - 80px);
	border:none;
	border-top:none;
	padding:15px;
        background:#555;
        color: aqua;
	overflow:auto;
	border-radius:0 0 4px 4px;
        background-color: #555;
}

.k-widget {
  background-color:#555;
}

.badge {
  background:#005B99;
  font-size:10px;
}

.pager li>a, .pager li>span {
  border-radius:4px;
}

.dropdown-menu>li>a:hover, .dropdown-menu>li>a:focus {
  color: #fff;
  text-decoration: none;
  background-color: #005B99;
}

/* FAQ and Feedback modal */
.modal-dialog, .modal-content {
  max-height: 100%;
}

.modal-body {
  max-height: 90%;
  overflow: auto;
}

.step2 {
  display: none;
}

.step3 {
  display: none;
}

.step4 {
  display: none;
}
/* </ modal */

/*Creates space on bootstrap tab for tab-options*/
a#tab-with-options {
  padding-right: 45px;
}
/*Pulls dropdown options inside tab-with-options*/
.tab-options {
  margin-left: -42px;
}

/*Title class from bootstrap-tour*/
.popover-title {
  font-weight: bold;
}

/*Navbar Primary*/
.navbar-primary {
  background-color: #005b99;
  border: none;
}
.navbar-primary .navbar-brand {
  color: rgba(255, 255, 255, 0.8);
}
.navbar-primary .navbar-brand:hover,
.navbar-primary .navbar-brand:focus {
  color: #ffffff;
  background-color: transparent;
}
.navbar-primary .navbar-text {
  color: rgba(255, 255, 255, 0.8);
}
.navbar-primary .navbar-nav > li > a {
  color: rgba(255, 255, 255, 0.8);
}
.navbar-primary .navbar-nav > li > a:hover,
.navbar-primary .navbar-nav > li > a:focus {
  color: #ffffff;
  background-color: transparent;
}
.navbar-primary .navbar-nav > .active > a,
.navbar-primary .navbar-nav > .active > a:hover,
.navbar-primary .navbar-nav > .active > a:focus {
  color: #ffffff;
  background-color: #004c80;
}
.navbar-primary .navbar-nav > .disabled > a,
.navbar-primary .navbar-nav > .disabled > a:hover,
.navbar-primary .navbar-nav > .disabled > a:focus {
  color: rgba(255, 255, 255, 0.8);
  background-color: transparent;
}
.navbar-primary .navbar-toggle {
  border-color: #003d66;
}
.navbar-primary .navbar-toggle:hover,
.navbar-primary .navbar-toggle:focus {
  background-color: #003d66;
}
.navbar-primary .navbar-toggle .icon-bar {
  background-color: #ffffff;
}
.navbar-primary .navbar-collapse,
.navbar-primary .navbar-form {
  border-color: #004675;
}
.navbar-primary .navbar-nav > .open > a,
.navbar-primary .navbar-nav > .open > a:hover,
.navbar-primary .navbar-nav > .open > a:focus {
  background-color: #004c80;
  color: #ffffff;
}
@media (max-width: 767px) {
  .navbar-primary .navbar-nav .open .dropdown-menu > .dropdown-header {
    border-color: #004c80;
  }
  .navbar-primary .navbar-nav .open .dropdown-menu .divider {
    background-color: #004c80;
  }
  .navbar-primary .navbar-nav .open .dropdown-menu > li > a {
    color: rgba(255, 255, 255, 0.8);
  }
  .navbar-primary .navbar-nav .open .dropdown-menu > li > a:hover,
  .navbar-primary .navbar-nav .open .dropdown-menu > li > a:focus {
    color: #ffffff;
    background-color: transparent;
  }
  .navbar-primary .navbar-nav .open .dropdown-menu > .active > a,
  .navbar-primary .navbar-nav .open .dropdown-menu > .active > a:hover,
  .navbar-primary .navbar-nav .open .dropdown-menu > .active > a:focus {
    color: #ffffff;
    background-color: #004c80;
  }
  .navbar-primary .navbar-nav .open .dropdown-menu > .disabled > a,
  .navbar-primary .navbar-nav .open .dropdown-menu > .disabled > a:hover,
  .navbar-primary .navbar-nav .open .dropdown-menu > .disabled > a:focus {
    color: rgba(255, 255, 255, 0.8);
    background-color: transparent;
  }
}
.navbar-primary .navbar-link {
  color: rgba(255, 255, 255, 0.8);
}
.navbar-primary .navbar-link:hover {
  color: #ffffff;
}
.navbar-primary .btn-link {
  color: rgba(255, 255, 255, 0.8);
}
.navbar-primary .btn-link:hover,
.navbar-primary .btn-link:focus {
  color: #ffffff;
}
.navbar-primary .btn-link[disabled]:hover,
fieldset[disabled] .navbar-primary .btn-link:hover,
.navbar-primary .btn-link[disabled]:focus,
fieldset[disabled] .navbar-primary .btn-link:focus {
  color: rgba(255, 255, 255, 0.8);
}
/*End Navbar Primary*/

.k-animation-container, .k-widget, .k-widget *, .k-animation-container *, .k-widget *:before, .k-animation-container *:after, .k-block .k-header, .k-list-container {
  box-sizing:border-box;
  -webkit-box-sizing:border-box;
}

.k-splitbar-draggable-horizontal {
  cursor: ew-resize;
}

}
