css:
@-moz-document domain("hardforum.com") {
/* Shrink text */
html {
    font: 13px / 1.2 sans-serif;
}
.p-nav-list > li {
    font-size: 14px;
}
.structItem-title {
    font-size: 13px;
}
.input {
    font-size: 13px;
}
.node-title {
    font-size: 13px;
}
.node--unread .node-title, .is-unread .structItem-title {
    font-weight: 400;
}
.node-meta {
    font-size: 12px;
}

/* Shrink logo */
.p-header-content .p-header-logo.p-header-logo--image img {
    max-height: 72px;
}

/* Shrink navigation */
.p-body-inner {
    padding-top: 10px;
}
.p-breadcrumbs--parent {
    margin-bottom: 5px;
}
.p-body-header {
    margin-bottom: 10px;
}

/* Thread listing cleanup */
.structItem > div {
    padding-top: 7px;
    padding-bottom: 7px;
}
.structItem-cell--newThread {
    display: none;
}
.block-filterBar {
    border-bottom: none;
    border-top-left-radius: 0px !important;
    border-top-right-radius: 0px !important;
}
.pageNav-page > a, .pageNav-jump {
    font-size: 12px;
    padding: 4px 8px;
}
.pageNav-main {
    margin-top: 4px;
}
.structItem-minor {
    margin-top: 1px;
}
.structItemContainer-group.structItemContainer-group--sticky::after {
    height: 4px;
}

/* Most recent thread in thread list */
.node-extra {
    padding: 0px !important;
}
.node-extra .node-extra--inner {
    border: none;
    /*border-left: 1px solid #000;*/
    padding: 10px 10px;
    height: 50px;
}

/* Navbar Cleanup*/
.p-navgroup-link, .p-navgroup {
    border-top-left-radius: 0px !important;
    border-top-right-radius: 0px !important;
}
.p-navgroup {
    background: none;
}
.p-navgroup-link:first-of-type.p-navgroup-link--whatsnew + .p-navgroup-link {
    border-left: 1px solid rgba(225,225,225,0.15);
}
.p-navgroup.p-discovery {
    margin-left: -1px;
}
.p-sectionLinks-inner {
    padding-left: 0px;
}

/* Remove copyright info */
.p-footer-copyright, .p-footer-copyright .p-pe-copyright a {
    color: #0000;
    max-height: 0px;
    padding-top: 0px;
}

/* Footer cleanup */
.p-footer-default {
    padding: 8.35px 15px;
}

/* Thread view */
.block--messages article.message {
    padding: 0px;
    border-radius: 0px;
}
.message-avatar-wrapper {
    bottom: -0.5em;
}
.message-userArrow {
    display: none;
}
.actionBar-action {
    font-size: 12px;
}
.reactionsBar {
    border: none;
    background-color: #0000;
}
.message .reactionsBar {
    padding: 0px;
    margin-top: 5px;
}
.message-footer .message-actionBar {
    margin-top: 5px;
}
}
