@-moz-document domain("pcland.hu") {

  html {
    background: #333;
  }
  
  body {
    max-width: 1050px;
  }
  
  #siteBody {
    background: #f3f3f3;
    margin-top: 115px;
  }
  
  #siteHeaderFelsomenu {
    background-color: #a61f22;
    max-width: 1048px;
  }
  
  #siteHeaderFelsomenuDoboz {
    width: 1098px;
    text-align: center;
    position: absolute;
  }
  
  #siteHeaderFelsomenuLogoImg {
