    height: 0px;
    margin-top: 12px;
    margin-bottom: -12px;
    padding-left: 200px;
    padding-bottom: 100px;
    background: transparent url("http://users.atw.hu/b95/css/PCL/pclandlogo.png");
    background-size: 200px;
    background-repeat: no-repeat;
    position: unset;
  }
  
  .siteHeaderFelsomenuLinkek {
    color: #f3f3f3;
    border-color: #58595b;
    line-height: 16px;
  }
  
  .siteHeaderFelsomenuLinkek:hover {
    color: #000;
    font-weight: bold;
    font-size: 14px;
    text-decoration: aqua;
  }
  
  .rightMiniHeader {
    background: #58595b;
  }
  
  #siteHeaderFelsomenuKosarKerses {
    margin-top: 48px;
  }
  
