    border-radius: 8px;
    box-shadow: 1px 1px 1px 1px #939598 inset;
  }
  
  #siteBodyBody_uzenet {
    background: #939598;
    margin: 0px 12px;
    border-radius: 12px;
  }
  
  #ut_friss_info {
    background: #f3f3f3;
    color: #000;
    box-shadow: 1.5px 1.5px 1.5px 1.5px #58595b inset;
    border: 0px;
    border-radius: 10px;
    margin: 33px 3px;
  }
  
  .fooldal_doboz {
    background: transparent;
    border: 0px;
  }
  
  .fooldal_doboz .fooldal_doboz_cim {
    background: transparent;
    border: 0px;
    margin: -36px 0px 0px 0px;
    color: #a61f22;
    font-size: 24px;
    font-weight: bold;
  }
  
  .fooldal_doboz_2 {
    border: 0px;
    box-shadow: 2px 2px 2px 2px #939598 inset;
    width: 335px;
    margin: 2px;
  }
  
  .fooldal_doboz_2 .fooldal_doboz_cim {
    background: transparent;
    border: 0px;
    margin-top: -40px;
    color: #333333;
    font-size: 20px;
    font-weight: bold;
  }
  
  .ajanlott_termekek_egy_sor__termeke_nev {
    color: #a61f22;
    text-decoration: aqua;
  }
  
  .ajanlott_termekek_egy_sor a {
    color: #58595b;
  }
  
  .ajanlott_termekek_egy_sor__ar {
    color: #333333;
  }
  
  #cssmenu > ul > li > a {
    background: linear-gradient(#999 0%, #666 100%);
    border-color: #000;
  }
  
  #cssmenu > ul > li > a:hover, #cssmenu > ul > li.open > a {
    background: linear-gradient(#666 0%, #999 100%);
    border-color: #000;
  }
  
  #cssmenu > ul > li.open > a {
    background: linear-gradient(#900 0%, #b00 100%);
    border-color: #000;
  }
  
  #cssmenu ul ul li a {
    background: #58595b;
    border-color: #333333;
  }
  
  .termekekDoboz, .termekekDoboz .kl {
    width: 540px;
  }
  
  .termekekDoboz .leiras {
    width: 400px;
  }
  
  .termekekDoboz .gycsz {
    width: 540px;
  }
  
  .termekekDoboz .gycsz div:first-child {
    width: 250px;
  }
  
  .termekekDobozB {
    border-color: transparent;
    border-radius: 8px;
    box-shadow: 1px 1px 1px 1px #aaa inset;
    width: 174px;
  }
  
  .termekekDobozB h2 {
    margin: 2px 6px;
  }
  
  .termekekDobozB h2 a {
    color: #000;
  }
  
  .termekekDobozB h2 a:hover {
    color: #a61f22;
  }
  
  .termekekDobozB .leiras div:nth-child(3) {
    font-size: 11px;
  }
  
  .termekekDobozB .gycsz {
    width: 170px;
  }
  
  .termekekDobozB .kep img {
    max-width: 160px;
  }
  
  img[src="/images/favorite_off.png"], img[src="/images/favorite_on.png"] {
    width: 24px;
  }
  
  .ajanlott_termekek_egy_sor {
    border-color: transparent !important;
  }
  
  .ajanlott_termekek_egy_sor:hover {
    box-shadow: 0px 0px 0px 0px !important;
  }
  
  #termekLapKepEsAdatokKepekAlapertkep_favorit {
    
  }
  
  .termekLapKepEsAdatokKepekAlapertkep {
    max-width: 325px;
  }
  
  #termekLapTermekNeve h1 a, #termekLapTermekNeve h1 a:hover {
    color: #333333;
  }
  
  #termekLapKepEsAdatokAdatokArak {
    background-color: #939598;
    border-radius: 8px;
    box-shadow: 2px 2px 2px 2px #333 inset;
  }
  
  .termekLapKepEsAdatokAdatokAdatsorAr {
    padding: 8px;
  }
  
  .termekLapKepEsAdatokAdatokAdatsorArNetto {
    color: #333;
  }
  
  .termekLapKepEsAdatokAdatokAdatsorArBrutto {
    color: #a61f22;
    font-weight: bold;
  }
  
  #termekLapKepEsAdatokAdatokKosarbaGomb {
    margin: 8px;
  }
  
  .ui-tabs .ui-tabs-nav {
    background: transparent;
    border-color: transparent;
  }
  
  .ui-tabs .ui-tabs-nav li {
    border: 1px solid #333333;
    border-radius: 4px;
    background: #58595b;
  }
  
  .ui-tabs .ui-tabs-nav li:hover {
    border: 1px solid #333333;
    border-radius: 4px;
    background: #a61f22;
  }
  
  .ui-state-hover a, .ui-state-hover a:hover, .ui-state-hover a:link, .ui-state-hover a:visited, .ui-state-focus a, .ui-state-focus a:hover, .ui-state-focus a:link, .ui-state-focus a:visited {
    color: #f3f3f3;
  }
  
  .ui-tabs .ui-tabs-nav li.ui-tabs-active, .ui-tabs .ui-tabs-nav li.ui-tabs-active:hover {
    border: 1px solid #333333;
    border-radius: 4px;
    background: transparent;
  }
  
  #beuszos_ajanlo, #beuszos_ajanlo:hover {
    border: 1px solid #333333;
  }
  
}
