css:
@-moz-document url-prefix("http://www.minecraftforge.net/forum/") {
body
{
    background: #2F2F2F;
}
#content_section
{
    background: #313131;
}
.windowbg2
{
    color: #808080;
    background-color: #2B2B2B;
}
.lockedbg2
{
    background: #151515;
}
body, td, th, tr
{
    color: #808080;
}
.windowbg, #preview_body
{
    color: #808080;
    background-color: #2B2B2B;
}
table.table_grid td
{
    border-bottom: 1px solid #444141;
    border-right: 1px solid #444141;
}
.stickybg2
{
    background: #101010;
}
.stickybg
{
    background: #101010;
}
.lockedbg
{
    background: #151515;
}
.description, .description_board, .plainbox
{
    background: #2B2B2B;
}
.description, .description_board, .plainbox
{
    border: 1px solid #444141;
}
input, button, select, textarea
{
    color: #808080;
    background: #313131;
    border: 1px solid #444141;
}
.roundframe
{
    padding: 0 10px;
    background: #313131;
    border-left: 1px solid #313131;
    border-right: 1px solid #313131;
}
span.upperframe
{
    background: #313131
}
span.upperframe span
{
    background: #313131
}
span.lowerframe span
{
    background: #313131
}
span.lowerframe
{
    background: #313131
}
code.bbc_code
{
    background: #1D1D1D;
    border-top: 2px solid #484848;
    border-bottom: 2px solid #484848;
}
.header_topbar
{
    background: url(http://i.imgur.com/wZnzfpO.png);
}
.header_main
{
    background: url(http://i.imgur.com/ntRE0CP.png);
}
.header_nav
{
    background: url(http://i.imgur.com/oTzzZxA.png);
}
a:link, a:visited
{
    color: #808080;
}
.windowbg span.topslice, .windowbg span.topslice span, .windowbg2 span.topslice, .windowbg2 span.topslice span, .windowbg2 span.botslice, .windowbg2 span.botslice span, .windowbg span.botslice, .windowbg span.botslice span
{
    background: url(http://i.imgur.com/RnBra58.png) 0 -30px no-repeat;
}
div.cat_bar
{
    background: url(http://i.imgur.com/RnBra58.png) no-repeat 0 -160px;
}
h4.catbg, h4.catbg2, h3.catbg, h3.catbg2, .table_list tbody.header td.catbg
{
    background: url(http://i.imgur.com/RnBra58.png) no-repeat 100% -160px;
}
.catbg, .catbg2, tr.catbg td, tr.catbg2 td, tr.catbg th, tr.catbg2 th
{
    background: url(http://i.imgur.com/RnBra58.png) no-repeat -10px -280px;
}
tr.catbg th.first_th
{
    background: url(http://i.imgur.com/RnBra58.png) no-repeat 0 -280px;
}
tr.catbg th.last_th
{
    background: url(http://i.imgur.com/RnBra58.png) no-repeat 100% -280px;
}
.bbc_link:link, .bbc_link:visited
{
    border-bottom: 1px solid #808080;
}
blockquote.bbc_standard_quote
{
    background-color: #565656;
}
blockquote.bbc_standard_quote, blockquote.bbc_alternate_quote
{
    border-top: 2px solid #3F3F42;
    border-bottom: 2px solid #3F3F42;
    border-right: 2px solid #3F3F42;
    border-left: 2px solid #3F3F42;
}
blockquote.bbc_alternate_quote
{
    background-color: #565656;
}
.buttonlist ul li a.active
{
    background: url(http://i.imgur.com/CGvueS7.png) no-repeat 0 -90px;
}
.buttonlist ul li a
{
    background: #313131 url(http://i.imgur.com/CGvueS7.png) no-repeat 0 -60px;
}
.buttonlist ul li a.active span
{
    background: url(http://i.imgur.com/CGvueS7.png) no-repeat 100% -90px;
}
.buttonlist ul li a span
{
    background: url(http://i.imgur.com/CGvueS7.png) no-repeat 100% -60px;
}
span[style]
{
    color: #808080
}
.spoiler-body
{
    background: none repeat scroll 0 0 #2B2B2B;
    border-top: 1px solid #2B2B2B;
    color: #7C7C7C;
}
.spoiler-wrap
{
    border: 2px solid #454545;
    background-color: #2B2B2B;
}
.spoiler-wrap
{
    border: 2px solid #454545;
    border-radius: 6px;
    background-color: #2B2B2B;
    margin: 0 auto 1em;
}
div.title_barIC h4.titlebg
{
    background: url(http://i.imgur.com/RnBra58.png) no-repeat 100% -120px;
}
div.title_barIC
{
    background: #313131 url(http://i.imgur.com/RnBra58.png) no-repeat 0 -120px;
}
.spoiler-head
{
    color: #747474;
}
}
