css:
@-moz-document domain("proxer.me") {
/* Gray Default (needed) */


/* Venipa's Design (Custom) */
* {outline:none;}
body {
    background: url("https://biyori.moe/external/wp.php") no-repeat center center fixed;
    -webkit-background-size: cover;
    -moz-background-size: cover;
    -o-background-size: cover;
    background-size: cover;
    color: #CCCCCC;
}

a {
    color: #00FFFF;
    text-decoration: none;
}

a:hover {
    color: #FFFF00;
}

#wrapper {
    margin: 0 auto;
    max-width: 1250px;
    min-width: 1050px;
    width: 100%;
    overflow: hidden;
}

#nav a {
    color: #FFF;
}

.topmenu {
    transition: all 0.15s ease-in-out;
    border-bottom: 1px solid transparent;
}

.topmenu:hover {
    border-bottom: 1px solid #368747!important;
}

.topmenu ul {
    background: #14171c;
    border: 0px solid #1a1a1a;
    border-top: 0px;
}

hr {
    border: 1px solid #368747;
}

#nav {
    background-color: #14171c;
}

#nav #leftNav li {
    transition: all 0.15s ease-in-out;
    border-left: 0px solid transparent;
}
.topmenu li:nth-last-child(1) {
        border-radius: 0 0 5px 0px;
}
#nav #leftNav li:hover {
    background: #368747;
}
#nav #leftNav .topmenu ul li hr {display:none;}
#nav #leftNav .topmenu ul li {
    border-left: 8px solid #368747;
}

#nav #leftNav .topmenu ul li:hover {
    border-left: 8px solid rgba(0, 255, 255, 0.3);
    border-right: 0px solid transparent;
}

#nav #outerNav #loginBubble {
    background: #14171c;
    border: 1px solid #1a1a1a;
    border-radius: 3px;
    box-shadow: none;
}

#nav #outerNav #loginBubble input[type="text"],
#nav #outerNav #loginBubble input[type="password"] {
    background: #e0e0e0;
    background-color: #e0e0e0;
    color: black;
    border: 0px;
    height: 16px;
    font-size: 14px;
    padding: 2px;
}

#nav #outerNav #loginBubble input[type="submit"] {
    padding: 6px;
    font-size: 13px;
    cursor: pointer;
    color: #dbd0d0;
    background: #263238;
    transition: all 0.15s ease-in-out;
    border-radius: 0px!important;
    border: 0px;
    border-left: 12px solid #263238;
    border-right: 12px solid #263238;
}

#nav #outerNav #loginBubble input[type="submit"]:hover {
    border-right: 5px solid #368747;
    border-left: 5px solid #368747;
}

#nav #outerNav #loginBubble li a {
    transition: all 0.15s ease-in-out;
    border-bottom: 2px solid transparent;
}

#nav #outerNav #loginBubble li a:hover {
    border-bottom: 2px solid #368747;
}

#loginBubble:after {
    display: none;
}

#loginNav.notLoggedIn {
    background-image: url("http://proxer.me/templates/proxer14/images/key.png");
}

#newsNav {
    background-image: url("http://proxer.me/templates/proxer14/images/notification_0.png");
}

#miscNav {
    background-image: url("http://proxer.me/templates/proxer14/images/notification_4.png");
}

#messageNav {
    background-image: url("http://proxer.me/templates/proxer14/images/notification_2.png");
}

#requestNav {
    background-image: url("http://proxer.me/templates/proxer14/images/notification_1.png");
}

#searchNav {
    background-image: url("http://proxer.me/templates/proxer14/images/notification_5.png");
}

#main {
    background-color: #14171c!important;
    border: 1px solid #1a1a1a!important;
    border-radius: 2px;
}

.moduletable {
    background-color: #14171c!important;
    border: 1px solid #1a1a1a!important;
    border-radius: 2px;
}

ul#simple-navi-wiki li a,
ul#simple-navi li.active a,
ul#simple-navi li a:hover {
    background: none;
}

ul#simple-navi,
ul#simple-navi-wiki {
    background-color: #14171c;
    border-radius: 2px 2px 0 0;
    font-weight: bold;
}

ul#simple-navi li a,
ul#simple-navi-wiki li.active a,
ul#simple-navi-wiki li a {
    color: #ccc;
    background: #263238;
    border-radius: 0px;
    border: 0px solid transparent;
    border-bottom: 3px solid transparent;
    transition: all 0.15s ease-in-out;
}

ul#simple-navi-wiki li a:hover {
    border-bottom: 3px solid #368747;
}

ul#simple-navi li a:hover {
    border-bottom: 3px solid #368747;
    background: rgba(255, 255, 255, 0.2)
}

ul#simple-navi-wiki li a,
ul#simple-navi li.active a {
    background: #005a75;
    color: #FFF;
}

#notificationBubble,
#loginBubble,
.customBubble {
    background: #14171c;
    text-shadow: 1px 1px 0 #000;
    border: 1px solid #FFF;
}

#notificationBubble:after,
#loginBubble:after,
.customBubble:after {
    border-color: #5E5E5E transparent;
}
.proxerSliderImage {
    box-shadow: inset 0px 0px 25px 10px black;
    border:2px solid #333;
}
#notificationBubble, #loginBubble, .customBubble, .pointerBubble {
  box-shadow: 0px 0px 25px 1px #000;
  
}
select#globalsearch_section option {
  background-color:#222;
}
input#submitSearch0,select#globalsearch_section,input#proxerSearch0 {
  background-color:rgba(255,255,255,.1);
  padding:4px 8px;
  color:#eee;
  border:0px solid transparent;
  border-bottom: 3px solid #368747;
  cursor:pointer;
}
input#submitSearch0:hover,select#globalsearch_section:hover {
  background-color:rgba(255,255,255,.15);
}
#nav #outerNav #loginBubble {border:0px solid transparent;border-top:5px solid #368747;}
#notificationBubble, #loginBubble, .customBubble {
  border:1px solid transparent;
}
div.inner {
  box-shadow:0px 0px 5px 1px black;
  background-color:rgba(255,255,255,.05);
  padding:8px 15px;
  margin-top:10px;
}
#loginBubble ul li a {
  margin-left:0px;
  padding-left:10px;
  
}
a.menu {
    border: 1px solid transparent;
    color: #FFF;
    background-color: rgba(255, 255, 255, 0.2);
}

a.menu.active,
a.menu:hover {
    background-color: rgba(255, 255, 255, 0.5);
    color: #000!important;
}

#box-table-a {
    text-align: left;
    border-collapse: collapse;
    transition: color 0.15s ease;
}

#box-table-a tr {
    border: 1px solid;
    height: 30px;
    border-bottom: 1px solid #fff;
    transition: all 0.15s ease-in-out;
}

#box-table-a tr:hover {
    border-bottom: 5px solid #368747;
    background: #1a1a1a;
    cursor: pointer;
}

#box-table-a th {
    background: #1e232b;
    border-bottom: 1px solid #14171c;
    color: #FFF;
}

#box-table-a td {
    border-bottom: 1px solid #bbb;
    color: #FFF;
    background: #14171c;
}

#box-table-a tr:hover td {
    background: #1a1a1a;
    color: #777;
    cursor: pointer;
}

#box-table-a tr:hover a {
    color: #eee;
    cursor: pointer;
}

#messages .message {
    background: repeat scroll 0 0 #5E5E5E;
    text-shadow: 1px 1px 0 #000;
    border: 1px solid #FFF;
    box-shadow: 5px 5px 25px #E7E7E7;
}

#chat {
    border-color: #777777;
    background-color: #5E5E5E;
}

.details td {
    background-color: rgba(255, 255, 255, 0.1);
    border-radius: 1px;
}

.scrollable.default-skin .scroll-bar .thumb {
    background-color: #FFF;
}

.ui-widget {
    background: #1e232b;
    color: cornsilk;
}

.ui-tooltip,
.ui-widget {
    box-shadow: none;
    border: 1px solid #005a75;
}

#chat,
#SGBG,
#OWBG {
    background-color: #1e232b;
}
a.showBubble {
  color: #eee;
}
a.showBubble:hover {
  color:#368747;
}
#miscNav:before,#newsNav:before,#messageNav:before,#requestNav:before,#searchNav:before {
  color:transparent;
  display:none;
  bottom:-36px;
  position:absolute;
  background:#222;
  line-height:initial;
  padding:4px 8px;
  margin-left:-8px;
  border-radius:2.5px;
  border-left:4px solid rgb(54, 135, 71);
}
#miscNav:hover:before,#newsNav:hover:before,#messageNav:hover:before,#requestNav:hover:before,#searchNav:hover:before {
  position:absolute;
  color:#eee;
  display:initial;
}
#miscNav:before {content:'Misc';}
#newsNav:before {content:'News';}
#messageNav:before {content:'Messages';}
#requestNav:before {content:'Friend Requests';}
#searchNav:before {content:'Search';}
#miscNav {background-image:url('https://biyori.moe/external/32/bookmark.png');}
#newsNav {background-image:url('https://biyori.moe/external/32/news.png');}
#messageNav {background-image:url('https://biyori.moe/external/32/message.png');}
#requestNav {background-image:url('https://biyori.moe/external/32/friends.png');}
#searchNav {background-image:url('https://biyori.moe/external/32/search.png');}
#miscNav,#newsNav,#messageNav,#requestNav,#searchNav {
  transition:all .15s ease-in-out;
}
#miscNav:hover,#newsNav:hover,#messageNav:hover,#requestNav:hover,#searchNav:hover {
  background-color:#333;
}
img[src='/images/misc/offlineicon.png'] {content: url('https://biyori.moe/external/16/notwatched.png');}
img[src='/images/misc/onlineicon.png'] {content: url('https://biyori.moe/external/16/watched.png');}
#box-table-a tr {
  border:2px solid #111;
}
table.profile tbody tr td font font{
  border-left:0px solid;
  background-color:rgba(255,255,255,.1)!important;
  border-left:4px solid!important;
  
}
table.profile tbody tr td font {
  background-color:transparent!important;
  margin:0px;
  padding:2px 5px;
  border:0px solid black!important;
  border-left:0px solid !important;
  margin:5px;
}
.borderLeft {
  border-left:0px solid transparent;
}
.listState1 .check {
    background-image: url("https://biyori.moe/external/watched.png") !important;
}
}
