css:
@-moz-document domain("willfulcasual.wordpress.com") {
/*Header*/
#header-text-nav-container {background-color: #232323 !important;}

/*Title Text*/
h1, h2, h3, h4, h5, h6 {color: #eeeeee !important;}

#content .post .article-content a {color: #eee;}

#content .post .article-content a:hover {color: #fff !important;}

.post .search a { color: #ffffff !important;}

  input[type="text"], input[type="password"], input[type="email"], input[type="search"], input[type="url"], textarea, .post-details {
	color: #eee !important;
}
a {color: /*[[linkcolor]]*/ !important;}
a:visited {color: /*[[linkvisited]]*/ !important;}
a:hover {color: /*[[linkhover]]*/ !important;}
  
.entry-title a {
	color: #3599C4 !important;
}

.entry-title a:visited { color: #7CCEA9 !important;}

 
.wf-active input[type="email"], .wf-active input[type="password"], .wf-active input[type="search"], .wf-active input[type="text"], .wf-active input[type="url"], .wf-active textarea {
	color: #eee !important;
	background: transparent !important;
} 

/*Main Text*/
body, select {color: #eee !important;}
  button, input, textarea {color: #333 !important;}
.light-text {
 color: #eee !important;
}
#primary a:hover {text-decoration: underline;}

/*Body*/ 
#main, .site, #secondary .widget, .error-404 .widget, #secondary { background: /*[[bg]]*/ !important;}

/*Bottom elements*/
#sharing_email .sharing_send, .sd-content ul li .option a.share-ustom, .sd-content ul li a.sd-button, .sd-content ul li.advanced a.share-more, .sd-content ul li.preview-item div.option.option-smart-off a, .sd-social-icon .sd-content ul li a.sd-button, .sd-social-icon-text .sd-content ul li a.sd-button, .sd-social-official .sd-content>ul>li .digg_button >a, .sd-social-official .sd-content>ul>li>a.sd-button, .sd-social-text .sd-content ul li a.sd-button {background-color: #232323 !important;}

.wpdiscuz-subscribe-bar {background-color: #232323 !important;}

input[type="email"], input[type="password"], textarea {background-color: #232323 !important;}

#wpcomm .wc-form-wrapper {background-color: #232323 !important;}

input[type="text"], input[type="email"], input[type="password"], textarea {background-color: #232323 !important;}

.wpdiscuz-front-actions {background-color: #232323 !important;}

/*Comments*/

#respond, #wpcomm .wc-comment .wc-comment-right {background: transparent !important;}
	#respond #comment-form-comment, #comment-form-share-text-padder, #respond .comment-form-fields div.comment-form-input input {
	background: transparent !important;
	border: 1px solid rgba(255, 255, 255, 0.4);
}

	#respond .comment-form-service * {
	color: #222 !important;
		background: transparent !important;
}


#wpcomm .wc-comment .wc-comment-right {background-color: #232323 !important;}

#wpcomm .wc-comment-text {color: #eeeeee !important;}
  .comment .comment-meta .comment-author .fn {
	color: #eee !important;
}

.wc-load-more-submit {background-color: #232323 !important;}
}
