css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(duckduckgo.com) {   
  
  
.tag-home__wrapper, #search_button_homepage, .header--aside__msg.js-side-menu-msg, #search_form_input_clear, #search_button, .badge-link.js-badge-link, .result.result--sep.js-result-sep.result--sep--hr.has-pagenum, .btn.feedback-btn, .search__autocomplete, #duckbar_static_sep, #duckbar_dynamic_sep  { display: none !important; }
.tile-nav, .tile-nav.can-scroll::after, .result__badge.badge--official.zci-official-site, .result__badge.badge--official { background-color: #06CB8D !important; }
  
.set-bookmarklet__title.js-set-bookmarklet-expand:hover, .set-reset__title.js-set-reset-expand:hover { background-color: #06CB8D !important; color: #333333 !important; }
  
.modal__list__link.metabar__dropdown__item.is-selected.js-dropdown-items { color: #06CB8D !important; background-color: #333333 !important; }
  
.btn.btn--primary.js-set-exit, .frm__switch__label.btn, .btn.btn--primary.js-cloudsave-load-btn { background-color: #06CB8D !important; border-color: #06CB8D !important; }

.result.results_links_deep.highlight_d.highlight { background-color: #292929 !important; -moz-transition: 325ms linear 0s !important;  } 

#zci-about, .cloudsave__error.js-cloudsave-error, .detail__pane, #zci-videos, #zci-qa { background-color: #292929 !important; } 

body, .site-wrapper-border, #header_wrapper, .tile, .tile__foot.tile__foot--news, .tile__expand.tile--info__expand.js-tile-expand, .tile--f__main.tile--loc__main, .modal__body, .modal__list__link.metabar__dropdown__item.js-dropdown-items:hover { background-color: #333333 !important; }  
  
.tile, .zci--type--tiles.is-active, .search--adv.search--header.js-search-form, .tile--f__main.tile--loc__main  { border-color: #232323 !important; } 
  
.zcm__link--sub.js-topic-menu-link:hover { color: #06CB8D !important; }
.tile.highlight { border-color: #333333 !important; box-shadow: 0px 0px 10px -5px #06CB8D; -moz-transition: 325ms linear 0s !important; }

.result__snippet > B, .search__input--adv.js-search-input, #links, .result__url__domain, .result__url__full, .msg__label.js-safe-search, .msg__label, .tile__title.js-ellipsis, .frm__desc > A, .tile--info__link, .info.info--head, .c-detail__title, .modal__list__link.metabar__dropdown__item.js-dropdown-items, .metabar__dropdown__selected.js-dropdown-selected, .frm__label, #did_you_mean, .tx-clr--slate, .msg__text.hide--screen-s { color: #F7F7F7 !important; } 
 
.tile__content.tile__content--news.js-ellipsis, .news-source, .tile__time, .tile__title, .zci__def__word.text--primary, .zci__def__pronunciation, .zci__def__definition, .zci__def__part-of-speech, .c-info__title, .c-info__content.chomp.js-ellipsis, .metabar__primary-text.js-metabar-primary, .tile__tx.tile--pr__sub.one-line, .info__label, .info__value, .set-bookmarklet__title.js-set-bookmarklet-expand, .set-reset__title.js-set-reset-expand, .tile__content.tile__content--sm,  .result__snippet { color: #C8C8C8 !important; } 

#search_form_input_homepage { color: #000000 !important; }   

.metabar.metabar--fixed.js-zcm-sticky , .tile-wrap, .search--adv.search--header.js-search-form, #zci-dictionary_definition, .result.results_links.highlight_v:hover, .msg.msg--result.msg--spelling.highlight_s.highlight { background-color: #292929 !important; } 
  
.result__a { color: #F7F7F7 !important; } 
.result__check { color: #333333 !important; }
  
.result__check:hover, .result__a:hover, .tile--info__link:hover, .msg.msg--result.msg--spelling.highlight_s > A, .result__menu, .hide--screen-s  { color: #06CB8D !important; } 
.zcm__link.js-zci-link.js-zci-link--meanings, .zcm__link.js-zci-link.js-zci-link--images, .zcm__link.js-zci-link.js-zci-link--videos, .zcm__link.js-zci-link.js-zci-link--dictionary_definition, .zcm__link.js-zci-link.js-zci-link--about, .zcm__link.js-zci-link.js-zci-link--apps, .zcm__link.js-zci-link.js-zci-link--products, .zcm__link.js-zci-link.js-zci-link--maps_places { color: #F7F7F7 !important;  }

.zcm__link.js-zci-link.js-zci-link--meanings.is-active, .zcm__link.js-zci-link.js-zci-link--images.is-active, .zcm__link.js-zci-link.js-zci-link--videos.is-active, .zcm__link.js-zci-link.js-zci-link--dictionary_definition.is-active, .zcm__link.js-zci-link.js-zci-link--about.is-active, .zcm__link.js-zci-link.js-zci-link--apps.is-active, .zcm__link.js-zci-link.js-zci-link--products.is-active, .zcm__link.js-zci-link.js-zci-link--maps_places.is-active, .zcm__link.js-zci-link.js-zci-link--qa.is-active,.zcm__link.js-zci-link.js-zci-link--news.is-active { color: #06CB8D !important; border-color: #06CB8D !important; }

.zcm__link.js-zci-link.js-zci-link--meanings:hover, .zcm__link.js-zci-link.js-zci-link--images:hover, .zcm__link.js-zci-link.js-zci-link--videos:hover, .zcm__link.js-zci-link.js-zci-link--dictionary_definition:hover, .zcm__link.js-zci-link.js-zci-link--about:hover, .zcm__link.js-zci-link.js-zci-link--apps:hover, .zcm__link.js-zci-link.js-zci-link--products:hover, .zcm__link.js-zci-link.js-zci-link--maps_places:hover, .modal__list__link.metabar__dropdown__item.js-dropdown-items:hover, .zcm__link.js-zci-link.js-zci-link--qa:hover, .zcm__link.js-zci-link.js-zci-link--news:hover { color: #06CB8D !important; }  

.zcm__link--sub.js-topic-menu-link.is-highlighted, .zcm__link--sub.js-topic-menu-link.is-selected, .frm__section-label, .no-visited, .js-spelling-link { color: #06CB8D !important; }    

#header > DIV:first-child > A > SPAN { background-image: url("https://duckduckgo.com/assets/logo_header_alt.v103.lg.svg"); }  

#logo_homepage_link { background-image: url("https://duckduckgo.com/assets/logo_homepage.alt.v106.svg"); }
  
}
