css:
@-moz-document url-prefix("https://play.google.com/music") {
.material #material-app-bar{
	background-color: #fff !important;
    color: #000 !important;
    border-bottom: 1px solid #e6e6e6 !important;
}
#material-breadcrumbs .tab-text{
	color: #000 !important;
}
paper-toolbar{
	height: 48px !important;
}
sj-search-box #input.sj-search-box{
	height: 32px !important;
    font-size: 14px !important;
}
sj-search-box{
    margin-top: -2px !important;
}
#material-app-bar .material-one-google{
	height: 48px !important;
}
sj-search-box #searchIcon.sj-search-box{
	top: 5px !important;
}
sj-search-box{
    border-radius: 0px !important;
    border: 1px solid #e6e6e6 !important;
}
sj-search-box #searchIcon.sj-search-box{
    color: #8c8c8c !important;
}
#material-one-left, #material-one-right{
	height: 32px !important;
}
#material-breadcrumbs{
	height: 32px !important;
    font-size: 16px !important;
    line-height: 32px !important;
}
.cluster{
	margin: 64px auto 0 auto !important;
}
.cluster[data-col-count="6"] .material-card{
	width: calc((100%/8) - 16px) !important;
}
.material-card{
	width: calc((100%/8) - 16px) !important;
}
paper-button{
	border-radius: 0px !important;
    margin-top: -6px !important;
}
.cluster .header .cluster-title{
	margin-top: 2px !important;
}
sj-search-box.sj-search-box-0[opened]{
	box-shadow: 0 8px 10px 1px rgba(0, 0, 0, 0), 0 3px 14px 2px rgba(0, 0, 0, 0), 0 5px 5px -3px rgba(0, 0, 0, 0) !important;
    border: 1px solid #262626 !important;
}
sj-search-box[opened]:not([num-suggestions="0"]) #suggestContainer.sj-search-box{
	padding: 0px 0 !important;
}
sj-search-suggestion{
    border-bottom: 1px solid #f2f2f2 !important;
}
sj-search-box #clearButton.sj-search-box{
	top: -4px !important;
    color: #8c8c8c !important;
}
.material-card[data-is-listen-now="true"]{
	height: 0px !important;
    visibility: hidden !important;
}
sj-search-suggestion iron-icon.sj-search-suggestion{
	color: #e6e6e6 !important;
}
body.material{
	background-color: #fbfbfb !important;
}
.material .cluster-text-protection{
	background-color: #fbfbfb !important;
}
.material .cluster-text-protection::before{
	background-color: #fbfbfb !important;
}
.material .solid-hero core-image#material-hero-image, .material .solid-hero iron-image#material-hero-image{
	background-color: #fbfbfb !important;
}
core-header-panel#content-container.has-hero-image #app-bar-text-protection, paper-header-panel#content-container.has-hero-image #app-bar-text-protection{
	background-image: -webkit-linear-gradient(top,rgba(0,0,0,0),rgba(0,0,0,0)) !important;
    background: linear-gradient(to bottom,rgba(0,0,0,0),rgba(0,0,0,0)) !important;
}
.material paper-fab, .material .material-container-details paper-fab, .material .material-container-details sj-fab{
	background: #fff !important;
    border: 1px solid #e6e6e6 !important;
}
paper-fab.paper-fab-0[elevation="1"]{
	box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0), 0 1px 5px 0 rgba(0, 0, 0, 0), 0 3px 1px -2px rgba(0, 0, 0, 0) !important;
}
paper-fab.paper-fab-0{
	color: #262626 !important;
    top: -16px !important;
	right: 16px !important;
}
.material-card{
	border-radius: 0px !important;
    box-shadow: 0 1px 4px 0 rgba(0,0,0,0) !important;
    margin: 4px !important;
    border: 1px solid #e6e6e6 !important;
}
.material-card:hover{
	border: 1px solid #262626 !important;
}
.material-card .title{
	font-size: 14px !important;
}
.material-card .sub-title{
	font-size: 12px !important;
}
.cluster .header .subtitle{
	visibility: hidden !important;
    height: 0px !important;
}
.cluster .header.has-subtitle{
	height: 32px !important;
}
.material .button.primary, .material paper-button.material-primary, .material sj-paper-button.material-primary, .material paper-button.material-primary:not([raised])[focused], .material .simple-dialog-buttons button.goog-buttonset-default{
	background: #fff !important;
    border: 1px solid #e6e6e6 !important;
    color: #000 !important;
}
.material .button.primary:hover, .material paper-button.material-primary:hover, .material sj-paper-button.material-primary:hover, .material paper-button.material-primary:not([raised])[focused]:hover, .material .simple-dialog-buttons button.goog-buttonset-default:hover{
	background: #fff !important;
    border: 1px solid #262626 !important;
    color: #000 !important;
}
#player{
	box-shadow: 0 0 8px rgba(0,0,0,0) !important;
    background-color: #fff !important;
    border-top: 1px solid #e6e6e6 !important;
}
#player.material{
	height: 64px !important;
    min-height: 64px !important;
}
#player.material .material-player-middle{
	height: 60px !important;
}
#player.material #material-player-right-wrapper{
	height: 60px !important;
}
#player.material #material-player-left-wrapper{
	height: 60px !important;
}
.material-shadow-z1{
	box-shadow: 0 1px 4px 0 rgba(0,0,0,0) !important;
    background-color: #fbfbfb !important;
}
.solid-hero .material-detail-view .has-hero-image{
	margin-top: 32px !important;
}
.material-detail-view .material-container-details .info{
	height: 128px !important;
}
.material-container-details{
	height: 128px !important;
}
.material-detail-view .material-container-details .cover .cover-card{
	height: 128px !important;
	width: 128px !important;
}
.material-detail-view .material-container-details .cover .cover-card .image{
	height: 128px !important;
	width: 128px !important;
}
.material-detail-view .material-container-details .info{
	margin: 0 0px 0 128px !important;
}
#nav-content-container{
	bottom: 64px !important;
}
.material .goog-menu{
	padding: 0px 0px!important;
}
.material #player.material .material-player-middle sj-icon-button[data-id="play-pause"]:not([disabled]), .material #player.material .material-player-middle paper-icon-button[data-id="play-pause"]:not([disabled]) iron-icon{
	color: #262626 !important;
}
#player.material .player-rating-container{
	background: #fff !important;
}
#material-player-right-wrapper .player-top-right-items{
top: -32px !important;
}
#player.material #playerSongInfo .image-wrapper{
	height: 64px !important;
    width: 64px !important;
}
#player.material .player-progress-wrapper{
	left: 64px !important;
}
#material-player-progress #sliderBar{
	height: 2px !important;
}
.paper-progress-0 #progressContainer.paper-progress, .paper-progress-0 .indeterminate.paper-progress:after{
	height: 2px !important;
}
.material paper-slider #sliderKnobInner.paper-slider, .material paper-progress #primaryProgress.paper-progress{
	background-color: #262626 !important;
}
::-webkit-scrollbar { 
    display: none; 
}
sj-mini-queue.sj-mini-queue-0{
	box-shadow: 0 16px 24px 2px rgba(0, 0, 0, 0), 0 6px 30px 5px rgba(0, 0, 0, 0), 0 8px 10px -5px rgba(0, 0, 0, 0) !important;
    border: 1px solid #e6e6e6 !important;
}
#queue-overlay::after{
    box-shadow: -12px 12px 15px 0px rgba(0,0,0,0) !important;
    visibility: hidden !important;
}
sj-mini-queue.scrolled #mini-queue-details.sj-mini-queue::after{
	box-shadow: inset 0px 5px 6px -3px rgba(0, 0, 0, 0) !important;
    border-bottom: 1px solid #262626 !important;
    bottom: 0px !important;
}
#dropShadow.has-shadow.paper-header-panel{
	box-shadow: inset 0px 5px 6px -3px rgba(0, 0, 0, 0) !important;
    height: 0px !important;
}
.nav-toolbar .menu-logo{
	margin-top: -16px !important;
    visibility: hidden !important;
}
#left-nav-close-button{
    margin-top: -16px !important;
}
#bottomBar.paper-toolbar{
	top: 48px !important;
    height: 48px !important;
}
#material-app-bar #header-tabs-container{
	height: 48px !important;
}
paper-tabs.tab-container{
	height: 48px !important;
}
#material-app-bar #action_bar_container{
	height: 48px !important;
}
#material-app-bar #countSummary{
	line-height: 48px !important;
}
.material-card-grid[data-virtual="true"]{
	margin-top: 64px !important;
}
.paper-tabs-0 #selectionBar.paper-tabs{
	background-color: #262626 !important;
}
.songlist-container{
	margin: 48px 8px 0 !important;
}
core-drawer-panel core-image#material-hero-image, paper-drawer-panel iron-image#material-hero-image{
	padding-top: 25% !important;
}
.material-detail-view .has-hero-image{
	margin-top: calc(25vw - 64px - 132px) !important;
}
.material-container{
	border-radius: 0px !important;
    border: 1px solid #e6e6e6 !important;
}
.material .more-songs-container{
	color: #262626 !important;
}
.material-detail-view .material-container-details .info .description-wrapper{
	height: 0px !important;
    visibility: hidden !important;
}
paper-header-panel.paper-header-panel-0[mode=scroll] #mainContainer.paper-header-panel{
	border-right: 1px solid #e6e6e6 !important;
}
.column .material-card[data-size="small"][data-type="imfl"] .image-wrapper{
	height: 0px !important;
    visibility: hidden !important;
}
.column .material-card[data-size="small"][data-type="imfl"]{
	height: 0px !important;
    visibility: hidden !important;
}
.material .cluster.material-cluster .lane-button core-icon, .material .cluster.material-cluster .lane-button iron-icon{
	color: #262626 !important;
}
.material .material-detail-view .material-container-details .read-more-button{
	color: #262626 !important;
}
.material-detail-view .artist-details .bio-wrapper .bio{
	max-height: 0px !important;
    height: 0px !important;
}
.material-detail-view .material-container-details .read-more-button-wrapper{
	margin-top: -5px !important;
}
.material paper-action-dialog sj-paper-button, .material paper-dialog .buttons paper-button{
	color: #262626 !important;
}
.material-detail-view .material-container-details .actions{
	margin-top: -98px !important;
    border-top: 0px !important;
}
.material-detail-view .material-container-details .actions sj-paper-button:first-of-type, .material-detail-view .material-container-details .actions sj-icon-button:first-of-type, .material-detail-view .material-container-details .actions paper-button:first-of-type, .material-detail-view .material-container-details .actions paper-icon-button:first-of-type{
	margin: 0 6px 0 356px !important;
    padding-bottom: 6px !important;
}
.material-detail-view .material-container-details .actions sj-paper-button:last-of-type, .material-detail-view .material-container-details .actions sj-icon-button:last-of-type, .material-detail-view .material-container-details .actions paper-button:last-of-type, .material-detail-view .material-container-details .actions paper-icon-button:last-of-type{
	margin: 0 0 0 6px !important;
    padding-bottom: 6px !important;
}
.material-detail-view .material-container-details .actions sj-paper-button, .material-detail-view .material-container-details .actions sj-icon-button, .material-detail-view .material-container-details .actions paper-button, .material-detail-view .material-container-details .actions paper-icon-button{
	margin: 0 4px 0 6px !important;
    padding-bottom: 6px !important;
}
.material-detail-view .artist-details{
	border-radius: 0px !important;
    border: 1px solid #e6e6e6 !important;
}
paper-dialog.paper-dialog-0{
    border: 1px solid #262626 !important;
    box-shadow: 0 16px 24px 2px rgba(0, 0, 0, 0), 0 6px 30px 5px rgba(0, 0, 0, 0), 0 8px 10px -5px rgba(0, 0, 0, 0) !important;
}
iron-overlay-backdrop.iron-overlay-backdrop-0{
	background-color: #000 !important;
}
iron-overlay-backdrop.iron-overlay-backdrop-0[opened]{
	opacity: 0.7 !important;
}
.entity-card.material-card{
	border: 0px !important;
}
.material .top-charts-view .song-row [data-col="index"] .content, .material .top-charts-view .song-row [data-col="index"] .column-content, .material .material-card .details .left-items .index{
	color: #262626 !important;
}
.material .explicit, .material .material-card .explicit, .material .material .song-row .explicit, .material .album-view .material-container-details .info .title .explicit{
	background: #262626 !important;
}
.material-container-details+.song-table{
	margin-top: 0px !important;
}
#player sj-icon-button[data-id="show-miniplayer"], #player paper-icon-button[data-id="show-miniplayer"]{
	visibility: hidden !important;
    height: 0px !important;
}
#player.material .material-player-middle paper-icon-button[data-id="play-pause"]{
	width: 64px !important;
    height: 64px !important;
}
.material .nav-item-container.selected, .material #nav_collections .nav-item-container.selected{
	color: #000 !important;
	border-left: 2px solid #000 !important;
}
.material .nav-item-container.selected core-icon, .material .nav-item-container.selected iron-icon, .material #nav_collections .nav-item-container.selected core-icon, .material #nav_collections .nav-item-container.selected iron-icon{
	color: #000 !important;
}
.material-detail-view .artist-details .name{
	text-align: left !important;
}
.material .song-row td:first-child, .material .header-row th:first-child{
	background-color: #fbfbfb !important;
}
.material .song-row td, .material .upload-progress-row td{
	background-color: #fbfbfb !important;
}
}
