@-moz-document domain("dcard.tw") {
/* --------------------------Body--------------------- */

body {
    background: #212121 !important;
    color: #ccc;
}

/* Links */
a {
    color: #7EBCFF;
}
a:hover {
    color: #ddd;
}
/* ---------------------Nav--------------------- */
.navbar-inverse {
    background-color: #37474F;
    border-color: #263238;
}
.badge {
    background-color: #FF6969 !important;
}

/* ---------------------Main--------------------- */
.segment,
.forum-first-affix,
.main-content,
.modal-content,
.card {
    background-color: #263238;
}

/* Alert Background */
.alertify-log-success {
    background-color: rgba(128, 203, 196, .9) !important;
}
.alertify-log-error {
    background-color: rgba(255, 105, 105, .9) !important;
}

/* ---------------------Buttons--------------------- */
/* Btn-Default */
.btn-default {
    color: #eee;
    background-color: #384248;
    border-color: #333;
}
.btn-default:hover {
    color: #eee;
    background-color: #4A575F;
    border-color: #333;
}
.btn-default.active,
.btn-default:active,
.btn-default:focus,
.btn-default:hover,
.open .dropdown-toggle.btn-default {
    color: #eee;
    background-color: #263238;
    border-color: #333;
}

/* Btn-Primary */
.btn-primary,
.button {
    color: #eee;
    background-color: #7EBCFF;
    border-color: #333;
}
.btn-primary.active,
.btn-primary:active,
.btn-primary:focus,
.btn-primary:hover,
.open .dropdown-toggle.btn-primary,
.button:active,
.button:focus,
.button:hover {
    color: #eee;
    background-color: #76B0EE;
    border-color: #333;
}
.btn-primary.disabled,
.btn-primary.disabled.active,
.btn-primary.disabled:active,
.btn-primary.disabled:focus,
.btn-primary.disabled:hover,
.btn-primary[disabled],
.btn-primary[disabled].active,
.btn-primary[disabled]:active,
.btn-primary[disabled]:focus,
.btn-primary[disabled]:hover,
fieldset[disabled] .btn-primary,
fieldset[disabled] .btn-primary.active,
fieldset[disabled] .btn-primary:active,
fieldset[disabled] .btn-primary:focus,
fieldset[disabled] .btn-primary:hover {
    background-color: #6CA2DB;
    border-color: #333;
}

/* Subscribed */
.subscribed {
    color: #7EBCFF;
    background: none;
    border: none;
}

/* Btn-success*/
.btn-success,
.list .btn-follow:disabled,
.list .followed,
.alertify-button-ok {
    background-color: #80CBC4;
    border-color: #79C0B9;
}
.btn-success.active,
.btn-success:active,
.btn-success:focus,
.btn-success:hover,
.open .dropdown-toggle.btn-success,
.list .followed:hover,
.alertify-button-ok,
.alertify-button-ok:focus,
.alertify-button-ok:hover {
    background-color: #7AC2BB;
    border-color: #79C0B9;
}

/* Btn-Like */
.list .btn-like:disabled,
.list .liked,
.list .liked:hover {
    background-color: #FF6969;
    color: #FF6969;
}
.list .comment .btn-like {
    background: none !important;
}

/* Button Text */
.btn-group > button {
	color: #fff !important;
}

/* Hyper-Button */
.hyper-button {
    color: #eee;
}

/* Alertify-Button */
.alertify-button-cancel,
.alertify-button-cancel:focus,
.alertify-button-cancel:hover {
    background-color: #FF6969;
    border-color: #FF5353;
}

/* JoinUs Button */
a#JoinUs {
	background-color: #37474F;
}

/* ---------------------Form--------------------- */
.form-control {
    background-color: #607D8B;
    color: #ddd;
}

/* PlaceHolder */
::-webkit-input-placeholder {
    color: #ccc !important;
}
:-moz-placeholder {
    color: #ccc !important;
}
::-moz-placeholder {
    color: #ccc !important;
}
:-ms-input-placeholder {
    color: #ccc !important;
}
:placeholder-shown {
    color: #ccc !important;
}

/* ---------------------Forums--------------------- */
#side .active,
#side .active:hover {
    background-color: #37474F;
}
#side .item:hover {
    background-color: rgba(64, 196, 255, .3);
}

/* ---------------------List--------------------- */
/* Background */
.list .highlight {
    background-color: #384248;
}
.list .unhighlight {
    background-color: #4A575F;
}

/* Text */
.list .item {
    color: #ddd;
}
.list .hover,
.list .item:hover {
    color: #bbb;
    background-color: rgba(64, 196, 255, .3);
}
.list .item .item-comment,
.list .item .item-like {
    color: #ccc;
}
.list .first .first-comment,
.list .first .first-like,
.list .first .first-title,
.list .first .first-author,
.list .first .first-forum,
.list .first .first-create,
.list .item .item-author.all,
.list .item .item-forum.all,
.list .item .item-create,
.list .item .item-author,
.list .item .item-forum {
    color: #ccc;
}

/* Circles */
.item-title .circle[style="background: rgb(185, 74, 72);"] {
    background: #FF6969 !important;
}
.item-title .circle[style="background: rgb(38, 109, 150);"] {
    background: #7EBCFF !important;
}

/* Pinned */
.item-title>span[ng-show="post.pinned&&!showForumField()"] {
    color: #FF6969 !important;
}

/* Alert */
.alert-info {
    background-color: #384248;
    border-color: #384248;
}

/* Search */
div[ng-show="schoolRemind"] {
    background-color: #384248 !important;
}

/* ---------------------Article--------------------- */
/* Background */
.post-modal .modal-content {
    background: #212121
}

/* Board Name */
.board-name,
button.hidden-xs.btn.btn-default.hyper-button.button-back-list {
    color: #7EBCFF;
}

/* Comments */
.list .count {
    background-color: #37474F
}
.list .comment .user {
    color: #ddd;
}
.list .comment .floor,
.list .comment .time {
    color: #ccc;
}
.content>p>span[style="color:#428BCA"] {
    color: #7EBCFF !important;
}

/* Dcard Link */
.dcard-link-info {
    background-color: #384248;
}
.dcard-link-info>div>div>div:first-child {
    color: #eee !important;
}
.dcard-link-info>div>div>div:last-child {
    color: #ccc !important;
}

/* Host's Comment */
.user>span[ng-show="comment.host"] {
    color: #A1FFF6;
}

/* Comment Input */
.scroll-op-bar .float-comment .title {
    background-color: #37474F;
    border-color: #263238;
}
.scroll-op-bar-comment,
.scroll-op-bar .float-ops {
    background-color: #263238;
}
.scroll-op-bar .float-comment .btn-close {
    background: none;
}

/* ---------------------Profile--------------------- */
.table-striped>tbody>tr:nth-child(odd)>td,
.table-striped>tbody>tr:nth-child(odd)>th {
    background-color: #384248;
}

/* ---------------------Friends--------------------- */
/* Background */
.well {
    background-color: #4A575F;
    border: 1px solid #384248;
}

/* Send Mail */
.float-comment {
    background-color: #263238;
}
.receiver-title {
    color: #ccc;
}
.receiver-name {
    color: #eee;
}

/* ---------------------Feedback--------------------- */
.logo-container {
    background-color: #37474F;
}

/* ---------------------Mail--------------------- */
.news a {
    color: #ddd;
}
.news a:hover {
    color: #ccc;
}
.news a strong {
    color: #7EBCFF;
}
.news a:hover strong {
    color: #76B0EE;
}

/* ---------------------DropDown Menu--------------------- */
.dropdown-menu {
    background-color: #384248;
}
.dropdown-menu>li>a {
    color: #7EBCFF;
}
.dropdown-menu>li>a:focus,
.dropdown-menu>li>a:hover {
    color: #ddd;
}

/* ---------------------Notification--------------------- */
/* Background */
#notification-ul {
    background-color: #384248 !important;
}

/* Text */
#notification-util>div>div {
    color: #eee !important;
}
#notification-util>div>a {
    color: #ccc !important;
}
.delete:hover {
    color: #FF6969 !important;
}

/* Icon */
.notification-list img {
    filter: grayscale(70%) brightness(300%);
    -webkit-filter: grayscale(70%) brightness(300%);
	-moz-filter: grayscale(70%) brightness(300%);
  	-ms-filter: grayscale(70%) brightness(300%);
	-o-filter: grayscale(70%) brightness(300%);
}

/* Notifications */
.unread {
    background-color: rgba(64, 196, 255, .2) !important;
}
.read {
    background-color: #4A575F !important;
}
.read:hover,
.unread:hover {
    background-color: rgba(64, 196, 255, .1) !important;
}
.notification-list {
    color: #7EBCFF !important;
}
.notification-list:hover {
    color: #ddd !important;
}

/* ---------------------Dialog--------------------- */
.rules {
	color: #eee !important;
}

.modal-container,
.alertify-dialog {
    background-color: #4A575F;
}

/* ---------------------AD--------------------- */
#misc,
.adsbygoogle,
.main-content .topic iframe {
    filter: grayscale(100%);
    filter: gray;
	-webkit-filter: grayscale(100%);
	-moz-filter: grayscale(100%);
	-ms-filter: grayscale(100%);
	-o-filter: grayscale(100%);
}

/* ---------------------Maintenance Message--------------------- */
h1 {
	color: #fff;
}

/* ---------------------Register--------------------- */
.strike span{
	color: #fff !important;
}
.strike span:before,
.strike span:after{
	background: #fff !important;
}

a[ng-click="useEmailSignup=1"] {
	color: #ccc !important;
}

.fresh-modal {
	background: #263238 !important;
}
}

@-moz-document url("https://www.dcard.tw/terms.html") {
/* ---------------------Terms--------------------- */
.content {
    background: #212121 !important;
    color: #ccc;
}

.nav {
    background-color: #37474F;
}

.footer {
    background-color: #263238;
}

.title,
li {
    color: #eee !important;
}

.content a {
    color: #7EBCFF;
}
.content a:hover {
    color: #ddd;
}
}

/*[[add-ons]]*/
/*[[add-ons2]]*/

style_setting_options.value:
@-moz-document url("https://www.dcard.tw/dcard") {
/* ---------------------Hide Profile--------------------- */
/* Hidder */
.dcard-photo-placeholder:before {
    content: "";
    display: block;
    width: 300px;
    height: 400px;
    max-width: 100%;
    margin: 0 auto;
    background: #37474F url(https://www.dcard.tw/img/logo.png) no-repeat center;
}

/* Photo */
.dcard-photo-placeholder img {
    opacity: 0;
    width: 300px;
    height: auto;
    margin-top: -400px;
    transition: opacity 1s;
    -webkit-transition: opacity 1s;
    -moz-transition: opacity 1s;
    -o-transition: opacity 1s;
}
.dcard-photo-placeholder img:hover {
    opacity: 1;
    transition: opacity 1s;
    -webkit-transition: opacity 1s;
    -moz-transition: opacity 1s;
    -o-transition: opacity 1s;
}

/* Text */
table>tbody>tr:first-child>td {
    color: rgba(204, 204, 204, 0);
    transition: all 1s;
}
table>tbody>tr:first-child>td:hover {
    color: rgba(204, 204, 204, 1);
    transition: all 1s;
}
}

@-moz-document domain("dcard.tw") {
/* ---------------------Outside Likes--------------------- */
a[ng-href="/f/whysoserious/p/741910"] .item-like span[ng-bind="post.likeDesc"] {
	display: none;
}
a[ng-href="/f/whysoserious/p/741910"] .item-like:after {
	content: "/*[[likes]]*/";
}
}
@-moz-document url("https://www.dcard.tw/f/whysoserious/p/741910") {
/* ---------------------Inside Likes--------------------- */
button[ng-click="likePost()"] span {
    display: none;
}
button[ng-click="likePost()"]:after {
    content: "喜歡 /*[[likes]]*/";
}
}
