css:
@-moz-document domain("hitbox.tv") {
#chat {
    width: 400px !important;
}

#leftcolumn {
    padding-right: 400px !important;
}

.poll .ng-hide {
    display: block !important;
}

#chat-tabs, .chat-input-wrap, .tse-scrollbar, .poll-close,
.poll-ended-note, .pollResults .item.userpick:before,
.userpick .winner .ng-binding:before, .chat-sticky, .poll-buttons, 
.poll-ended-note .ng-binding .ng-hide, .poll-minimised-msg {
    display: none !important;
    opacity: 0 !important;
}

.chat-main {
    top: 0 !important;
}

.poll-buttons {
    height: 0 !important;
}

.chatPoll .poll.pollResults .item.winner {
    opacity: 1 !important;
    font-weight: 600 !important;
}
.chatPoll .poll.pollResults .bar {
    height: 3px !important;
}
}
