@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("hdrezka.me") {

  .has-brand.active-brand.pp.fixed-header.no-touch {padding-top: 0px !important;}
   #skin_5681, #skin_link, .skin-block {display: none !important;}
  
 body, .has-brand.active-brand.pp.fixed-header.b-christmas.no-touch.rootmedial_load
  
  {


  background-image: url("/*[[back]]*/") !important; background-color: #FFFFFF !important;

	background-attachment: fixed !important;

	background-clip: border-box !important;

	background-origin: padding-box !important;

	background-position: center center !important;

	background-repeat: no-repeat !important;

  background-size: cover !important;

}

  
/*.b-post {background-color: rgba(255, 255, 255, 0.8)}*/

.b-content__sidebar, .b-content__inline_sideprobar, div > .b-content__inline_inner.b-content__inline_inner_mainprobar.clearfix > div:nth-of-type(2), .rm-b {display: none !important;}

.b-content__main > div:nth-child(13), .b-content__main > div:nth-child(14), #vkwidget1, #bn_d200998dcb, #bn_1dd334bf10, .telecine.invisible, .html5videob5db4df1 {display: none !important;}
  
  
 .b-collections__newest, .b-newest_slider_wrapper {background: rgba(88,88,88, 0.0)}
  
   .b-wrapper, .b-tophead_wrapper fixed, #main, #wrapper, .b-footer {width: 1138px !important;}
    .b-newest_slider {width: auto !important;}
 .b-content__inline_inner.b-content__inline_inner_mainprobar.clearfix:nth-child(1) {width: 1138px !important; padding-left: 30px;}

  /*отступы*/
  .b-tophead-left.pull-left, .b-topnav__inner.clearfix, .b-topnav__item-link.active_section, .b-content__htitle, .b-content__main_filters_item, .b-content__description, .speedbar, .b-footer__slogan, .b-post__title, .b-post__origtitle, .b-post__infolast, .b-post__description_title, .b-post__description_text, .b-footer__menu {padding-left: 10px;}
  
  .b-tophead-right.pull-right, .b-post__infotable_right_inner, .b-post__description_text {padding-right: 10px;}
  
  .b-post__support_holder {padding: 40px 50px 10px 10px;}
  .b-post__social_holder {padding: 13px 10px 11px 250px;}
  .b-search__form {width: 215px;}
  
/*  #vkwidget1 {position: absolute; bottom: 100px; right: 20px !important;}*/
  
 .b-content__columns {padding-right: 0px !important;}
  
  .b-sidelist, .b-sidetitle, #addcomment-title {text-align: center}

  #comments {width: 95%}

  .b-player, #player {background: #fff !important;}
  
  .b-post__support_holder {color: #000; width: 113%;}
 
  #videoplayer iframe, #ownplayer {width: 830px; height: 390px; padding-top: 0px}
  
 #player {padding-right: 190px; padding-top: 10px}

