css:
@-moz-document domain("klavogonki.ru") {
/* Личный тюнинг */
#speedpanel {background:none}
#speedpanel-canvas, #speed-label, #errors-label, #speedpanel-back, #speedpanel-top, #speedpanel-arrow {display: none;}
#status table td { height: 10px; }
#status table td#gamedesc { color: #ccc !important; margin: 0; padding: 0; }




/* Компактность*/
#head #logo {padding: 5px 30px;}
#logo img {height: 40px;}
#head .right {padding:0;}
#head .right .menu {margin-top:0;}
.gameblock {margin: 0}



.player/* Уменьшение визуального шума: приглушение второстепенных */
.voc_desc,
.voc_rating,
.people,
.imobilco-link { display: none; }

#typeblock #bookinfo #again .hotkey { color: #aaa; display: none; }
#again .note,
#gametype-mdash { display: none; }

#howtoplay .voc_desc { display: block; }



/* Уменьшение визуального шума: приглушение второстепенных элементов */
#report_link,
#errorwork {
    opacity: .4;
}
.player, .player .divider { background:none; }



/* Фикс налезающих блоков с установленным расширением KlavoTools */
#players-count-lbl {
    position: absolute;
    padding-left: 10px;
}
#sort-panel {
    padding-top: 60px;
}
#autosort + label {
    top: 0 !important;
    margin-left: 5px;
}



/* KlavoTools: список сохраннённых игр */
.recent-game.pin-game {
    background-color: #F8F4E6;
}
.recent-game.pin-game:hover {
    background-color: #e7e3d8;
}
.recent-game,
.recent-game:hover,
.recent-game.pin-game,
.recent-game.pin-game:hover {
    border: 0 !important;;
    margin: 0 0px 0 0 !important;
}



/* Выделение себя фоном в общем списке участников заездов */
.you {
    color: #428BCA;
}



/* Шапка: текущий раздел в меню */
#head .right .menu a {
    width: auto;
    margin-right: 30px;
}
#head .right .menu a.active {
    font-weight: bold;
    background: none;
    color: black;
    text-decoration: underline;
}

/* Шапка: ссылка на активный словарь */
.userpanel .gametype {
    text-align: left;
}
#gametype-link {
    color: #B7FFB3 !important;
    border-color: #B7FFB3 !important;
}

/* Шапка: информация об очках и рейтинге */
.userpanel #userpanel-dailybonus,
.userpanel #race-scores {
    font-weight: normal !important;
    font-size: 0px;
    top: 0 !important;
}
.userpanel #userpanel-dailybonus span,
.userpanel #race-scores {
    font-size: 14px !important;
}
.userpanel #userpanel-dailybonus span:before {
    content: "+";
}
.userpanel #race-scores {
/*
    margin-right: 5px;
*/
    top: -1px !important;
    background-color: #B7FFB3;
    font-weight: bold !important;
    font-size: 12px !important;
    border-radius: 8px;
    margin: 0 5px 0 5px;
    padding: 2px 5px 4px 5px !important;
    color: #000 !important;
}
.daily-scores small {
    font-size: 12px !important;
    color: #000 !important;
    font-weight: bold !important;
}



/* Профиль */
#profile-index .stats {
    border: none;
}


/* Заезд: косяк с медальками */
.name_content {overflow: inherit;}
.nick_content {max-width: 96px;overflow: hidden;}

/* Заезд: информация о заезде и статус (рядом со спидометром) */
#status {
    background: transparent;
    padding-left: 0;
}
#status table { width: 740px; }
#status table .gametype-sign-wrapper { width: 80px; }

#waiting {
    font-size: 1px;
	color: #fff;
	line-height: 12px;
}
/* Заезд: таймер */
#waiting_timeout,
#racing_time {
    font-size: 40px;
    display: block;
	color: #000;
	margin-left: -3px;
}
#status-inner {
    opacity: 1;
}


/* Заезд: играть ещё раз */
#typeblock #bookinfo #again > a,
#typeblock #bookinfo #again td:nth-child(2) a {
    font-weight: bold;
    font-size: 18px;
    line-height: 18px;
}

.imobilco th,
.imobilco td div:nth-child(1) { display: none; }
.imobilco .author { text-align: center; }
.imobilco .name { margin-bottom: 20px; text-align: center; }
#bookinfo .bar {display:none}

/* Заезд: текст */
#typeblock {
    background-color: #F9F5E9;
/*
    -webkit-box-shadow: inset 0px -15px 20px -20px rgba(0,0,0,0.75);
    -moz-box-shadow: inset 0px -15px 20px -20px rgba(0,0,0,0.75);
    box-shadow: inset 0px -15px 20px -20px rgba(0,0,0,0.75);
*/
    -webkit-box-shadow: 0px 10px 35px -15px rgba(0,0,0,0.75);
    -moz-box-shadow: 0px 10px 35px -15px rgba(0,0,0,0.75);
    box-shadow: 0px 10px 35px -15px rgba(0,0,0,0.75);
    border-radius: 12px;
}
.r .bl,
.r .br,
.r .tl,
.r .tr {
    background: none;
}
#typefocus { background-color: #fff; }

/* Заезд: показать ошибки */
#errors_tab {
    background: #f9f5e9;
    border-radius: 0 0 12px 12px;
}
#errors_tab,
#errors_text p {
    background: none;
}
#errors_tab a:hover,
#host_start:hover {
    border-color: #b20;
}

/* Приведения стиля заглушки для скрытого текста к обычному стилю текста */
#typeblock #hiddentext {
    background-color: #F9F5E9 !important;
    color: #888;
    font-weight: normal;
    text-shadow: none;
    text-align: left;
    padding: 0;
    font-size: 28px;
    opacity: .5;
}



/* Фикс спидометра для широких экранов (слева торчит копия) */
#speedpanel-back,
#speedpanel-top,
#speedpanel-arrow {
    left: -2000px !important;
}



/* Подвал */
#footer table { height: 100px; }
#footer a { margin: 0 5px; }
#copyright { opacity: .4; }
#footer table td:nth-child(1) { display: none; }
#footer table td:nth-child(2) { padding-right: 20px !important; }



/* Подпись рангов */
.rang1:after,
.rang2:after,
.rang3:after,
.rang4:after,
.rang5:after,
.rang6:after,
.rang7:after,
.rang8:after,
.rang9:after {
    content: '—';
    border: none;
    font-size: 9px;
    font-weight: normal;
    opacity: .7;
    display: none;
}

#players .rang1:after,
#players .rang2:after,
#players .rang3:after,
#players .rang4:after,
#players .rang5:after,
#players .rang6:after,
#players .rang7:after,
#players .rang8:after,
#players .rang9:after {
    display: none;
    position: absolute;
    top:-7px;
    left:1px;
}

#gamelist .rang1:after,
#gamelist .rang2:after,
#gamelist .rang3:after,
#gamelist .rang4:after,
#gamelist .rang5:after,
#gamelist .rang6:after,
#gamelist .rang7:after,
#gamelist .rang8:after,
#gamelist .rang9:after {
    display: block;
    margin-top: 3px;
}

.rang1:after { content: 'Новичок' !important; }
.rang2:after { content: 'Любитель' !important; }
.rang3:after { content: 'Таксист' !important; }
.rang4:after { content: 'Профи' !important; }
.rang5:after { content: 'Гонщик' !important; }
.rang6:after { content: 'Маньяк' !important; }
.rang7:after { content: 'Супермен' !important; }
.rang8:after { content: 'Кибергонщик' !important; }
.rang9:after { content: 'Экстракибер' !important; }
}
