css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://bgm.tv/m/"),
               url-prefix("http://bgm.tv/update/"){
  html{background-color: #222222!important;}
  BODY{width: 100%!important;max-width: 800px!important;margin-left: auto!important;margin-right: auto!important;
    box-shadow: 0px 0px 10px rgba(0, 0, 0,0.8) !important;
  }
}

@-moz-document url-prefix("http://bgm.tv/m/prg") {
  BODY{background-color: rgba(34,34,34,1)!important;}
  /*时空管理局*/
  #navNeue{background:unset!important;background-color:black!important;height:40px!important;border-bottom: 2px solid #369CF8!important; }
  #navNeue > li > a,#navNeue > li > a.focus{
  font-weight: normal !important;line-height: 40px!important;
  text-shadow:unset!important;}
  #navNeue > li > a{color:gray!important;}
  #navNeue > li > a.focus{background:#369CF8!important;color:white!important;}
  #navNeue > li > a:hover{color:#369CF8!important;}
  #navNeue > li > a.focus:hover{color:white!important;}
  /*全部、动画、三次元、书籍*/
  #prgCatrgoryFilter{background:#333!important;border-radius:unset!important;}
  #prgCatrgoryFilter > li > a{border:0!important;color:#36A3DB!important;font-size:14px!important;font-weight: bold!important;box-shadow: 0px 1px 1px rgba(0, 0, 0,0.8) !important;}
  #prgCatrgoryFilter > li > a:hover{font-size:14px!important;font-weight: bold!important;}
  #prgCatrgoryFilter > li > a.focus{box-shadow: 1px 1px 2px rgba(0, 0, 0,0.8) !important;padding-bottom: 8px!important;font-size:16px!important;border-bottom:2px solid gray!important;}/*当前选项*/
  /*展开、紧凑按钮*/
  DIV#prgManagerHeader > A#prgManagerSettings{box-shadow: 0px 0px 10px rgba(0, 0, 0,0.8) !important;}
  /*正在观看的项目 ALL*/
  div.subjectItem{border-bottom: 1px solid rgba(34,34,34,0.5)!important;}
  div.subjectItem > div.clearit.subjectPanel{
    background-color: rgba(34,34,34,1)!important;
    border:unset!important;
    border-radius:unset!important;
    }
  /*章节、收藏、当前观看进度 三个按钮栏*/
  div.clearit.subjectPanel > ul.tools.clearit{border-radius:unset!important;
    border-top:unset!important;
    background:#333!important;}
  .subjectItem > DIV.clearit.subjectPanel > ul.tools.clearit > LI > A{border:0 !important;
    font-weight:normal!important;
    font-family: arial,songti,Georgia, serif!important;
    font-size: 14px!important;
    color:gray!important;
    box-shadow:0px 0px 1px rgba(0,0,0,1)!important;
    }
  /*当前观看进度*/
  .subjectItem > DIV.clearit.subjectPanel > ul.tools.clearit > li:nth-child(3) > a{background:unset!important;}
  /*展开的章节栏*/
  .subjectItem > DIV.clearit.subjectPanel > .epPanel.prg_list.clearit{background:rgba(34,34,34,1)!important;
  box-shadow:inset 2px 5px 10px rgba(0,0,0,1)!important;
  padding:15px 0px 5px 10px!important;
  transition:opacity 0.3s linear!important;}
 /*观看进度条*/
  .progress.ll{background:unset!important;}/*进度条背景*/
  .btnSubmit.ll > input.btn{color:unset!important;background:#AAA!important;}/*更新按钮*/
  .progress.ll > .input > .inputtext{background:#AAA!important;} /*更新输入栏*/
  /*放送中*/
  .subjectItem > DIV.clearit.subjectPanel > DIV.header.clearit > SPAN{color:white!important;}
  /*弹出窗口*/
  #prgEpModal > DIV.wrapper{width: 100%!important;max-width: 800px!important;margin-left: auto!important;margin-right: auto!important;}
}
