css:
@-moz-document domain("g2a.com") {
:root {
	--main: #111;
	--bg1: #151515;
	--bg2: #1a1a1a;
	--bg3: #222;
	--bg4: #252525;
	--bg5: #2a2a2a;
	--bg6: #333;
}

body, html, .page--homepage, .page--category, .page--homepage, .page--search {
	background: var(--main) !important;
}
.section--loot, .footer-wrap, .footer-main, .product-sections__wrapper, .footer .footer-wrap {
	background: var(--bg1);
}
.guides__wrapper .guide__item {
	background: var(--bg1);
	border-color: var(--bg1);
}
.sticky-header-homepage, .sticky-header__bar {
	background: var(--bg1);
	border-top: 1px solid var(--bg6);
	box-shadow: 0 3px 6px rgba(0,0,0,0.1), 0 2px 3px rgba(0,0,0,0.1);
}
.btn.btn-secondary, .Card__base, .attributes-list, .product-section__attributes, .topbar-widget-mymarketplace .popover .popover-content {
	background: var(--bg2);
	border-color: var(--bg2);
}
.btn.btn-secondary:hover {
	background: var(--bg3);
	border-color: var(--bg3);
	color: #03A9F4;
}
.popover-content, .topbar-widget-footer {
	background: var(--bg3);
	border-color: var(--bg3);
}
.go-to-top-arrow {
	background: var(--bg4);
	opacity: 1;
}
.go-to-top-arrow svg {
	fill: #fff;
}
.footer-main, .footer-header .footer-padding, .product__title, .product-tags, .product-section, .attributes-list li, .filters__item, .btn-group .btn, .btn-group .btn:last-child, .panel-layout__row hr {
	border-color: var(--bg6)
}
.boku, .topbar-widget-mymarketplace .popover .popover-content .popover-options .option .option-icon {
	filter: invert(1);
	-webkit-filter: invert(1);
}
.fullscreen-search-container {
	top: 15px;
	overflow: hidden;
	border-radius: 3px;
}
.fullscreen-search-form {
	padding: 0;
}
.fullscreen-search-form-fields {
	border: none;
	padding: 0;
	margin: 0;
}
.fullscreen-search-input {
	font-size: 14px;
	line-height: 0;
	box-sizing: border-box;
	padding-left: 14px;
	color: #000;
}
.fullscreen-search-submit {
	background: #f2470f;
	display: flex;
	justify-content: center;
	align-items: center;
	border-radius: 0;
	width: 46px;
}
.fullscreen-search-submit .icon {
	fill: #fff;
}
.fullscreen-search-preloader {
	position: relative;
	left: -10px;
}
.fullscreen-search[data-results="true"] .fullscreen-search-form .results {
	padding: 5px 14px 10px 14px;
	border-top: 2px solid #f2470f;
	margin: 0;
}
.fullscreen-search-results .Card__base {
	background: #fff;
	border: 1px solid #e0e0e0;
}
.fullscreen-search-results .Card__base .Card__title, .fullscreen-search-results .Card__base .Card__price {
	color: #444;
}
.listing-header__sort .label {
	color: #fff;
	width: 60px
}
.listing-header hr {
	border-color: var(--bg6);
}
body.fullscreen-search-on {
	overflow: auto;
}
.product-offers .offer {
	background: var(--bg1);
	border-bottom: 1px solid var(--bg6)
}
.expander__fader {
	background-image: -webkit-gradient(linear,left top,left bottom,from(rgba(0,0,0,0.1)),to(rgba(20,20,20,1)));
	background-image: linear-gradient(180deg,rgba(0,0,0,0.1) 0,rgba(20,20,20,1));
}
.topbar-widget-platforms .popover-content a {
	border-color: var(--bg6);
	color: #fff;
}
.topbar-widget-platforms .popover-content a:hover {
	background: var(--bg5);
}
.cat-navbar .popover-subcategories::before {
	background-color: var(--bg6);
}





















.loot-banner__open, .loot-banner__open:hover, .footer-payments .more, .footer-links-column label, .categories-filter__title, .listing-header__query-results, .pagination .pagination-page, .product-tags > li, .cashback-widget__percentage, .cashback-widget__description, .shield-info__title, .shield-info__item, .shield-wrapper span.price, .user-widget .user-rating .rating-data > span:not(.separator), .user-widget .user-rating .rating-data, .product__price .price-label, .product-offers__title, .business-banner__description, .product-offers-header .icon, .product-offers-header .title, .navigation__link, .navigation__link:hover, .product-section__content__content, .product-section__title, .attributes-list__title, .attributes-list li > .attributes-list__value, .attributes-list li > strong, .guides__wrapper .guide__content .title, .guides__wrapper .guide__text, .product-section__content p:last-child, .product-section__content__description, .product__rating-popover .ratings-group p span, .popover-options .option, .popover-title, .listing-header__products-found, .listing-header__query-results, .filters__title, .price-filter__title, .price-filter__currency, .radio-filter__title, .radio > span, .price-filter__dash, .categories-filter__item--active span, .cat-navbar .popover-column .option--sub, .panel-layout .panel-layout__title .title h1, .filter-container label, .Card__skin-exterior-type {color: #fff;}

.section__header h2, .section__header h2 a, .section__header h2 a:hover, .sticky-header-homepage__header, .sticky-header-homepage__header a, .sticky-header-homepage__header a:hover, .Card__description, .Card__title, .Card__price, .Card--loot .Card__title a, .Card--loot .Card__description, .Card__loot-points, .listing-header__title-wrapper h1, .product__title {color: #fff; font-weight: 400;}

.sticky-header-homepage__header a:first-child .icon, .section__header h2 .icon, .topbar-widget-footer .icon, .filters .btn-link .icon {fill: #fff;}



}
