css:
@-moz-document domain("app.plex.tv"), domain("127.0.0.1"), domain("localhost") {
/* ================= Global settings ================== */

html, body, body.thumbnail
{ background-color: #5d0000;
    color: #F7D56C
}

.header, .footer
{ background-color: #121A23;
    color: #EBF0F5;
    border: 0px
}

.header a
{ color: #EBF0F5;
}

.footer a
{ color: #EBF0F5;
}

.help-block
{ color: #ECD075;
}

.text-success
{ color: #23F723;
}

.text-muted
{ color: #B99898;
}

.text-danger
{ color: #E32B2B;
    font-weight: 600
}
/*FF1919*/

.media-poster
{ border-radius: 5px;
    background-color: #880303;
}

.filter-bar
{ background-color: #6F0101;
    border-top: 1px solid #400101;
    border-bottom: 1px solid #400101;
}

.filter-bar .dropdown-friendly-name
{ color: #D89B50;
}

.caret-icon
{ color: #F9BE03;
}
/* ==================== Navigation Bar and Nav Bar Elements =============== */

.MenuContainer-menu-2HyzK
{ padding: 8px 0;
    width: 170px;
    border-radius: 4px;
    background-color: #720000;
    box-shadow: 0 4px 10px rgba(0, 0, 0, .3);
}

.ServerMenu-serverMenu-1-R2h
{ width: 15pc;
    max-height: 316px;
    background-color: #6C0000;
}

.Link-default-12wtj
{ color: #F5D79D;
}

.nav-bar
{ box-shadow: 0 0 0 3px rgba(0, 0, 0, .2);
}

div.nav-bar
{ background: url("http://i.imgur.com/7NsQWXf.png") no-repeat center !important;
    border-bottom: 1px solid black;
}

.background-container
{ background-image: url(http://i.imgur.com/5mwtBpv.png);
    background-size: cover;
}


.side-bar
{ background-image: linear-gradient(to right, rgba(70, 4, 4, 0.99), rgba(97, 3, 3, 0.94) 50%, rgba(0, 0, 0, .4)) !important;
}

.side-bar
{ width: 240px;
    padding: 0 5px 5px;
    overflow-x: visible;
}

.Sidebar-sidebar-4nJ9g
{ background-image: linear-gradient(to right, rgba(70, 4, 4, 0.99), rgba(97, 3, 3, 0.94) 50%, rgba(0, 0, 0, .4)) !important;
}

.ServerMenuButton-serverMenuButton-u9Vd6
{ background-color: rgba(0, 0, 0, .2);
    color: #f9be03;
}

.SidebarList-sidebarListHeader-2PjMw
{ padding: 15px 10px 10px 25px;
    color: #F7D56C;
    text-transform: uppercase;
    font-size: 9pt;
    font-family: Open Sans Bold, Helvetica Neue, Helvetica, Arial, sans-serif;
}

.action-bar
{ background-color: hsl(0, 96%, 12%);
}

.nav-bar-nav>li>a
{ padding-top: 0;
    padding-bottom: 0;
    line-height: 60px;
    color: #F5D79D;
}

.dropdown-menu
{ min-width: 160px;
    padding: 5px 0;
    margin: 2px 0 0;
    font-size: 14px;
    background-color: #980404;
    border: 1px solid #222;
    border: 1px solid rgba(0, 0, 0, .15);
    border-radius: 3px;
    box-shadow: 0 6px 12px rgba(0, 0, 0, .175);
}

.dropdown-menu>li>a
{ display: block;
    padding: 3px 20px;
    clear: both;
    font-weight: 400;
    line-height: 1.71428571;
    color: #D8D6D6;
    white-space: nowrap;
}

.dropdown-poster-toggle .media-poster.placeholder-friend
{ background-color: #EBF0F5;
    -webkit-box-shadow: inset 0 0 0 2px yellow;
    box-shadow: inset 0 0 0 1px #f9be03;
}

.show-side-bar.show-action-bar .filter-bar-fixed
{ left: 300px;
}

.dashboard-section-list-item .section-icon
{ color: #B7C4CE;
    background-color: inherit;
}

.dashboard-section-list-item:hover
{ background-color: rgba(63, 70, 77, .175);
}
/* ======================== Metadata Editor  AND Form Control Elements ======================*/

.modal-header
{ background-color: #690808;
    border-top: 1px solid #8A0A0A;
    border-bottom: 1px solid #4C0202;
}

.modal-header>h4>.modal-icon
{ margin-right: 5px;
    font-size: 18px;
    color: #FFC107;
}

.modal-content
{ background-color: #960F0F;
}

.modal-footer
{ background-color: #690808;
    border-top: 1px solid #860505;
    border-bottom: 1px solid #960F0F;
}

.modal-body-with-panes .modal-nav-pane
{ background-image: -webkit-gradient(left, linear, 0 0, 0 100%, from(rgba(26, 26, 26, 0.4)), color-stop(50%, rgba(19, 19, 19, 0.4)), to(rgba(0, 0, 0, 0.4)));
    background-image: -webkit-linear-gradient(left, rgba(26, 26, 26, .4), hsla(0, 0%, 7%, .4) 50%, rgba(0, 0, 0, .4));
    background-image: linear-gradient(to right, rgb(62, 2, 2), hsl(0, 86%, 15%) 50%, rgba(0, 0, 0, .4));
    background-repeat: no-repeat;
}

.edit-lock-addon
{ color: #121A23;
    background-color: #2A3139;
    border-color: #2A3139;
}
/*
.form-control
{ color: #eee;
}

.form-control
{ display: block;
    background-color: #790D0D;
}
*/

.form-control-search
{ height: 29px;
    padding: 3px 28px 2px 32px;
    background-color: #F5F1F1;
    border-radius: 5px !important;
}

.selectize-group .form-control.selectize-control.single .selectize-input input
{ color: #555;
    width: 100%;
    position: absolute;
    left: 14px;
    cursor: text;
}

div.selectize-dropdown.multi.form-control.lockable-field
{ color: #EEE;
    background-color: rgba(63, 70, 77, .99);
}

.files ul li
{ color: #EEE;
    background-color: rgba(150, 15, 15, .92);
}

ul.media-info-file-list.well
{ background-color: #690808;
}
/* ========================== Server Settings Page Elements ============================ */

.server-account-container>.server-account-info .server-account-username
{ overflow: hidden;
    text-overflow: ellipsis;
    white-space: nowrap;
    color: #23F723;
}

.check-for-updates-container>.install-info-label .success-icon
{ margin-right: 2px;
    color: #23F723;
}

.server-settings-nav-list-item.success .success
{ display: inline-block;
    color: #23F723;
}

.alert-list-container
{ background-color: #17212c;
}

.server-settings-nav-list-item.error .error
{ display: inline-block;
    color: #EDFF00;
}

.server-settings-nav-list-item.unknown .unknown
{ display: inline-block;
    color: #FFEB3B;
}

input#input_FriendlyName_c.form-control
{ background-color: #8E0808;
}
/*================= Drop Down Menu Elements ========================*/

a.delete-btn.has-danger:hover
{ color: yellow;
    background-color: red;
}

a.delete-btn.has-danger
{ color: red;
    background-color: yellow;
}

a.delete-btn.has-danger:hover:before
{ color: yellow;
    background-color: red;
    content: '!!  '
}

a.delete-btn.has-danger:hover:after
{ color: yellow;
    background-color: red;
    content: '  !!'
}

.nav-pills>li.active>a
{ background-color: #720000;
    border-top: 1px solid #6F0101;
    border-bottom: 1px solid #6F0101;
}

.nav-pills>li.active>a:hover
{ background-color: #720000;
    border-top: 1px solid #6F0101;
    border-bottom: 1px solid #6F0101;
}

.nav-pills>li.active>a:focus
{ background-color: #720000;
    border-top: 1px solid #6F0101;
    border-bottom: 1px solid #6F0101;
}

.nav-pills>li>a
{ border-radius: 5px;
}

.nav>li>a:hover
{ text-decoration: none;
    background-color: hsla(15, 100%, 45%, 0.05);
}

.nav>li>a:focus
{ text-decoration: none;
    background-color: hsla(15, 100%, 45%, 0.05);
}

.nav>li>a:focus
{ text-decoration: none;
    background-color: hsla(15, 100%, 45%, 0.05);
}

.nav-pills-sm>li.active>a
{ background-color: #720000;
    padding-top: 4px;
    padding-bottom: 4px;
}

.web-settings-container h4.settings-header
{ color: #f9be03;
}
/*====================== Buttons ==========================*/

button.server-claim-btn.btn.btn-default.btn-loading
{ background-color: #720000;
}

.btn-default
{ border-color: #ccc;
    color: #fff;
    background-color: #ffa31a;
    box-shadow: inset 0 1px 0 #4A0202;
}

.btn-default:hover
{ border-color: #ECA114;
    color: #fff;
    text-shadow: 0 -1px 1px rgba(0, 0, 0, .4), 0 0 15px rgba(0, 0, 0, .2);
    background-color: #e68a00;
}

.btn-primary
{ color: #fff;
    background-color: #ffa31a;
    box-shadow: inset 0 1px 0 #ebac60;
}

.btn-primary:hover
{ color: #fff;
    text-shadow: 0 -1px 1px rgba(0, 0, 0, .4), 0 0 15px rgba(0, 0, 0, .2);
    background-color: #e68a00;
}

.btn-sm
{ padding: 2px 14px;
    font-size: 12px;
    line-height: 1.5;
    border-radius: 3px;
    background-color: #A01919;
}

button.server-sign-out-btn.btn.btn-default.btn-loading
{ background-color: #FF9900;
}

button.server-sign-in-btn.btn.btn-primary.btn-loading:hover
{ background-color: #23F723;
}

button.server-sign-out-btn.btn.btn-default.btn-loading:hover
{ background-color: red;
}

.btn-gray
{ padding: 0;
    color: #F5D79D;
    background-color: #ECA114;
    text-shadow: none;
    background: 0 0;
    border: 0;
    -webkit-box-shadow: none;
    box-shadow: none;
}

.btn-gray.disabled
{ color: #8C5D04;
    cursor: default;
}

button.remote-access-disable-btn.btn.btn-sm.btn-default
{ color: #FFF;
    background-color: #e59029;
}

button.remote-access-disable-btn.btn.btn-sm.btn-default:hover
{ background-color: #FF1919;
}

button.remote-access-enable-btn.btn.btn-sm.btn-primary
{ color: #FFF;
    background-color: #e59029;
}

button.remote-access-enable-btn.btn.btn-sm.btn-primary:hover
{ color: #FFF;
    background-color: #198D19;
}
/* ================= Tables and Cards Elements =================== */

.card-handle
{ display: inline-block;
    width: 26px;
    margin-right: 10px;
    font-size: 12px;
    color: #F7BA50;
    text-align: center;
    background-color: #580101;
}

.card-sortable
{ background-color: #720000;
}

.card-sortable:hover
{ background-color: rgba(112, 0, 0, .875);
    border-top: red;
}

.card-sortable:focus
{ background-color: #720000;
    border-top: 1px solid #720000;
}

.card
{ background-color: #720000;
    border-top: 1px solid #A91212;
    border-bottom: 1px solid #731717;
}

.card-sortable:hover .card-handle, .card-sortable:focus .card-handle
{ background-color: #B50606;
}

.card-sortable:hover .card-handle
{ background-color: #720000;
    color: #F1DBB9;
}

.card-action-btn
{ padding: 0;
    color: #E0C342;
}

.ul.list.contributer-list
{ background-color: #F2F2F2 !important
}

.card-list
{ background-color: #6F0101;
}

a.card:hover, a.card:focus
{ background-color: rgba(63, 20, 77, .175);
}

.nav-cards>li>.card
{ background-color: #720000;
    border-bottom: 1px solid #720000;
}

.nav-cards>li>a.card:focus
{ background-color: #4d0000;
    border-bottom: 1px solid #4d0000;
    border-top: 1px solid #720000;
}

.nav-cards>li>a.card:hover
{ color: #ccc;
    border-top: 1px solid #BD0303;
    background-color: rgba(102, 0, 0, .875)
}

.nav>li>a:focus
{ text-decoration: none;
    background-color: hsla(15, 100%, 45%, 0.05);
}
/* ========================= Miscellanious ============================ */

.remote-access-status-icon
{ float: left;
    margin-right: 20px;
    font-size: 60px;
    color: #358294;
}

.remote-access-disable-warning-icon
{ float: left;
    margin-top: 10px;
    margin-right: 15px;
    font-size: 24px;
    color: #F5D907;
}

.user-poster
{ background-color: #5d0000;
    /*358294*/
    
    -webkit-box-shadow: inset 0 0 0 2px yellow;
    box-shadow: inset 0 0 0 1px black;
    border-radius: 3px
}

.check-for-updates-container.has-release-notes>.release-notes-btn
{ border-radius: 2px;
}

.label-primary
{ border-radius: 4px;
}

.device-icon-default
{ background-color: #fbaf23;
    border: 3px solid #ffff00
}

.glyphicon circle-question-mark
{ color: green;
    background-color: blue;
}
/*
.user-playlist-full .plex-icon
{ color: #358294;
}

.user-playlist-full .plex-icon:hover
{ color: #ff941a;
}
*/

.user-playlist-full .button-icon
{ top: -5px;
    color: #ECA114;
}

.user-playlist-full .button-icon:hover
{ top: -5px;
    color: #e67a00;
}

.user-playlist-help
{ color: #ECA114;
}

.user-playlist-help:hover
{ color: #e67a00;
}

.queue-help-container .row .queue-email.disabled
{ background-color: rgba(92, 109, 114, .62);
}

.label-default
{ background-color: rgba(92, 109, 114, .62);
    border-radius: 5px!important
}

.label-default:hover
{ background-color: #86B4BF!important
}
/* Playback Screen */

.mini-player
{ height: 5pc;
    position: fixed;
    right: 0;
    bottom: 0;
    left: 0;
    z-index: 1010;
    background-color: #940404;
    border-top: 1px solid #CE0707;
    box-shadow: 0 0 0 3px rgba(0, 0, 0, .2);
}
/*Web App Log*/

.logs-container textarea
{ width: 100%;
    height: 100%;
    background-color: #7B0303;
}
/* Add Library */

.edit-section-modal .wizard-blocks-group>.wizard-block>.wizard-block-icon
{ color: #F9BE03;
}

.edit-section-modal .wizard-blocks-group>.wizard-block.selectable:hover
{ color: #bbb;
    cursor: pointer;
    background-color: #690808;
}
}
