css:
@-moz-document url-prefix("https://www.zhihu.com") {
.zu-top {
    background: rgba(25, 113, 202, 0.79);
    border-bottom: 0px;
    box-shadow: none;
}
button,
button.zu-top-add-question,
.zg-btn-blue,
.modal-dialog-title {
    border: none;
    border-bottom: 0px;
    box-shadow: none;
    background: rgba(11, 105, 199, 0.8);
    text-shadow: initial;
}
.modal-dialog-title {
    border-radius: 8px 8px 0 0;
    margin: 0;
}
.zg-btn-unfollow,
.zg-btn-disabled,
textarea,
.modal-dialog,
.topic-feed-page .feed-switcher,
.modal-dialog-title-text {
    border: none !important;
}
.zg-btn-white {
    border: none;
    border-bottom: 0px;
    box-shadow: none;
    text-shadow: initial;
    background: #e6e6e8;
}
.zu-top-search-input,
.zu-top-search-input:focus,
.zu-top-search-form button.zu-top-search-button {
    background: rgba(225, 234, 242, 0);
    border: none;
    box-shadow: none !important;
    color: #fff;
}
.zu-top-search-input::-webkit-input-placeholder {
    color: #eee;
}
.zg-btn-white {
    background: #eee;
}
body {
    font-size: 14px;
}
h2 {
    font-size: 15px;
}
a {
    /*color: rgb(34, 143, 214);*/
    
    color: #418FC5;
}
a:-webkit-any-link {
    text-decoration: none !important;
}
a:hover {
    text-decoration: none !important;
    color: rgb(19, 96, 146);
}
.zm-item:hover,
.goog-inline-block:hover {
    text-decoration: none !important;
}
.zu-top-nav-li.current {
    background: rgba(22, 114, 206, 0.79);
}
.zg-btn-green,
.zg-btn-follow {
    color: #fff!important;
    text-shadow: initial;
    background: #86b846;
    border: none;
    box-shadow: none;
}
.zg-noti-number {
    background: rgba(245, 53, 90, 0.9);
    border: none;
    box-shadow: none;
}
.feed-item .avatar a {
    width: 40px;
    height: 40px;
}
.feed-item .avatar img {
    border-radius: 40px;
}
.zu-top-nav-userinfo.selected {
    height: 45px!important;
    background: rgba(22, 114, 206, 0.79);
}
.top-nav-profile a {
    background: rgba(22, 114, 206, 0.85);
}
.profile-navbar {
    background-color: initial;
    border-top: 0px;
    box-shadow: none;
    border-radius: 0px;
}
input,
.profile-navbar .item.active,
.zh-profile-card .lower .meta .item,
.top-nav-dropdown li a {
    border: none;
    box-shadow: none !important;
    text-shadow: inherit;
}
.zm-profile-header,
.zm-profile-section-wrap {
    border: 1px solid rgba(221, 221, 221, 0.62);
    box-shadow: none;
}
.goog-menu,
._InputBox_root_1Xwi,
._CommentBox_root_G_1m._CommentBox_bordered_3Fo-,
.zu-noti7-popup .zu-top-nav-live-inner,
.question-invite-panel,
.zg-form-text-input,
.zm-editable-editor-field-wrap,
.zm-tag-editor-editor,
.ac-renderer,
.zm-comment-form .zm-comment-textarea,
.zm-comment-form .zm-comment-editable,
.zm-comment-box {
    box-shadow: none !important;
}
._CommentForm_bordered_2isg {
    border-top: 0px;
}
@media (max-width: 1000px) {
    .zg-wrap {
        width: 93%;
    }
}
@media (min-width:1000px) {
    .zg-wrap {
        width: 72%;
    }
}
._CommentBox_root_G_1m._CommentBox_bordered_3Fo- {
    border: 1px solid #eee !important;
}
.popover .popover-content,
.zm-tag-editor-editor,
.zg-form-text-input,
.zm-editable-editor-field-wrap {
    border: solid 1px #ddd !important;
}
.zm-tag-editor-labels:hover .zu-edit-button,
.zm-editable-status-normal:hover .zu-edit-button,
.zm-editable-status-normal:hover .zm-editable-tip,
.answer-head:hover zm-item-vote-info,
.answer-head:hover .zm-item-vote-info {
    opacity: 1;
}
.zu-edit-button,
.zm-editable-tip,
.answer-head .zm-item-vote-info {
    transition: all .3s;
    opacity: 0;
}
.zh-answers-title {
    border: 0px
}
.zm-meta-panel {
    background: rgba(255, 255, 255, 0.83);
}
}

@-moz-document url-prefix("http://zhuanlan.zhihu.com") {
.navbar {
    border: none;
    box-shadow: none;
    background-image: initial;
    background-color: rgba(237, 238, 239, 0.65);
}
.ui-menu-button>menu {
    box-shadow: none;
}
.btn-dark-green {
    color: #fff!important;
    text-shadow: initial;
    background: #86b846;
    border: none;
    box-shadow: none;
}
}
