css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("www.respekt.cz") {
  .page-content h1, .page-content h2, .page-content h3, .page-content h4, .page-content h5, .page-content h6, .sectitle {
    color: #ccc;
  }
  .layout-canvas, .siteheader-inner, .siteheader-maincategories, .shadowbox, .morelink-link, .stritem-content {
    background-color: #222;
    color: #ccc;
  }
  .issuedetail-categorized-item {
    color: #fff;
  }
  .issuedetail-categorized-item:hover {
    color: #fff;
  }
  .issuedetail-categorized-sectionname {
    color: #ff2d46;
  }
  a {
    color: #ccc;
    text-decoration: none;
  }
  a:hover {
    color: #fff;
    text-decoration: none;
  }
  .postlink-link:hover,.postlink.view-search .postlink-link:hover, .morelink-link:hover, .currentissue-headline:hover, .result-link:hover {
    color: #fff;
    text-decoration: none;
  }
  .searchovl-placeholder:hover {
    text-decoration: none;
  }
  .btn, .btn-light {
    background-color: #555;
    color: #ccc;
  }
  .btn:hover, .btn-light:hover {
    background-color: #000;
    color: #fff;
  }
  .commform-input, input[type="email"].form-input.view-footer, .tag-wrap-begin {
    background-color: #000;
    color: #fff;
  }
  .tag, .tag-wrap-begin, .postlink-tags .tag {
    background-color: #555;
    color: #fff;
  }
  .tag-wrap-begin::before {
    border-left-color: #555;
    border-right-color: #555;
  }
  .tag-wrap-begin::after {
    background-color: #222;
  }
  .tag::before {
    background-color: #555;
  }
  .tag:hover {
    background-color: #d0021b;
    color: #fff;
  }
  .commbar-popup, .post-actions-popup, .stritem-popup, .commbar-popup a, .post-actions-popup a, .stritem-popup a {
    background-color: #000;
    color: #fff;    
  }
  .popular-item, .thumbside-title, .sectitle.view-light .sectitle-extra {
    color: #ddd;
  }
  .issuedetail-highlighted-title {
    color: #fff;
  }
  .issuedetail-highlighted-item:hover {
    background-color: #000;
  }
  .comment-content, .comment-author {
    color: #ddd;
  }
  .currentissue-share-store:hover {
    filter: none;
    border-color: #fff;
  }
}
