css:
/*AGENT_SHEET*/
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
@-moz-document url(about:addons), url-prefix(about:addons), url(chrome://mozapps/content/extensions/extensions.xul), url-prefix(chrome://mozapps/content/extensions/extensions.xul) {
     #addons-page hbox vbox #view-port #list-view #addon-list .addon[selected] {
          background-color:  #f0f0f0 !important;
          border-inline-start-color: #f0f0f0 !important;
          color: black !important;
     }
     #addons-page hbox vbox #view-port #list-view #addon-list .addon:hover {
          background-color: #fafafa !important;
          border-inline-start-color: #fafafa !important;
          color: black !important;
     }
     #addons-page hbox vbox #view-port #list-view #addon-list .addon[selected]:hover {
          background-color: #eaeaea !important;
          border-inline-start-color: #eaeaea !important;
          color: black !important;
     }
     #addons-page hbox vbox #view-port #list-view button.sorter:not([checkState="2"]):hover,
     #addons-page hbox vbox #categories .category:not([selected]):hover {
          background-color: mediumblue !important;
          color: HighlightText !important;
     }
     #addons-page hbox vbox #view-port #list-view button.sorter[checkState="2"],
     #addons-page hbox vbox #categories .category[selected] {
          background-color: darkblue !important;
          color: HighlightText !important;
     }
     #addons-page hbox vbox #view-port #list-view button.sorter[checkState="2"]:hover,
     #addons-page hbox vbox #categories .category[selected]:hover {
          background-color: midnightblue !important;
          color: HighlightText !important;
     }
     #addons-page #categories .category[selected] {
          -moz-border-start: solid 4px darkblue !important;
     }
     #addons-page #categories .category[selected]:hover {
          -moz-border-start: solid 4px midnightblue !important;
     }
     #addons-page button.sorter {
          box-shadow: none !important;
     }
     .nav-button.header-button, #header-utils-btn,
     button:not([disabled="true"]):not(.sorter):not(.button-link) {
          -moz-appearance: button !important;
          color: black !important;
     }
     menulist {
          -moz-appearance: menulist !important;
     }
     .menulist-dropmarker {
          -moz-appearance: menulist-button !important;
          margin: -2px !important;
     }
     .menulist-label-box {
          margin: 0px !important;
     }
     menuitem:hover:not([disabled="true"]) {
          background: Highlight !important;
          color: HighlightText !important;
     }
     textbox {
          -moz-appearance: textfield !important;
     }
     .header-button {
          cursor: default !important;
     }
     #addons-page checkbox .checkbox-check {
          -moz-appearance: checkbox  !important;
          box-shadow: unset !important;
          padding: 0px !important;
          margin: 0px !important;
          width: 10px !important;
          height: 10px !important;
     }
     #addons-page radio .radio-check {
          -moz-appearance: radio !important;
          box-shadow: unset !important;
          padding: 0px !important;
          margin: 0px !important;
          width: 10px !important;
          height: 10px !important;
     }
     .radio-label-box {
          outline: unset !important;
     }
     .spinbuttons-button.spinbuttons-up, .spinbuttons-button.spinbuttons-down {
          padding: 0px !important;
          margin: 0px !important;
     }
}
