css:
/*AGENT_SHEET*/
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
@-moz-document url(about:config) {
  textbox {
    -moz-appearance: textfield !important;
  }
  treecol, treecolpicker {
    -moz-appearance: button !important;
    color: black !important;
    padding: 0px !important;
  }
  tree {
    -moz-appearance: listbox !important;
    margin-bottom: 0px !important;
  }
  treecols {
    border: 0px !important;
  }
  treechildren::-moz-tree-cell-text(hover) {
    color: HighlightText !important;
  }
  treechildren::-moz-tree-row(hover) {
    background: blue !important;
  }
  treechildren::-moz-tree-row(selected) {
    border: none !important;
    background: midnightblue !important;
  }
}
@-moz-document url(about:permissions) {
  textbox {
    -moz-appearance: textfield !important;
  }
  menulist {
    -moz-appearance: menulist !important;
  }
  .menulist-dropmarker {
    -moz-appearance: menulist-button !important;
    margin: 0px !important;
  }
  button {
    -moz-appearance: button !important;
  }
  button:not([disabled="true"]) {
    color: black !important;
  }
  richlistbox {
    -moz-appearance: listbox !important;
    color: black !important;
  }
  #permissions-page #sites-list #all-sites-item .site-favicon {
    list-style: url("chrome://browser/skin/identity-not-secure.svg") !important;
  }
  menuitem:hover {
    background: darkblue !important;
    color: white !important;
  }
  menuitem[selected] {
    border: none !important;
    background: midnightblue !important;
  }
}
@-moz-document url(about:sessionrestore) {
  button {
    -moz-appearance: button !important;
  }
  button:not([disabled="true"]) {
    color: black !important;
  }
  button[disabled="true"] {
    cursor: default !important;
    color: rgb(51, 51, 51) !important;
  }
  tree {
    -moz-appearance: listbox !important;
  }
  treecols {
    border: 0px !important;
  }
  treecol {
    -moz-appearance: button !important;
    color: black !important;
  }
  treechildren::-moz-tree-cell-text(hover) {
    color: HighlightText !important;
  }
  treechildren::-moz-tree-row(hover) {
    background: blue !important;
  }
  treechildren::-moz-tree-row(selected) {
    border: none !important;
    background: midnightblue !important;
  }
  treechildren::-moz-tree-image(title, leaf) {
    list-style: url("chrome://browser/skin/identity-not-secure.svg") !important;
  }
}
@-moz-document url-prefix("about:stylish") {
  button {
    -moz-appearance: button !important;
  }
  button:not([disabled="true"]) {
    color: black !important;
  }
  textbox {
    -moz-appearance: textfield !important;
  }
  #main-area {
    background: linear-gradient(to bottom right, #f5f5f5,-moz-Dialog,#f5f5f5,-moz-Dialog) !important;
  }
}
#main-window[customizing="true"] #PanelUI-footer-inner {
  max-width: 272px !important;
}
#main-window #customization-footer .customizationmode-button {
  -moz-appearance: button !important;
  box-shadow: none !important;
  --toolbarbutton-active-boxshadow: transparent !important;
  --toolbarbutton-hover-boxshadow: transparent !important;
  --toolbarbutton-hover-bordercolor: transparent !important;
  --toolbarbutton-hover-background: transparent !important;
  --toolbarbutton-active-background: transparent !important;
  --toolbarbutton-active-bordercolor: transparent !important;
}
#main-window findbar textbox {
  -moz-appearance: textfield !important;
}
#main-window findbar textbox[status="notfound"] {
  color: red !important;
}
#main-window findbar toolbarbutton {
  -moz-appearance: button !important;
  box-shadow: none !important;
  --toolbarbutton-active-boxshadow: transparent !important;
  --toolbarbutton-hover-boxshadow: transparent !important;
  --toolbarbutton-hover-bordercolor: transparent !important;
  --toolbarbutton-hover-background: transparent !important;
  --toolbarbutton-active-background: transparent !important;
  --toolbarbutton-active-bordercolor: transparent !important;
}
#main-window findbar toolbarbutton[class="findbar-closebutton close-icon"] {
  list-style-image: url("data:image/png;base64,") !important;
  -moz-appearance: none !important;
  -moz-image-region: auto !important;
  padding: 0px !important;
  height: 1px !important;
  margin: 6px !important;
  border: solid 1px transparent !important;
}
#main-window findbar toolbarbutton[class="findbar-closebutton close-icon"]:hover {
  border: outset 1px -moz-dialog !important;
}
#main-window findbar toolbarbutton[class="findbar-closebutton close-icon"]:hover:active {
  border: inset 1px -moz-dialog !important;
}
#main-window findbar toolbarbutton[class="findbar-closebutton close-icon"]:hover:active .toolbarbutton-icon {
  margin-top: 1px !important;
  margin-left: 1px !important;
  margin-right: -1px !important;
  margin-bottom: -1px !important;
}
#main-window toolbarbutton[class="close-icon tabbable"] {
  list-style-image: url("data:image/png;base64,") !important;
  -moz-image-region: auto !important;
  padding: 0px !important;
  margin-right: 3px !important;
  border: solid 1px transparent !important;
}
#main-window toolbarbutton[class="close-icon tabbable"] .toolbarbutton-icon {
  padding: 0px !important;
  margin-top: 1px !important;
}
#main-window toolbarbutton[class="close-icon tabbable"]:hover {
  border: outset 1px -moz-dialog !important;
}
#main-window toolbarbutton[class="close-icon tabbable"]:hover:active {
  border: inset 1px -moz-dialog !important;
}
#main-window toolbarbutton[class="close-icon tabbable"]:hover:active .toolbarbutton-icon {
  margin-top: 1px !important;
  margin-left: 1px !important;
  margin-right: -1px !important;
  margin-bottom: -1px !important;
}
