css:
/*AGENT_SHEET*/
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
@-moz-document url(about:preferences), url-prefix(about:preferences), url(chrome://passwordmgr/content/passwordManager.xul), 
url(chrome://passwordmgr/content/passwordManagerExceptions.xul), url(chrome://mozapps/content/preferences/changemp.xul),
url-prefix(chrome://browser/content/preferences/), url-prefix(chrome://mozapps/content/preferences/) {
  #startupGroup, #notificationsGroup, #addonsPhishingGroup, #trackingGroup {
    margin-top: -2px !important;
  }
  #mainPrefPane #defaultEngineGroup {
    margin-top: 2px !important;
  }
  page stack #mainPrefPane[fx46plus="true"] groupbox#trackingGroup {
    margin-top: 9px !important;
  }
  #oneClickSearchProvidersGroup {
    margin-top: 6px !important;
  }
  #engineList, #addEngines {
    margin-top: 0px !important;
    margin-bottom: 0px !important;
  }
  #oneClickSearchProvidersGroup > separator {
    display: none !important;
  }
  #applicationsContent {
    margin-top: 2px !important;
    margin-right: -4px !important;
  }
  #noFxaAccount, #hasFxaAccount {
    margin-top: 4px !important;
  }
  #hasFxaCaption, #noFxaCaption {
    margin-top: 4px !important;
  }
  #accessibilityGroup, #connectionGroup, #updateApp, #certSelection {
    margin-top: 0px !important;
  }
  #dataChoicesPanel groupbox:first-child {
    margin-top: -2px !important;
  }
  #tabsElement {
    margin-top: 9px !important;
    margin-bottom: 3px !important;
    margin-right: 0px !important;
    width: auto !important;
    -moz-appearance: listbox !important;
  }
  #generalTab {
    width: 108px !important;
    text-align: center !important;
  }
  #dataChoicesTab {
    width: 160px !important;
    text-align: center !important;
  }
  #networkTab {
    width: 108px !important;
    text-align: center !important;
  }
  #updateTab {
    width: 135px !important;
    text-align: center !important;
  }
  #encryptionTab {
    width: 135px !important;
    text-align: center !important;
  }
  #generalWindowOpenBox, #tabmixplusBox > separator:nth-child(2), #tabmixplusBox > separator:nth-child(4), #btn_tabmixplus {
    display: none !important;
  }
  menupopup menuitem:not([selected]):hover {
    background-color: darkblue !important;
  }
  .menulist-dropmarker {
    -moz-appearance: menulist-button !important;
    margin: 0px !important;
  }
  .spinbuttons-button.spinbuttons-up, .spinbuttons-button.spinbuttons-down {
    -moz-appearance: button !important;
    margin: 0px !important;
  }
  tree, listbox, richlistbox:not([id="categories"]), #cookieInfoBox {
    -moz-appearance: listbox !important;
  }
  button:not([disabled="true"]), colorpicker[type="button"] {
    -moz-appearance: button !important;
    color: black !important;
  }
  button[disabled="true"] {
    cursor: default !important;
  }
  .autocomplete-treecol, popupset {
    cursor: default !important;
    margin: 0px !important;
    -moz-appearance: menupopup !important;
  }
  .autocomplete-treebody {
   min-height: 132px !important;
   max-height: 132px !important;
  }
  #dialogOverlay #dialogBox {
    border: outset 2px -moz-dialog !important;
    border-radius: 0px !important;
  }
  #dialogOverlay #dialogBox .groupbox-title {
    -moz-appearance: -moz-window-titlebar !important;
    color: HighlightText !important;
    margin-top: -2px !important;
    margin-bottom: 8px !important;
    font-weight: normal !important;
  }
  #dialogOverlay #dialogBox caption #dialogTitle  {
    font-size: 11px !important;
    text-align: left !important;
    margin-left: 0px !important;
    margin-top: 1px !important;
    margin-bottom: -1px !important;
    -moz-margin-start: 4px !important;
  }
  #dialogOverlay #dialogBox caption #dialogClose {
    -moz-appearance: -moz-window-button-close !important;
    list-style-image: none !important;
    margin: 0px !important;
    margin-top: 2px !important;
    margin-right: 0px !important;
  }
  #dialogOverlay #dialogBox resizer {
    display: none !important;
  }
  .menulist-icon {
    height: 16px !important;
  }
  rows listitem:hover,
  tree treechildren::-moz-tree-row(hover) {
    background: darkblue !important;
    color: white !important;
  }
  tree treechildren::-moz-tree-cell-text(hover) {
    color: HighlightText !important;
  }
  rows listitem[selected],
  tree treechildren::-moz-tree-row(selected) {
    border: none !important;
    background: midnightblue !important;
  }
  .autocomplete-treebody::-moz-tree-row {
    background: transparent !important;
  }
  .autocomplete-treebody::-moz-tree-cell-text {
    color: inherit !important;
  }
  .autocomplete-treebody::-moz-tree-row(hover) {
    background: midnightblue !important;
  }
  .autocomplete-treebody::-moz-tree-cell-text(hover) {
    color: HighlightText !important;
  }
  .autocomplete-treecol, .autocomplete-tree.plain, popupset {
    cursor: default !important;
    margin: 0px !important;
    -moz-appearance: menupopup !important;
  }
  .autocomplete-tree.plain .tree-scrollbar {
    margin: 0px !important;
  }
  .autocomplete-treebody {
   min-height: 132px !important;
   max-height: 132px !important;
  }
  #cookiesChildren::-moz-tree-image {
    margin-top: -2px !important;
    margin-bottom: -2px !important;
    margin-left: -2px !important;
  }
  #cookiesChildren::-moz-tree-image(leaf, domainCol) {
    list-style: url("chrome://browser/skin/identity-not-secure.svg") !important;
  }
  treecols {
    border: 0px !important;
  }
  treecolpicker {
    -moz-appearance: button !important;
    padding: 0px !important;
  }
  window[subdialog="true"] #signonsTree .treecol-text {
    margin-left: -9px !important;
    margin-right: -9px !important;
  }
  .checkbox-label-box {
    outline-style: none !important;
  }
  window:not([subdialog="true"]) treecol, window:not([subdialog="true"]) treecolpicker {
    -moz-appearance: treeheadercell !important;
  }
}
