css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain("youtube.com") {

  

  #watch7-sidebar {display:none}

  #watch-action-panels {display:none}

  #watch-discussion {display:none}

  #footer-container {display:none}

  #watch8-action-buttons{display:none}

  .branded-page-v2-secondary-col {display:none}

  #watch-description-extras {display:none}

  .yt-subscription-button-subscriber-count-branded-horizontal{display:none !important;}

  /*.yt-uix-button-subscription-container button {background-color: #cccccc;}*/

  #watch7-subscription-container{display:none}

  #yt-masthead-logo-fragment {display:none}

  #upload-btn {display:none}

  .notifications-container {display:none}

  #watch7-container{margin-left: 213px;}

  #yt-masthead-content{position: relative; left: 17px;}

  #yt-masthead-user img {display:none}
  
  #feed-main-what_to_watch {display:none}


  /* make player controls do not overlay on video */
  
  @media all and (max-height: 869px) { 
    #player-api { height: 525px !important; }
    .html5-video-container video { top: 0 !important; }
    #theater-background { height: 525px; }
    #placeholder-player { height: 525px; margin-bottom: 3px;}
  }
  @media all and (min-height: 870px) { 
    #player-api { height: 765px !important; }
    .html5-video-container video { top: 0 !important; }
    #theater-background { height: 765px; }
    #placeholder-player { height: 765px; margin-bottom: 3px;}
  }
  
  .branding-img {display:none !important;} /* user logo over video left bottom corner */
  .ytp-cards-button {display:none} /* (i) button over video right top corner */
  .ytp-cards-teaser {display:none !important} /* ads over video right top corner */
  
  .iv-promo {display:none} /* video suggestion pop-up */
  

  



}
