css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.mweor.com/topics.php") { 
td img {
    filter: sepia(1) hue-rotate(-145deg) saturate(3) !important;
    } 
}

@-moz-document domain("mweor.com") {

  * {
  font-family: "arial" !important;
  font-size: 11px !important;
  border-radius: 0px !important;
}
    
    #menu_holder {
    position: absolute;
    display: initial !important;
    top: -5px;
    left: 35% !important;
    margin: auto !important;
}
    
    #menu_holder div.menu_dropdown, .menu_shadow {
    background: #431584;
}
    
    #menu_holder div.menu_dropdown div.menu_dropdown_inner {
        background: #F7EFFD;
}
    
#menu_holder div.menu_dropdown div.menu_subtitle {
    color: #a452eb;
    text-transform: uppercase !important;
}
    
    #menu_holder div.menu_dropdown a {
color: #5C259E;
    }
    
    #menu_holder div.menu_dropdown a:hover {
color: #000;
    }
    
    #layoutuinfo, #gametime, #index_middle, #bottombanneri, #topbanneri, #link_home {
        display: none;
}
    
    #lostpass {
    position: absolute;
    top: 156px;
    left: 307px;
    background: url(http://www.mweor.com/images/games/item_hunter.png);
    width: 275px;
    height: 275px;
    color: transparent !important;
    background-size: contain;
}
    
    #lostpass a { color: transparent !important;
    }
    
    #content {
    width: 50% !important;
    padding: 0px !important;
    top: -74px !important;
    background: transparent !important;
    margin: auto;
}
    
    .ci_notice, #notice {
    background: rgba(255, 233, 5, 0.2);
    font-size: 14px;
    line-height: 16px;
    color: #a452eb;
    padding: 5px;
    border: 1px solid #ffef0a;
    text-align: center !important;
}
    
    .itemholder {
    background: rgba(255, 233, 5, 0.25);
}
    
    #holder {
    top: 0px !important;
    left: 0px !important;
    height: 100% !important;
    width: 100% !important;
}
    #contentholder {
        left: 0px !important;
        box-shadow: 10px 0px 0px rgba(67, 21, 132, 0.5), 0px 10px 0px rgba(67, 21, 132, 0.5), -10px 0px 0px rgba(67, 21, 132, 0.5), 0px -10px 0px rgba(67, 21, 132, 0.5), 5px 5px 0px rgba(67, 21, 132, 0.5), -5px 5px 0px rgba(67, 21, 132, 0.5), 5px -5px 0px rgba(67, 21, 132, 0.5), -5px -5px 0px rgba(67, 21, 132, 0.5);
        background: white;
        padding: 15px !important;
        margin-top: 50px !important;
        border-radius: 15px !important;
        z-index: 2;
    }
    
    .ui-tooltip {
    box-shadow: 3px 3px 0px rgba(0, 0, 0, 0.25);
    background: #FFF !important;
    border: 1px solid #bfbfbf !important;
    color: #a452eb !important;
}
    
    .ui-widget-content {
    border: 1px solid #bfbfbf;
    background: #FFF;
    color: #000;
}

#mweor_tabs .ui-widget-header {
    width: 723px;
    border: 1px solid #bfbfbf;
    background: #bfbfbf;
    margin-left: -3px !important;
    margin-top: -3px !important;
}

 .ui-widget-header {
    border: 1px solid #bfbfbf;
    background: #bfbfbf;
}

.ui-state-active, .ui-widget-content .ui-state-active, .ui-widget-header .ui-state-active {
    border: 1px solid #bfbfbf;
    background: #FFF !important;
}

.ui-state-default, .ui-widget-content .ui-state-default, .ui-widget-header .ui-state-default {
    background: rgba(255, 255, 255, 0.5);
    border: 1px solid #bfbfbf;
}

.ui-state-hover, .ui-widget-content .ui-state-hover, .ui-widget-header .ui-state-hover, .ui-state-focus, .ui-widget-content .ui-state-focus, .ui-widget-header .ui-state-focus {
    background: #BB7EF0;
    border: 1px solid #bfbfbf;
}

.ui-widget-overlay {
    background: #bfbfbf;
    opacity: 0.6;
}
    
    .good_bkg {
    background: rgba(164, 82, 235, 0.5);
}
    
    .bad {
    color: #ff7e00;
}
    
    .good {
    font-size: 14px !important;
    color: #a452eb;
}
    
    #mweor_good_text span, .good_label {
    background: #ffe905;
    color: gray;
}
  
a {
    color: #6a2ead;
  }
  
a:hover {
    color: #D29E78;
  }
    
    img[style="position: absolute; top: 10px; left: 10px; z-index: 3;"] {
filter: sepia(1) hue-rotate(-145deg) saturate(3) !important;
    }
    
    #topbanner {
        width: 100% !important;
    }
    
.formholder {
background: rgb(255, 251, 205);
}
    
.tborder {
    border: 1px solid rgba(106, 46, 173, 0.8);
}
    
.fa-info::before {
    content: "i";
    font-family: georgia !important;
    font-size: 10pt !important;
}

#bodyi {
    background: #A452EB;
    z-index: 1;
    height: 50%;
    box-shadow: 0px 20px 0px RGBA(164, 82, 235, 0.5);
    opacity: 0.4;
    position: fixed !important;
}

body {
    background: #431584;
}

.popup {
    background: #e1bfff;
   box-shadow: 4px 4px 0px RGBA(67, 21, 132, 0.5);
}
    
#addbookmark {
    position: fixed;
    right: 5px;
    top: 5px;
    filter: invert(1) !important;
}

#bookmarkclose {
    color: #6A2EAD !important;
    font-size: 16px !important;
}

.theader, tr.theader {
    background: #431584;
}

#eventtxt {
    background: rgba(225, 191, 255, 0.5);
    padding-bottom: 5px !important;
    border: 1px solid #431584;
    box-shadow: 4px 4px 0px RGBA(67, 21, 132, 0.3);
}
    
#eventtxt a:hover {color: white !important;}
    
    .theader {
    background: #431584 !important;
}

.button, .button_tall {
    font-weight: normal;
    color: white;
    border: 1px solid #431584;
    background-color: #431584 !important;
    background: #431584 !important;
}

.button:hover {
    font-weight: normal;
    color: white;
    border: 1px solid #A452EB;
    background-color: #A452EB !important;
    background: #A452EB !important;
}
    
    .map_container img {
    border: 1px solid #6a2ead;
    filter: sepia(1) hue-rotate(-153deg) saturate(2) !important;
}

.trow1, .trow1 table, .trow1 tr, .trow1 td {
    background: #ffffff;
    color: #6a2ead;
}

.trow2, .trow2 table, .trow2 tr, .trow2 td {
    background: rgba(255, 248, 179, 0.5) !important;
    background-color: rgba(255, 248, 179, 0.5) !important;
    color: #431584;
}

.trow3 img {
        max-width: 530px;
}
    
    #footer {
    color: #ffe905;
    opacity: 0.7;
    position: fixed;
    margin: auto !important;
    bottom: 0px;
    z-index: 1;
    left: 25%;
}

#footer a {
    color: #ffe905;
}

#footer::after {
    content: " [Purple Layout by #37120]";
} 
    
    .trans {filter: sepia(1) hue-rotate(-153deg) saturate(3.5) !important;}
    
}
