css:
@-moz-document domain(fastmail.com) {
div.app-belowToolbar {background-color: #222;}
div.app-main div.app-belowToolbar {border-left: 1px solid #000;}
div.app-belowToolbar a {color: #999;}
div.app-belowToolbar a.is-selected {background-color: #000; color: #eee;}
div.app-belowToolbar a.v-PushSelect-option.is-selected {background-color: inherit;}
div.app-belowToolbar a.v-PushSelect-option:hover {color: #eee; background-color: inherit;}
div.app-belowToolbar a:hover {background-color: #333;}
div.app-toolbar {border-bottom: 1px solid #000; background-color: #161616;}
div.app-toolbar a {color: #999;}
div.app-toolbar a:hover {color: #eee; background-color: #333;}
div.app-toolbar div.v-Toolbar-section button.v-Button {border: 1px solid #444; background-color: #000; color: #999;}
div.app-toolbar div.v-Toolbar-section button.v-Button:hover { background-color: #333; color: #eee;}
div.app-toolbar div.v-Toolbar-section label.v-SearchText {background-color: #000; border: 1px solid #444;}
label.v-SearchText input.v-Text-input {background-color: #000; color: #999;}
div.v-StatusBar, div.v-UsageInfo {background-color: #000; color: #999; border: 1px solid #444;}
div.v-Sidebar-editLink {border-top: 1px solid #000;}
div.app-toolbar span.v-Toolbar-divider:first-child {border-left: 1px solid #444;}
div.v-Mailbox div.v-MailboxItem {background-color: #161616; border-bottom: 1px solid #444;}
div.v-Mailbox div.v-MailboxItem.is-unread span.v-MailboxItem-name, div.v-Mailbox div.v-MailboxItem.is-unread span.v-MailboxItem-subject {color: #eee;}
div.app-viewBar {border-bottom: 1px solid #444;}
h1.v-Thread-title {color: #eee;}
div.v-Toolbar-section--right {color: #999;}
div.v-Toolbar-section select.v-Select-input {background-color: #000; color: #999; border: 1px solid #444;}
div#mail > div.app-main {background: #000;}
.v-PushSelect-content {background-color: #333;}
.v-ListKBFocus {background: orange;}
.v-MailboxItem.is-unread {font-weight: 400;}
.v-MailboxItem.is-unread .v-MailboxItem-from::before {color: orange;}
.v-MailboxItem-subject > .icon-reply {color: orange;}
#splash {background: #ddd url("http://seeworthymedia.com/wp-content/uploads/rainy-day-window.jpg") no-repeat;background-image: url("http://seeworthymedia.com/wp-content/uploads/rainy-day-window.jpg");}
.icon-paperclip::before {color: skyblue;}
.v-Message-body a, div.v-RichText-input a {color: #166CC5 !important;}




}
