css:
@-moz-document regexp("https://secure.runescape.com/(?=.*?\\/comapp).*") {
.content {
    background-color: #3A3252;
}
.message .notification p {
    background-color: rgba(0, 0, 0, 0.25);
    color: #F3FCFE;
    box-shadow: none;
}
.my-message .message-content {
    border-radius: 10px;
    border-bottom-right-radius: 0px;
    box-shadow: 0 1px 1px rgba(0, 0, 0, 0.5);
    background-color: #726395;
    color: white;
    border: 0px;
}
.message-content {
    border-radius: 10px;
    border-bottom-left-radius: 0px;
    box-shadow: 0 1px 1px rgba(0, 0, 0, 0.5);
    background-color: #726395;
    color: white;
    border: 0px;
}
.message-content p {
    color: #FFFFFF;
}
.historical .message-content {
    border-radius: 0px;
    box-shadow: 0 1px 1px rgba(0, 0, 0, 0.5);
    background-color: #544D63;
    color: white;
    border: 0px;
}
.main-menu li a {
    background-color: #1c2b36;
}
.message-content:after {
    display: none;
}
.message-content:before {
    display: none;
}
.new-message p {
    color: #FFFFFF;
}
.sub-footer {
    background: #3A3252;
}
.new-message .wrap {
    background: #3A3252;
    color: #FFFFFF;
    border: 1px solid #726395;
}
.new-message input[type="submit"] {
    background: #726395;
    color: #FFFFFF;
}
.footer {
    background: #726395;
}
.footer a {
    color: #3A3252;
}
.header {
    background: #3A3252;
    color: white;
    border-bottom: solid 2px #726395;
}
.header a.back,
.header a.forward {
    color: white;
}
.header a.back:active,
.header a.forward:active {
    color: #726395;
}
.sub-header {
    background: #726395;
}
a {
    color: white;
}
.sub-header a.active,
.sub-header a:active {
    background: #3A3252;
    color: #FFFFFF;
}
.main-menu li a {
    background: #3A3252;
}
.main-menu li .icon {
    border-right: 2px solid #FFFFFF;
    background: #563C82;
}
.main-menu li a.active {
    background-color: #563C82;
    box-shadow: none;
    background: #726395;
}
.main-menu li a.active .icon {
    border-right: 2px solid #FFFFFF;
    background: #563C82;
    color: #FFFFFF;
}
@media screen and (min-width: 768px) {
    .content, .sub-header, .sub-footer {
        border-left: 2px solid #726395;
    }
}
.header a {
    color: #FFFFFF;
}
.main-menu li a.active span {
    color: #FFFFFF;
}
.main-menu li .notification {
    background: #726395;
    color: #FFFFFF;
    font-size: 12px;
    font-weight: normal;
}
.icon-home,
.icon-home:before,
.icon-home:after {
    background-color: #FFFFFF;
}
input[type="submit"]:active {
    background: #734AB9;
}
/*friend list*/

.user.offline .user-content {
    background: #563C82;
    color: white;
}
.user.offline .user-content:before {
    border-right-color: #563C82;
}
.modal {
    border: 1px solid #726395;
    background: #3A3252;
}
.modal h2 {
    color: #FFFFFF;
}
.modal p {
    color: #FFFFFF;
}
.button {
    border: 1px solid #726395;
}
.button:hover {
    background-color: #726395;
}
.multi-radio-stack label.first {
    border: 1px solid #726395;
}
.multi-radio-stack label.first {
    color: #FFFFFF;
}
.multi-radio-stack label:hover {
    background-color: #726395;
}
.user-content .author .icon:before {
    color: #FFFFFF;
}
/*settings*/

.zebra li:nth-child(odd) {
    background: #3A3252;
    color: white;
}
.checkbox-wrap input:checked + label {
    background: #FFFFFF;
}
.zebra li.has-children.closed h2 i {
    color: white;
}
.zebra li.has-children {
    background: #726395;
}
.zebra li.has-children:nth-child(odd) {
    background: #726395;
}
.multi-radio-wrap input:checked + label {
    background: #726395;
    border-color: #726395;
}
.multi-radio-wrap label {
    border: 1px solid #726395;
}
.zebra li {
    background: #3A3252;
}
input[type="text"],
input[type="search"],
input[type="email"],
input[type="url"],
input[type="password"],
input[type="number"],
input[type="tel"] {
    border: 1px solid #FFFFFF;
    color: #FFFFFF;
}
input[type="text"]:focus,
input[type="search"]:focus,
input[type="email"]:focus,
input[type="url"]:focus,
input[type="password"]:focus,
input[type="number"]:focus,
input[type="tel"]:focus {
    border-color: #FFFFFF;
}
.button.pill:active {
    background: #563C82;
    text-shadow: none;
}
.zebra li.has-children ul li.has-children {
    background: #563C82;
}
.multi-radio-stack input:checked + label {
    background: #563C82;
    border-color: #726395;
    color: #FFFFFF;
    text-shadow: 0 0 2px rgba(0, 0, 0, 0.5);
}
/*d and d*/

.generic-list .details {
    background: #563C82;
}
.event .details .icon-ddavailable {
    color: #FFFFFF;
}
.generic-list .details .icon-forward {
    color: #FFFFFF;
}
.generic-list .details .subtitle {
    color: #9E9E9E;
}
.event.locked .icon,
.event.locked .details {
    background: #563C82;
    border-color: #563C82;
}
.generic-detail .details {
    background: #563C82;
}
.generic-detail .details:before {
    background: #563C82;
}
p {
    color: #FFFFFF;
}
/*stock market*/

.sub-header.grey {
    background: #3A3252;
}
.wrapped-bar-form .wrap {
    background: #3A3252;
    color: #FFFFFF;
    border: 1px solid #726395;
}
p.empty-message.error {
    border: none;
}
.generic-list .icon {
    border-right: 2px solid #FFFFFF;
    background: #3A3252;
}
.generic-detail .details-list li {
    background: #726395;
}
.generic-detail .details-list li.active {
    background: #563C82;
}
.generic-detail {
    color: #FFFFFF;
}
/*bank*/

.grid li a {
    background: #563C82;
}
.bank .footer .pill-wrap .pill {
    background: #3A3252;
    color: #fff;
}
.bank .item-popup {
    color: #FFFFFF;
    background: rgb(58, 50, 82);
    border-top: 1px solid #726395;
    border-left: 2px solid #726395;
}
.bank .item-popup .icon {
    background: #563C82;
    border-radius: 5px;
}
.bank .items a.active {
    box-shadow: inset 0 0 0 2px #FFFFFF;
}
/*ge*/

.ge-slots .bar-wrap {
    background: #726395;
}
.button.pill:active {
    background: #734AB9;
}
.ge-slot .goto-stockmarket {
    background: #726395;
    color: #FFFFFF;
}
.ge-slot .status a.clickable {
    background: #726395;
    color: #FFFFFF;
}
.ge-slot .bar-wrap {
    background: #726395;
}
.generic-detail.selling .icon {
    border-right: 2px solid #FFFFFF;
}
.generic-detail .icon {
    background: #3A3252;
}
.ge-slots .slot.selling .icon {
    border-right: 2px solid #FFFFFF;
}
.ge-slot .status {
    border-top: 1px solid #726395;
}
.user-usage,
.bank .slot-usage {
    border-bottom: 1px solid #726395;
}
input[type="submit"].primary {
    background: #A3B6BA;
    color: #0d252f;
}
/*login screen*/

.login {
    background: #3A3252;
}
.login form input[type="text"],
.login form input[type="password"] {
    background: #726395;
}
.login form button[type="submit"]:disabled {
    color: #726395;
}
.login form button[type="submit"] {
    color: #726395;
}
.login form input[type="password"] {
    color: #FFFFFF;
}
.login form button[type="submit"]:focus,
.login form button[type="submit"]:active {
    color: #FFFFFF;
}
}
