@namespace url(http://www.w3.org/1999/xhtml);

/**** Style the whole domain ****/
@-moz-document domain("astalavista.box.sk") {

/* Changelog
08.21.07:
 - Fixed after Astalavista.box.sk made changes to the HTML.

 11.01.06:
 - Add an alternate way to remove the old numbering.
   Read the comments marked by A1 and A2 for further info.
*/

/* HTML and Body */

html, body {
    cursor: default !important;
    margin: 0 !important;
    padding: 0 !important;
    color: #AAA !important;
    background: #666 !important;
}

/* Hide Ads and evreything that's not what we want */

img,
.adHeadline,
object[data*="content"],
iframe[src*="ad"],
p font[size="-1"][face="arial"],
p font[size="-2"][face="verdana"],
font[size="-1"][face="verdana"],
td {
    display: none !important;
}

/* Style the header */

form[action="http://astalavista.box.sk/cgi-bin/robot"] {
    height: 105px !important;
    margin-top: 36px !important;
    padding: 40px 0 0 145px !important;
    text-align: left !important;
    background:  url("data:image/png;base64,") 15px 25px no-repeat !important;
}
form[action="http://astalavista.box.sk/cgi-bin/robot"] input[type="submit"] {
    margin-top: 0 !important;
    padding: 1px 5px !important;
    font-size: 9px !important;
    color: #222 !important;
    border: 1px solid #999 !important;
    background: #666 !important;
}
form[action="http://astalavista.box.sk/cgi-bin/robot"] input[type="submit"]:hover {
    color: #111 !important;
    border: 1px solid #CCC !important;
    background: #999 !important;
}
form[action="http://astalavista.box.sk/cgi-bin/robot"] input[type="text"] {
    margin-top: 0 !important;
    padding: 2px !important;
    width: 250px !important;
    font-size: 10px !important;
    color: #666 !important;
    border: 1px solid #DDD !important;
    background: #AAA !important;
}
form[action="http://astalavista.box.sk/cgi-bin/robot"] input[type="text"]:focus {
    color: #933 !important;
    background: #FEE !important;
}

/* Hide everything inside the header that does not belong to the main search */
form form, form p {
    display: none !important;
}

/* Style the main td in which the results list and header are */

td[valign="top"][align="left"][bgcolor="#404040"] {
    counter-reset: pageNum;
    width: 70% !important;
    min-height: 100% !important;
    margin-left: 14% !important;
    padding: 0 20px !important;
    position: absolute !important;
    top: 0 !important;
    display: block !important;
    border-left: 1px solid #999 !important;
    border-right: 1px solid #999 !important;
    background: #444 url("data:image/png;base64,") top repeat-x !important;
}
td[valign="top"][align="left"][bgcolor="#404040"]:after {
    display: block !important;
    margin-top: 35px !important;
    padding-bottom: 50px !important;
    text-align: center !important;
    font-weight: bold !important;
    color: #EEE !important;
    content: "If you like it - buy it!" !important;
}

}


/**** Tweak a few things in the results page ****/

@-moz-document url-prefix(http://astalavista.box.sk/cgi-bin/robot?srch=) {

form[action="http://astalavista.box.sk/cgi-bin/robot"] {
    height: 105px !important;
    margin-top: 33px !important;
    padding: 40px 0 0 145px !important;
}

td[valign="top"][align="left"][bgcolor="#404040"] {
    min-height: 100% !important;
}
form[action="http://astalavista.box.sk/cgi-bin/robot"] input[type="submit"] {
    margin-top: 2px !important;
}
form[action="http://astalavista.box.sk/cgi-bin/robot"] input[type="text"] {
    margin-top: 2px !important;
    margin-right: 5px !important;
}
td[valign="top"][align="left"][bgcolor="#404040"]:after {
    display: block !important;
    margin-top: 35px !important;
    padding-bottom: 50px !important;
    text-align: center !important;
    font-weight: bold !important;
    color: #EEE !important;
    content: "I hope you found what you were looking for..." !important;
}

p {
    font-size: 1px !important;
}

center + p {
    display: none !important;
}
center:after {
    display: block !important;
}

/* Uncomment this segment if you're not seeing the links in the results page. */
/** A1 **/
/*
p b:first-child {
    display: none !important;
}
*/
/** A1 **/

/* Comment all 5 segments bellow if you wish to use the alternate method above. */
/** A2 **/
p b {
    display: none !important;
}
p > b {
    display: block !important;
    position: absolute !important;
    top: 170px !important;
    left: 30px !important;
    font-size: 16px !important;
    color: #DDD !important;
}
p > b i:after {
    font-style: normal !important;
    content: " were" !important;
}
p > b:before {
    content: "The " !important;
}
p > b:after {
    content: "." !important;
}
/** A2 **/

/* Links */

a[target="_blank"] b {
    display: inline !important;
}
a[target="_blank"] {
    /*display: block !important;*/
    float: left !important;
    width: 100% !important;
    margin-top: 15px !important;
    margin-bottom: 0 !important;
    text-decoration: none !important;
    color: #CCC !important;
    background: #444 !important;
}
a[target="_blank"]:before {
    content: "Website " counter(pageNum) ": ";
    counter-increment: pageNum;
}
a[target="_blank"]:hover {
    color: #FFF !important;
}
a[target="_blank"]:visited {
    cursor: default !important;
    color: #CCC !important;
    text-decoration: line-through !important;
}

}
