}
@font-face {
	font-family: 'AileronBold';
	src: url('https://fontlibrary.org/assets/fonts/aileron/c2c609c1043931188df4838d66367e55/3cdf602b3281ee10ef3c7260cafd634f/AileronBold.otf')
	format('opentype');
