@font-face {
	font-family: 'HackRegular';
	src: url('https://fontlibrary.org/assets/fonts/hack/73a447a6923d284e62830693346088f7/b06686925a0ce39e55db2ed8adf3cb10/HackRegular.ttf')
	format('truetype');
      }
* {
	font-family: 'AileronRegular' !important;
}
a, .bbcode_postedby {
	font-family: 'AileronSemiBold' !important;
}
#navbar *, #posts div.posthead > *, div.postfoot *, .username.popupctrl *, .bbcode_postedby strong, b {
	font-family: 'AileronBold' !important;
}
pre, code, code *, kbd, samp, tt {
	font-family: 'HackRegular', monospace !important;
	line-height: 1.2em;
}

/***[ Stretch'n'Shrink Content ]***/
html, body {
	height: 99%;
	width: 100%;
}

/* -------------------------
      HOME PAGE RULES
------------------------- */
.bottom-line #root {
	width: 90%;
}
.bottom-line #root #header {
	background:	url('http://www.ps3hax.net/wp-content/themes/hax/images/bg_wide.jpg');
	background-size: 100% 100%;
}
.bottom-line #header .content {
	background-size: 100% 100%;
}
.bottom-line #main {
	width: auto;
	background-size: contain;
	padding: 10px 40px;
}
.bottom-line #gallerycontent, .bottom-line #main-block {
	width: 100%;
	margin: 0;
}
.bottom-line #footer {
	background-size: 100% 100%;
	text-align: center;
}
.bottom-line #footer .copyright:after {
	content: "\000A Chaos H.P. -CSS- \00A9 hekel CC-BY-SA-4.0";
	white-space: pre;
	font-style: italic;
}

/***[ NavMenus ]***/
#menu-hax-connect li {
	background:	linear-gradient(to bottom, #080808 0%,#282828 4%,#363636 7%,#252525 22%,#1f1f1f 33%,#1e1e1e 52%,#0d0d0d 56%,#2f2f2f 96%,#292929 100%);
	padding: 0 0px 0 0;
}
#menu-hax-connect li:before { float: left; }
#menu-hax-connect li:last-child:after { float: right; }
#menu-hax-connect li:before, #menu-hax-connect li:last-child:after {
	overflow: auto;
	content: " ";
	background: url('http://www.ps3hax.net/wp-content/themes/hax/images/submenu.gif');
	width: 28px;
	height: 27px;
}

/***[ Articles ]***/
#content .post {
	background: url('http://www.ps3hax.net/PS3HAX/images/statusicon/thread_new-30.png') no-repeat,
	linear-gradient(to bottom, #AAA -5px, #E4E4E4 7px, #FBFBFB 66px, #CCC 72px);
	background-position: 15px 7px, 0px;
	background-color: #F0F0F0;
	width: 100%;
	border-style: solid;
	border-radius: 15px;
	border-color: #B6B6B6;
	box-shadow:
		inset 1px 1px 1px 0px #B6A9A9,
		1px 1px 3px 1px #655;
	border-width: 1px;
	margin-bottom: 10px;
}
#content .post .footer {
	background-size: 100% auto;
	border-radius: 0 0 14px 11px;
	background: linear-gradient(#F0F0F0, #B6B6B6);
}
#content .post .content {
	background: linear-gradient(#F0F0F0, #FFF, #F0F0F0);
	background-color: transparent;
	border-top: 1px solid #B6B6B6;
	border-bottom: 1px dashed #B6B6B6;
	padding-bottom: 1em;
}
#content .post .comments h3 {
	margin-top: -47px;
}

/* -------------------------
        FORUM RULES
------------------------- */
body {
	background-position: center -19px;
	background-size: 100% 850px;
	max-width: none;
	min-width: 600px;
}
#vbtopx_content .topx-content /* Remove Lines in Statistics Container */ {
	background: none;
}
#vbtopx_content .topx-bit /* Fix Lines in Statistics Container */ {
	background: url('http://www.ps3hax.net/vietvbb/topx/listbg.gif');
}
.forumrow.table {
	background-size: contain;
}

#wrapper {
/* background: transparent; */
	width: 98.5%;
}
div.floatcontainer.footer /* Reduce Footer - Fixes Horizontal Scrollbar */ {
	text-align: center;
	vertical-align: middle;
	width: 99.8%;
}
#footer_links.footer_links /*  Center Footer Text when using a Small Font */ {
	margin-top: 7px;
}
.below_body, .below_body a:hover {
	background-color: rgba(222,100,100,0.1);
	color: #FFF;
}
.below_body a, .below_body .time {
	color: rgb(222,100,100);
}
.below_body #footer_copyright:after {
	content: "\000A Chaos H.P. -CSS- \00A9 hekel CC-BY-SA-4.0";
	white-space: pre;
	font-style: italic;
}
/***[ profile ]***/
dl.stats.foldercount *, dl.stats.foldercount .folder_count, .shade {
	color: #3e3e3e;
}
.blocksubhead img {
	display: inherit !important;
}
/*
#view-activitystream, #profile_tabs dd:nth-child(3) {
	display: none; plugin is supposedly broken? 
}
*/
/***[ Posts ]***/
.posthead {
	padding: 6px 0 0 0 !important;
}
.postdate.old {
	margin: -2px 0 4px 10px !important;
}
.nodecontrols {
	margin: 2px 0 0 0 !important;
}
.signaturecontainer {
	overflow-wrap: break-word;
}
.postdetails hr, .preview hr {
	border-style: solid;
	border-width: 1px 1em 0 1em;
	border-color: #AAA transparent;
	margin: 0.2em;
}
/***[ Code Containers ]***/
.bbcode_container div.bbcode_description {
	color: white;
	border-right: 1.7em solid transparent;
	border-bottom: 17px solid #457595;
	padding: 0 5px;
	height: 0px;
	display: inline-block;
	overflow: visible;
}
.bbcode_container .bbcode_code {
	overflow: auto !important;
	word-wrap: normal;
	border-radius: 0 7px 7px 7px;
	border: 1px solid #457595 !important;
	height: auto !important;
	max-height: 360px;
}
.bbcode_code code span {
/* prevent wordwrap when syntax highlighting */
	white-space: nowrap;
}
.bbcode_code::-webkit-scrollbar {
	width: 9px;
	height: 9px;
}
.bbcode_code::-webkit-scrollbar-track, pre.bbcode_code::-webkit-scrollbar-corner {
    background: transparent !important;
 /* (!imp) override a certain global scrollbar */
	border: 0;
}
.bbcode_code::-webkit-scrollbar-track-piece {
	margin: 0 !important;
}
.bbcode_code::-webkit-scrollbar-thumb {
    background: rgba(147,13,13,0.5) !important;
 /* (!imp) override a certain global scrollbar */
    border-radius: 6px;
	border: 0;
	box-shadow: none;
}
.bbcode_code::-webkit-scrollbar-thumb:hover {
    background: rgba(147,13,13,0.7) !important;
 /* (!imp) override a certain global scrollbar */
}
.bbcode_code::-webkit-scrollbar-thumb:active {
    background: rgba(147,13,13,0.9) !important;
 /* (!imp) override a certain global scrollbar */
}
/***[ Quote Containers ]***/
.bbcode_container .bbcode_quote_container {
	background: none !important;
	width: 0 !important;
	height: 0 !important;
	top: 4px;
	left: -15px !important;
	border-top: 12px solid transparent;
	border-bottom: 3px solid transparent;
	border-right: 14px solid #457595;
}
.bbcode_quote_container ~ * .bbcode_quote_container {
	border-top: 3px solid transparent;
	border-bottom: 12px solid transparent;
	border-right: 14px solid #457595;
}
.bbcode_container .bbcode_quote {
	border-radius: 7px !important;
	border: 1px solid #457595 !important;
}
.bbcode_container div.bbcode_quote div.message {
	padding: 4px 0;
}
/** Frontpage Quotes **/
#commentssection .bbcode_postedby img[alt="Quote"] {
	content: url(http://www.ps3hax.net/PS3HAX/images/misc/quote_icon.png);
}
#commentssection .bbcode_postedby img[alt="View Post"] {
	content: url(http://www.ps3hax.net/PS3HAX/images/buttons/viewpost-right.png);
}
#commentssection .bbcode_container .quote_container div.bbcode_postedby {
	margin-top: -15px;
}
#commentssection .bbcode_container .quote_container div.message {
	padding: 5px 15px;
}
#commentssection .bbcode_container .bbcode_quote_container {
	margin-left: -20px;
}
#commentssection .bbcode_container .quote_container {
	padding: 5px;
}
#commentssection .bbcode_container .bbcode_quote {
	margin: 5px 5px 5px 15px;
}
/***[ Shrink'n'Fix Navbars ]***/
.navtabs li.selected {
	background-size: contain;
	height: 28px;
}
.navtabs li a.navtab {
	background-size: contain;
	/*Fix Text*/
	/*For Funsies*/
	height: 28px;
	line-height: 2em;
	text-shadow: -1px -1px #FFFFFF;
}
ul.navtabs {
	background-size: contain;
	height: 28px;
}
ul.navtabs ul /* move 2nd navbar up */ {
	/* height: 2em;  Test */
	margin-top: 6px;
	top: 30px;
}
span.clickhere-btn a img {
	float: left;
	height: 25px;
	margin-left: -19px;
	margin-top: -26px;
	position: absolute;
}
div.above_body /* shrink margin above breadcrumbs */ {
	margin: 0px;
}

/***[ Shrink'n'Fix Banner & Logos ]***/
div.floatcontainer.doc_header {
	height: 104px;
	width: 100%;
}
div.banner_scheme-status {
	background-size: contain;
	min-height: 104px;
	width: 530px;
}
div.custom_banner {
	float: right;
	line-height: normal;
	min-height: 104px;
	width: 0px;
}
div.ps3_ps4 {
	background-size: contain;
	position: absolute;
	top: 2px;
	right: 10px;
	min-height: 136px;
	width: 284px;
	z-index: 100;
}
a.logo-image img {
	height: 42px;
	margin-top: -15px;
}

/***[ Shrink Login NavBar ]***/
#login-top {
	background-size: contain;
	min-height: 25px; /* Reduce Top Bar Height */
	vertical-align: middle;
	width: 100%;
}
div.toplinks {
	right: 1em;
	top: -2px; /* Center when reducing Top Bar (Fix Later) */
}

/***[ Forum Sidebar ]***/
#sidebar /* affects homepage too */ {
	display: none;
} 
#content_container #content {
	margin-right: 0;
}
}

@-moz-document domain("ps3hax.net") {
/* Cleanup Ad Placeholders */
/* Home Page */
.bottom-line #leader, #island_bottom, #island_both, .adctr, .adspot, #sky_top, #sky_bottom, #top_sidebar_NS300x250, .GoogleActiveViewClass, #top_sidebar_NS300x250_2, #top_sidebar_video {
