css:
@-moz-document domain("twitter.com") {
@font-face {
    font-family: 'Droid Arabic Naskh';
    font-style: normal;
    font-weight: 700;
    src: url(//fonts.gstatic.com/ea/droidarabicnaskh/v7/DroidNaskh-Regular.eot);
    src: url(//fonts.gstatic.com/ea/droidarabicnaskh/v7/DroidNaskh-Regular.eot?#iefix) format('embedded-opentype'), url(//fonts.gstatic.com/ea/droidarabicnaskh/v7/DroidNaskh-Regular.woff2) format('woff2'), url(//fonts.gstatic.com/ea/droidarabicnaskh/v7/DroidNaskh-Regular.woff) format('woff'), url(//fonts.gstatic.com/ea/droidarabicnaskh/v7/DroidNaskh-Regular.ttf) format('truetype');
}
.tweet-box,
.dm-message,
.tweet-text-rtl,
.tweet-text,
js-tweet-text {
    font-family: Droid Arabic Naskh !important;
    font-size: 18px;
}
[lang="ar"],
.pretty-link b {
    font-family: Droid Arabic Naskh !important;
}
[lang="ar"],
.PollXChoiceTextOnly-choice--text,
PollXChoiceTextOnly-choice--text,
QuoteTweet-container,
.QuoteTweet-container,
.PollXChoiceTextOnly-progress,
PollXChoiceTextOnly-progress {
    font-family: Droid Arabic Naskh !important;
    font-size: 18px;
    font-weight: 700 !important;
}
.fullname {
    font-family: Droid Arabic Naskh !important;
    font-weight: lighter !important;
    font-size: 18px;
}
[lang=en],
.twitter-timeline-link,
twitter-hashtag.pretty-link {
    font-family: tahoma !important;
    font-size: 18px !important;
    font-weight: lighter;
}
a,
a:hover,
a:focus,
a:active {
    color: #ff0000;
}
.stream-item {
    margin-bottom: 20px;
}
.flex-module-inner {
    font-family: Droid Arabic Naskh !important;
    text-align: right;
    padding-top: 10px;
}
.tweet p {
    line-height: 150%;
}
.stream-item-header {
    padding-bottom: 20px;
}
.dashboard-right {
    display: none;
}
.content-main {
    width: 880px;
}
.TwitterCardsGrid {
    text-align: right;
    direction: rtl;
}
.PollXChoiceTextOnly-choice--text {
    padding: 10px;
}
.DMInboxItem-snippet {
    direction: rtl;
    font-family: Droid Arabic Naskh;
    font-size: 15px;
}
.DirectMessage-text .tweet-text {
    font-size: 15px;
}
[lang="ar"],
.stream-item-activity-notification .latest-tweet .tweet-row {
    max-width: 100%;
    direction: rtl;
}
}
