/* Mxmods*/
body {
    background-color: rgb(36, 36, 36) !important;
    background-image: url("http://blog.spoongraphics.co.uk/wp-content/uploads/2010/camo-patterns/Urban-Camo.jpg") !important;
    background-size: 25% !important;
    background-repeat:repeat;
    
    text-align: left;
}

/*titolo discussion*/
.Discussion .PageTitle h1{
    border-radius: 20px !important;
    -webkit-border-radius: 20px !important;
    -moz-border-radius: 20px !important;
}


.DataList .ItemDiscussion {
    background-color: rgb(36, 36, 36) !important;
}

/* generici sfondi /|\*/


div.discussion{
    background-color: rgba(65, 65, 65,0) !important;
}

div.DiscussionHeader{
    background-color: rgb(65, 65, 65) !important;
}

div.AuthorWrap{
    background-color: rgb(65, 65, 65) !important;
}

a.PhotoWrap{
	-webkit-box-shadow: 3px 3px 5px 0px rgba(36, 36, 36, 0.75);
	-moz-box-shadow:    3px 3px 5px 0px rgba(36, 36, 36, 0.75);
	box-shadow:         3px 3px 5px 0px rgba(36, 36, 36, 0.75);
}

a.Username{
	text-shadow: 0px 0px 11px rgba(0, 0, 0, 1);
}
.Author a{
    font-size: 18px !important;
}

div.ItemDiscussion{
    background-color: rgb(65, 65, 65) !important;
    border-radius: 20px !important;
    -webkit-border-radius: 20px !important;
    -moz-border-radius: 20px !important;
}
/* Zona /|\ titolo */

div.Item-BodyWrap{
    border-radius: 20px !important;
    -webkit-border-radius: 20px !important;
    -moz-border-radius: 20px !important;
    background-color: rgb(36, 36, 36) !important;
}
.DiscussionMeta{
    border-radius: 20px !important;
    -webkit-border-radius: 20px !important;
    -moz-border-radius: 20px !important;   
    height: 60%;
}

.MItem:first-child {
    vertical-align:middle !important;   
    margin-left: 10px !important;
}

.MItem:last-child {
    margin-right: 10px !important;
}

div.Item-Body{
    border-radius: 25px;
}

div.Reactions{
    background-color: rgba(36, 36, 36, 0) !important;
    margin: 10px 0px 0px !important;
}

.ReactButton{
    background-color: rgb(36, 36, 36) !important;
    margin: -8px 17px !important;
}


.Message{
    background-color: rgba(36, 36, 36, 0) !important;
}
/* zona commenti*/
.DataList .Item, .NarrowList .Item{
	padding-top:10px !important;
}

.CommentHeading{
    background-color: #3F3F3F !important;
    border-radius: 20px !important;
    -webkit-border-radius: 20px !important;
    -moz-border-radius: 20px !important;
    text-align: center; /* "comments"*/
}

li.ItemComment {
    background-color: #3F3F3F !important;
    border-radius: 20px !important;
    -webkit-border-radius: 20px !important;
    -moz-border-radius: 20px !important;
}

.Item-Header.CommentHeader {
    background-color: #3F3F3F !important;
}

.Comment {
    background-color: #3F3F3F !important;
}

.Item-Header .CommentHeader{
    background-color: rgb(36, 36, 36)!important;
}

div.CommentInfo{
	border-radius: 20px !important;
    -webkit-border-radius: 20px !important;
    -moz-border-radius: 20px !important;   
}

.HomepageTitle{
	border-radius: 20px !important;
    -webkit-border-radius: 20px !important;
    -moz-border-radius: 20px !important;
}

/*aggiungi commenti zone */

div.MessageForm{
    border-radius: 20px !important;
    -webkit-border-radius: 20px !important;
    -moz-border-radius: 20px !important;
    background-color: rgb(36, 36, 36)!important;
    padding-top:10px;
}

h2.H{
    margin-left:10px;
    margin-bottom: -10px;
}

/* barra dx*/
div.PanelColumn{
    background-color: rgb(36, 36, 36) !important;
    border-radius: 10px 10px 10px 10px;
	-moz-border-radius: 10px 10px 10px 10px;
	-webkit-border-radius: 10px 10px 10px 10px;
	border: 5px solid #242424;
}

/* nav bar*/
.navbar {
    left: 225px;
    font-size: 15px;
    margin-bottom: 5px;
    position: fixed;
    width: 900px;
    z-index:10000;
}

/* immagine lmao*/
.black {
    padding-top: 40px;
}

/* menu categorie*/

table {
    background:rgb(36, 36, 36);
    border-collapse: collapse; 
    background-color: rgb(36, 36, 36) !important;
    border-radius: 10px 10px 10px 10px;
	-moz-border-radius: 10px 10px 10px 10px;
	-webkit-border-radius: 10px 10px 10px 10px;
	border: 5px solid #242424;
}
/* dentro la categoria*/
li.Item{
    border-radius: 20px !important;
    -webkit-border-radius: 20px !important;
    -moz-border-radius: 20px !important;
    padding-left: 13px !important;
    padding-right: 13px!important;
}
