padding:0!important;margin:0 20px 0 20px!important}

ul {font-size:12px!important}

ul a:visited, h1 a:visited, h2 a:visited, h3 a:visited,p a:visited, div a:visited {color:#070!important}

a[class="good-rank"]::before {content: "\2713"!important;padding-right:5px!important;color:#090!important}
a.good-rank {text-decoration:none!important}

