a.edit-link {font-size:0!important;padding:5px!important;}

#header-bar form#search {width:100%!important;text-align:right!important;margin:-15px 0 0 0!important;padding:0 0 0 0!important}
#header-bar {width:100%!important;background-color:transparent!important;background-image:none!important;height:80px!important}
#header-bar a {color:#fff!important}
#header-bar div {clear:both!important}

#navigation a, #navigation a:visited, #navigation .current-page {margin-right:4px;border: 1px solid #ABBEA7!important;background-color:#42902D!important;color: #fff!important;padding:4px 6px 2px 6px!important;font-weight: normal!important;-moz-border-radius-topright:6px!important;-moz-border-radius-topleft:6px!important;}
#navigation a:hover {background-color:#000!important}
#navigation .current-page {border:0!important;background-color: transparent!important;}
#navigation {font-size:12px!important;position:absolute!important;top:82px!important;height:auto!important;margin:0!important;padding:0!important}

#logo a {text-transform:capitalize!important}
#logo {margin:0 0 0 0!important;width:300px!important;height:60px;padding:20px 0 0 0!important}

/**** Delete line below to remove gecko ****/
#logo {background-color:transparent!important;background-repeat:no-repeat!important;background-position:80px 0!important;background-image: url("data:image/png;base64,")!important;}
/*******************************************/

h1+p+ul#style-list {background-color:transparent!important;background-repeat:no-repeat!important;background-position:bottom right!important;
background-image: url("data:image/gif;base64,")!important;}

#main-text {padding-top:10px!important;}

#sidebar {background-color:#fff!important;background-repeat:repeat-x!important;background-position:0 -101px!important;background-image: url("data:image/jpg;base64,")!important;width:300px!important;border:0!important;padding:15px 0 15px 0!important;-moz-border-radius:15px!important}
#sidebar li {list-style:none!important;padding:3px!important;font-size:12px!important}
#sidebar ul {margin:0!important;padding:0 10px 0 10px!important}
#sidebar h2 {height:20px!important;text-align:center!important;padding:3px!important;}
#sidebar img[src*="/images/feed-icon-16x16.png"] {height:11px!important}
#style-list {-moz-column-count:3!important;list-style:none!important;}

.category-style-top {padding:5px 10px 25px 20px!important;background-repeat:repeat-x!important;margin:5px!important;background-position:0 -121px!important;-moz-border-radius:20px!important;background-image: url("data:image/jpg;base64,")!important;}
.category-style-top h2 {margin:5px 0 5px 0!important;text-align:center!important;color:#fff!important;}
.category-style-top ul {margin:10px auto 0 auto!important;list-style:decimal inside!important;padding:0!important}

.category-meta h2 {margin:5px 0 5px 0!important;padding:0!important}
.category-meta h2+ul#style-list {margin:0!important;padding:15px!important;background-color:#fff!important;background-repeat:repeat-x!important;background-position:0 -121px!important;-moz-border-radius:20px!important;background-image: url("data:image/jpg;base64,")!important;}

h1+ul#style-list {list-style:decimal outside!important;padding:0}
h1+ul#style-list li {-moz-border-radius-bottomright:9px!important;-moz-border-radius-topright:9px!important;margin-left:20px!important;margin-bottom:3px!important;padding:2px 0 2px 6px!important;background:#eee!important;}

.form-controls input,.form-controls textarea {font-size:11px;line-height:1.5em}

#search-submit {background-color:#060!important;border-color:#ccc!important;border-style:outset!important}

#view-code {font-size:11px!important;line-height:1.5!important;padding:5px!important}

div.ad, #simple-link-ads,.simple-link-ads,#wonderful-ad-area {display:none!important}

}

@-moz-document url-prefix(http://userstyles.org/users/), url-prefix("https://userstyles.org/users/") {

#header-bar+h1,body > h2 {text-align:center!important;margin:10px 0 10px 0!important}
#header-bar+h1+ul:not([id]) {text-align: center !important;padding: 0!important;margin: 0!important;-moz-appearance: toolbar !important;list-style:none!important;font-size:11px!important;}
#header-bar+h1+ul:not([id]) li {margin: 4px!important;display:inline-block!important;-moz-appearance:toolbarbutton!important;}
#header-bar+h1+ul:not([id]) li>a {display: list-item !important;list-style-position: inside !important;text-decoration: none !important;color: -moz-fieldtext !important;cursor: pointer!important;}
#header-bar+h1+ul li>a[href*="migrate_openid"] {display:none!important}
#header-bar+h1+ul li>a[href*="new"] {list-style-image: url(data:image/png;base64,) !important;}
#header-bar+h1+ul li>a[href*="edit"] {list-style-image: url(data:image/png;base64,) !important;}
#header-bar+h1+ul li>a[href*="logout"] {list-style-image: url(data:image/png;base64,) !important;}
#header-bar+h1+ul li>a[href*="ments/"] {list-style-image: url(data:image/png;base64,) !important;}
#header-bar+h1+ul li>a[href*="ments_"] {list-style-image: url(data:image/png;base64,) !important;}
#header-bar+h1+ul li>a[href*="forum."] {list-style-image: url(data:image/png;base64,) !important;}
h2+ul#style-list {-moz-column-count:3!important;padding:0!important;margin:0!important;line-height:1.3em;text-align:right!important}

}

@-moz-document url-prefix(http://forum.userstyles.org/), url-prefix("https://forum.userstyles.org/") {

#Header {background-image:none!important;background-color:transparent!important;border:0!important}
#Header h1 {margin:25px 0 0 5px!important;text-align:left!important;}
#Header h1 a {font-size:32px!important;color:#fff!important;text-decoration:none!important;text-transform:capitalize!important}
#Header ul {margin:16px 0 0 0!important;text-align:left!important}
#Header ul li {background:none!important;margin-right:5px!important}
#Header ul li.TabOn {border:0!important;} #Header ul li.TabOn a {background:#000!important;color:#0e0!important;font-weight:normal!important}
#Header ul li a {background-color:#272!important;padding:5px 8px 5px 8px!important;-moz-border-radius-topleft:6px!important;-moz-border-radius-topright:6px!important}
#Header ul li a:hover {background-color:#161!important;}
#Panel {background:transparent!important;float:left!important;clear:left!important}
#Discussions li ul {background-color:#efffef!important;margin-bottom:5px!important;border:1px outset #000!important}
#Discussions li.Read ul,.NoNewComments {background-color:#fffeec !important;}
#Discussions li {background-color:transparent!important}
.PageList, div[class="ContentInfo Top"] > *,div[class="ContentInfo Bottom"] > * {color:#000!important;border:0!important}
#frmPostComment,[class="Account Preferences"] form,#frmAccountPersonal, #frmPostDiscussion,#SearchSimple {background-color:transparent!important;border:0!important;padding:8px!important;}
[class="Account Preferences"] form *,#frmAccountPersonal *,#frmPostDiscussion label, #SimpleSearchRadios,#SimpleSearchRadios label, .SearchSwitch {color:#000!important;}
#Session,#Session a {color:#fff!important}
}

@-moz-document url-prefix(http://blog.userstyles.org/){
body, #sidebar {background-image:none!important}
}
