css:
@-moz-document domain("search.lilo.org") {
body, div#projects {
    background: #101010 !important;
    color: #c5c5c5 !important;
}

div.gsc-adBlock, div.addiv {
    display: none !important;
}

a {
    background: #101010 !important;
    color: #AAC3F3 !important;
}

div.resulturl {
    color: #85A691 !important;
}

#logolilo {
    height: 0 !important;
    width: 0 !important;
    /* these numbers match the new image's dimensions */
    
    padding-left: 115px !important;
    padding-top: 40px !important;
    background: url(http://i.imgur.com/opdt59A.png) no-repeat !important;
}

#droppng {
    height: 0 !important;
    width: 0 !important;
    /* these numbers match the new image's dimensions */
    
    padding-left: 40px !important;
    padding-top: 40px !important;
    background: url(http://i.imgur.com/oRXP7xi.png) no-repeat !important;
}

#openprefs {
    height: 0 !important;
    width: 0 !important;
    /* these numbers match the new image's dimensions */
    
    padding-left: 40px !important;
    padding-top: 40px !important;
    background: url(http://i.imgur.com/k0v3DFX.png) no-repeat !important;
}

#searchbutton.btn-default, #searchbuttonmin.btn-default, #gsc-i-id1 {
    background-color: #232629 !important;
    border-color: #444444 !important;
    color: #c5c5c5 !important;
}

#searchbutton.btn-default:hover, #searchbuttonmin.btn-default:hover {
    background-color: #284172 !important;
}

.gsc-tabhInactive {
    background-color: #101010 !important;
}

#col1 {
    border-bottom: 1px solid #444444 !important;
    background: #101010 !important;
}

ul {
    padding-left: 20px !important;
}

#dropcount {
    padding-left: 5px !important;
}

#thetab, #nbresults, div.modal-header {
    border-bottom-color: #444444 !important;
}

div.modal-content {
    background-color: #101010 !important;
}

div.modal-footer {
    border-top: 2px solid #444444 !important;
}

li {
    margin-bottom: 0px !important;
}

img[src*="www.lilosearch.org/img/searchhome/icone-goutteeau.png"] {
    background-image: url("http://i.imgur.com/oRXP7xi.png");
    height: 0 !important;
    width: 0 !important;
    padding-left: 40px !important;
    padding-top: 40px !important;
}

span.noimage, img.favicon {
    background-color: #242729 !important;
    border: none !important;
}

a.favorite {
    box-shadow: 1px 1px 5px 1px grey !important;
    margin-top: 2px;
}

#divfavorites {
    height: 270px !important;
}

input#birds.form-control.ui-autocomplete-input, button.btn.btn-default.searchbutton {
    background-color: #232629 !important;
    border-color: #444444 !important;
    color: #c5c5c5 !important;
}

button.btn.btn-default.searchbutton {
    border-top-right-radius: 4px !important;
    border-bottom-right-radius: 4px !important;
}

div.project {
    background: #101010 !important;
}
}
