css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("movie4k.tv") {
  
  html{    
    background-image: url(http://wallpaper.pickywallpapers.com/1920x1080/soft-and-simple.jpg) !important;
    background-attachment: fixed !important;
    background-size: 100% 100% !important;
    height: 100% !important;
  }
  
  body{
    background: rgba(0,0,0,0.3) !important;
    min-height: 100% !important;
}
  
  #tdmoviesheader{
    background: none !important;
  }
  
  .moviedescription{
    font-size: 15px !important;
    background: rgba(255,255,255,0.4) !important;
    padding: 5px !important;
    margin-top: 2px !important;
    text-align: justify !important;
  }
  
  #content{
  }

  td,
  tr{
    background: rgba(255,255,255,0.1) !important;
    transition: 0.6s !important;
  }
  
  #maincontentnew>div>a>img,
  #maincontent2>div>a>img{
    border: 1px solid white !important;
    transition: 0.6s !important;
  }
  
  #maincontentnew>div>a>img:hover,
  #maincontent2>div>a>img:hover{
    border: 1px solid gray !important
  }
  
  #maincontent2{
    padding-left: 5px !important;
  }
  
  #maincontent2,
  #maincontentnew,
  tr:hover{
    padding-top: 5px !important;
    background: rgba(255,255,255,0.2) !important;
  }
  
  #maincontenttvshow>*{
    background: rgba(255,255,255,0.3) !important;
  }
    
  div{
    transition: 0.6s !important;
    border: none !important;
  }
  
  #tablemoviesindex2:hover{
    background: rgba(0,0,0,0.5) !important;
    color: white !important;
    transition: 0.6s !important;
    border-right: 3px solid white !important;
  }
  
  #tablemoviesindex2>a:hover{
    color: white !important;
  }
  
  a{
    color: black !important;
    text-decoration: none !important;
    color: #1C1C1C !important;
  }
  
  a:hover{
    color: black !important;
  }
  
  #tdmovies:hover{
   background: rgba(255,255,255,0.5) !important;
  }
  
  #xline2{
    opacity: 0 !important;
  }
  
  .menutag,
  .test123,
  #menue,
  .days,
  select{
    background: none !important;
    transition: 0.6s !important;
  }
    
  .aussen,
  .innen,
  .innen-1,
  select{
    transition: 0.6s !important;
    background: rgba(255,255,255,0.4) !important;
    color: #2E2E2E !important;
    border: none !important;
    padding: 1px !important;
  }
  
  .innen:hover,
  .innen-1:hover,
  option:hover{
    background: rgba(0,0,0,0.3) !important;
    color: #2E2E2E !important;
    transition: 0.6s !important;
  }
  
  input{
    background: rgba(255,255,255,0.3) !important;
    color: #2E2E2E !important;
    border: none !important;
    box-shadow: none!important;
    padding: 3px !important;
    border-radius: 1px 1px 1px 1px !important;
  }
  
  #searchAutoComplete{
    background: rgba(255,255,255,0.7) !important;
    border: none !important;
    border-bottom: 10px solid #2E2E2E !important;
    padding-left: 4px !important;
    padding-bottom: 4px !important;
  }
  
  table,
  td,
  tr{
    border: none !important;
    box-shadow: none !important;
  }
  
  #maincontent5>*{
    padding: 5px !important;
    background: rgba(255,255,255,0.3) !important;
  }
  
  #maincontent5>div>a>img{
    width: 0;
    height: 0;
    border-left: 140px solid #037CA9 !important;
    border-top: 80px solid transparent;
    border-bottom: 80px solid transparent;
    opacity: 0.5 !important;
    margin: 50px !important;
    transition: 0.6s !important;
  }
  
  #maincontent5>div>a>img:hover{
    opacity: 0.9 !important;
  }
  
  #SIMILARMOVIES,
  .SM_similarmovies{
    background: rgba(255,255,255,0.4) !important;
    color: black !important;
  }
  
  .SM_similarmovies{
    min-height: 190px !important;
  }  
  
  .SM_schatten.SM_fleft:hover,
  .SM_schatten.SM_fleft:hover .SM_pictitle{
    background: rgba(0,0,0,0.6) !important;
  }
  
  .SM_pictitle{
    margin-top: 24px !important;
    margin-bottom: 20px !important;
    padding-bottom: 5px !important;
  }
  
  #attentionIcon{
    transition: 6s !important;
  }  
  
  #feedBody,
  .feedBackground,
  #feedHeaderContainer{
    background: rgba(255,255,255,0.2) !important;
  }
  
  .mainmenue>a{
    font-family: Estrangelo Edessa !important;
    color: rgba(255,255,255,0.9) !important;
    font-size: 17px !important;
    margin-right: 5px !important;
  }
  
  .comments img{
    opacity: 0 !important;
  }
  
  .comment{
    background: rgba(255,255,255,0.3) !important;
  }
  
  .test,
  .test1a{
    background: transparent !important;
  }
  
  #boxwhite{
    background: rgba(0,0,255,0.1) !important;
    color: white !important;
  }
  
  #boxgrey{
    background: rgba(255,255,255,0.3) !important;
  }
  
  #boxgrey:hover{
    background: rgba(255,255,255,0.6) !important;
  }
    
}
