css:
@-moz-document domain("naver.com") {
  iframe[src^="http://ad.naver.com/"],
  /*메인광고*/ #da_top,#veta_top,#da_brand,#veta_branding,#footer_banner>li:nth-child(3),
  /*파워링크*/ .ad_section.section,
  /*지식인ad*/ .broadcast_kin,#ad_randicon,#pc_detail_bottom,
  /*웹툰광고*/ #adpostArea,.ads_sc,.preview_store
  {display:none!important}}
@-moz-document domain("blog.me"),domain("naver.com"){
  /*포스트ad*/ .post_adpost,.searchad{display:none!important}}

@-moz-document domain("search.naver.com"){
#header_wrap{position:fixed!important}
#container{padding-top:131px!important}
#wrap{background:none!important}
#footer{display:none!important}}

@-moz-document url("http://www.naver.com/") {
.button_login{border:0!important}
.button_login .bl_inner{background-color:transparent!important}
html{overflow:hidden!important}
.button_login>.bl_inner>:not(.bl_btn),div.lnb>div:not(#search),#container>div:not(#column_right),#column_right>:not(#com_cast):not(#account),#footer,#search_link,#da_brand,#ad_topexpand,[id*=promotion],#flashLogoId,.gnb {display:none!important}
#com_cast{z-index:100!important}
#container {width:100%!important;position:absolute!important;top:0px}
#search {width:431px!important}
.login_wrap,.profile,.service {border:1px solid #dcdcdc!important}
#column_right {position:absolute!important;top:0px!important;right:0px!important}
div.my_area {position:absolute!important;left:0px!important}
#sform {width:437px!important}
.lnb {position:absolute;top:35%;left:50%;width:372px;margin-left:-200px}
.lnb>* {position:relative!important;top:0!important;right:0!important;left:0!important}
.lnb>h1>img {display:none!important}
.lnb>h1 {background:url(https://upload.wikimedia.org/wikipedia/commons/thumb/2/23/Naver_Logotype.svg/520px-Naver_Logotype.svg.png)no-repeat!important;background-size:295px 60px!important;height:85px;left:70px!important}}

@-moz-document domain("my.naver.com"){
.profile .user_area{border-bottom:none!important}}

