css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 
  url-prefix("https://www.mendeley.com/library/"), 
  url-prefix("https://www.mendeley.com/viewer/"),
  url("https://www.mendeley.com/newsfeed/"), 
  url-prefix("https://www.mendeley.com/suggest/"), 
  url-prefix("https://www.mendeley.com/research-papers/"),
  url-prefix("https://www.mendeley.com/scopus-claim/"),
  url-prefix("https://www.mendeley.com/community/"),
  url-prefix("https://data.mendeley.com/"),
  url-prefix("https://www.mendeley.com/careers/"),
  url-prefix("https://www.mendeley.com/research-funding/"),
  url-prefix("https://www.mendeley.com/settings/"), 
  url-prefix("https://www.mendeley.com/profiles/"), /* url-prefix("https://www.mendeley.com/importer/"), */ 
  url-prefix("https://www.mendeley.com/import/iframe") 
{
  
  /** last url-prefix obtained from an <iframe> with id, "mendeley-import-panel-iframe" in saved publication page **/
  
  /* Dropdown options e.g. in Settings */
  select, select option {
    color: #aaaaaa;
  }
  
  /* Input fields */
  input, select, textarea {
    background-color: #282828;
    color: #aaaaaa;
  }
  
  /*********** https://www.mendeley.com/library/ ***********/
  
  /*** Top  ***/
  .masthead.web-library {
    background-color: #333333;
  }
  
  .masthead.web-library .logo {
    filter: invert(100%);
  }
  
  /* 'Feed', 'Library', 'Suggest', 'Stats',... */
  .masthead.web-library .navigation-list {
    background-color: #333333;
  }
  
  .masthead.web-library .navigation-list li {
    color: #aaaaaa;
  }
  
  /* Library Search */
  .masthead.web-library .masthead-library-search input {
    background-color: #282828;
  }
  
  /* Profile dropdown */
  .masthead.web-library .navigation-list li ul {
    background-color: #282828;
  }
  
  /*** Left-side Pane ***/
  #library-toolbar-container {
    background-color: #333333;
  }
  
  /* 'Add' dropdown */
  #library-toolbar > li button {
    background-color: #333333;
  }
  
  .no-touch #library-toolbar > li button:hover {
    background-color: #666;
  }
  
  #library-toolbar > li .menu {
    background-color: #333333;
  }
  
  #library-toolbar > li .menu li {
    color: #aaaaaa;
  }
  
  .no-touch #library-toolbar > li .menu li:hover {
    background-color: #484848;
  }
  
  /* 'My Library' */
  .accordion-pane {
    background-color: #333333 !important; /* rgb(166,166,166) #252525 */
  }
  
  .accordion .accordion-pane .accordion-trigger {
    border-top-color: #666;
    border-bottom-color: #666;
  }
  
  #content-navigation {
    border-right-color: #666;
  }
  
  /* Row */
  .folder-list .folder a {
    color: #aaaaaa;
  }
  
  .no-touch .folder-list .folder:hover, .no-touch .create-folder:hover, .create-folder-name {
    background-color: #484848;
  }
  
  .folder-list .folder a:hover {
    color: #bbb;
  }
  
  ul.folder-toolbar {
    background-color: #282828;
  }
  
  /* Folder 'â‹®' Dropdown */
  .contextmenu {
    background-color: #282828;
    border-color: #666;
  }
  
  .no-touch .contextmenu .menu li a:hover {
    background-color: #484848;
    color: #ccc;
  }
  
/*   .folder-list .folder .rename-folder {
    background-color: #282828;
  }
  
  .folderlist .renaming {
    background: #282828;
  } */
  
  /*** Middle Pane ***/
  .panel-components {
    background-color: #282828;
    color: rgb(166,166,166);
  }
  
  .document-list {
    background-color: #202020 !important;
    color: rgb(166,166,166);
  }
  
  /* Add to-Delete-Export Top bar */
  .content-toolbar {
    background-color: #202020;
    border-bottom-color: #666;
  }
  
  .selection-toolbar > li {
    color: #aaaaaa;
  }
  
  .no-touch .selection-toolbar > li button:hover {
    background-color: #282828;
  }
  
  #document-list-toolbar > li {
    border-left-color: #666;
  }
  
  .no-touch #document-list-toolbar > li button:hover {
    background-color: #282828;
  }
  
  #document-list-toolbar > li .menu {
    background-color: #202020;
    border-color: #666;
  }
  
  .no-touch #document-list-toolbar > li .menu li:hover {
    background-color: #333333;
    color: #ccc;
  }
  
  /** Row item **/
  .item.selected {
    background-color: #333333 !important;
  }
  
  .document-list ul .item {
    border-bottom-color: #484848;
  }
  
  /* Checkbox */
  input[type="checkbox"] + label span.tick-styled {
    color: #202020;
  }
  
  /* Favorite Star */
  .document-list ul .item .starred-indicator {
    color: #333333;
  }
  
  .document-list ul .item.selected .starred-indicator {
    color: #484848;
  }
  
  /** Pages Bottom bar **/
  .document-pagination {
    background-color: #202020;
    border-top-color: #666;
  }
  
  /* Empty Bottom Middle Pane on no results */
  #content-centralarea {
    background-color: #202020;
  }
  
  /* Right side Document-Details Pane */
  .tabs-container {
    background-color: #202020;
  }
  
  /* White fading shadow */
  .document-metadata-view .document-abstract.document-abstract-expandable .document-abstract-content::before {
    box-shadow: inset 0 -45px 55px -10px #282828; /* except color, remaing values kept same */
  }
  
  /* Empty Top Right Pane on no results */
  #content-related {
    background-color: #282828;
    border-left-color: #666;
  }
  
  /* Right side Document-Notes Pane */
  #document-details-notes .pad {
    background-color: #202020;
    border-color: #333333;
  }
  
  #document-details-notes .menu {
    background-color: #333333;
    border-left-color: #333333;
    border-right-color: #333333;
  }
  
  /* Headers 'Details', 'Notes' */
  #content-related .document-details-tabs button {
    border-bottom-color: #666;
  }
  
  #content-related .document-details-tabs button:hover {
    background-color: #333333;
    border-bottom-color: #666;
  }
  
  /* Files */
  .document-file-list ul li + li {
    border-top-color: #666;
  }
  
  /* Collapse Handle */
  #collapsehandle-inner {
    background: #363636;
  }
  
  #collapsehandle-inner::before {
    border-top-color: transparent;
    border-left-color: transparent;
    border-bottom-color: #363636;
    border-right-color: #363636;
  }
  
  #collapsehandle-inner::after {
    border-bottom-color: transparent;
    border-left-color: transparent;
    border-top-color: #363636;
    border-right-color: #363636;
  }
  
  #collapsehandle-inner .icon {
    color: #aaaaaa;
  }
  
  /* 'Edit' button */
  .panel.view .btn-edit {
    background-color: #333333;
    border-color: #666;
    color: #666;
  }
  
  /*** Right Pane on clicking 'Edit' ***/
  .document-metadata-edit {
    background-color: #202020;
  }
  
  .document-metadata-edit .document-edit-controls {
    background-color: #282828;
    border-bottom-color: #484848;
  }
  
  .document-metadata-edit .document-edit-controls .action-cancel {
    background-color: #333333;
    border-color: #666;
    color: #aaaaaa;
  }
  
  .document-metadata-edit .document-edit-tick-styled {
    color: #282828;
  }
  
/*   / * Right side Document Pane width * /
  #content-related {
    width: 500px; / * 336px * /
  }
  
  .content-related-collapsed #content-related {
    right: -500px; / * -336px * /
  }
  
  .panel.view .btn-edit {
    left: 404px; / * 240px; * /
  } */
  
  /* Top-level pane */
  .document-metadata-view {
    background-color: #282828;
    color: rgb(166,166,166);
  }
  
  .btn-details {
    background-color: #202020;
  }
  
  .btn-notes {
    background-color: #202020;
  }
  
  /*** Pop-up Pane on 'Add to' ***/
  
  /* Header */
  .modal .modal-content .modal-header {
    background-color: #484848;
    border-bottom-color: #666;
  }
  
  /* 'Create Folder' */
  .organize-new-container .organize-newfolder {
    background-color: #282828;
  }
  
  .organize-new-container .organize-newfolder input {
    color: #aaaaaa;
  }
  
  /* Existing folder rows */
  .organize-new-container .organize-newfolder.selected {
    background-color: #484848;
  }
  
  .organize-list-container .organize-list .node.selected {
    background-color: #363636 !important;
  }
  
  .no-touch .organize-list-container .organize-list .node:hover {
    background-color: #484848;
  }
  
  /* 'Add to' button */
  .organize-destination-container .organize-submit {
    border-color: #666;
  }
  
  /* Footer */
  .modal .modal-content .modal-footer {
    background-color: #333333;
    border-top-color: #484848;
  }
  
  /* 'Groups' tab */
  .organize-new-container .organize-newgroup a {
    background-color: #282828;
  }
  
  /*** Getting Started Screens ***/
  
  /* Welcome Pane */
  .welcome-screen, .welcome-screen .welcome-footer .divider span {
    background-color: #282828;
    border-color: #282828;
  }
  
  .mb-button-secondary {
    background-color: #202020;
  }
  
  /* Add and Create Pop-up */
  .bubble-wrap {
    background-color: #282828;
  }
  
  .bubble-wrap .bubble-content {
    color: #aaaaaa;
  }
  
  .bubble-wrap.point-up::before {
    border-bottom-color: #282828;
  }
  
  .bubble-wrap.point-left::before {
    border-right-color: #282828;
  }
  
  .bubble-wrap.point-right::before {
    border-left-color: #282828;
  }
  
  /*********** https://www.mendeley.com/profiles/ ***********/
  .completion-list li {
    background-color: #282828;
  }
  
  /*********** https://www.mendeley.com/settings/privacy/ ***********/
  
  /* 'Privacy Levels' Pane */
  #privacy-settings .card.info {
    background-color: #333333;
  }
  
  /*********** https://www.mendeley.com/viewer/ ***********/
  
  div#viewerContainer {
    background-color: #333333;
  }
  
  /*-*-* Related Sites *-*-*/
  
  .masthead {
    background-color: #202020;
  }
  
  .masthead .logo {
    filter: invert(100%);
  }
  
  /* 'Feed', 'Library', 'Suggest', 'Stats',... */
  .navigation-list {
    background-color: #202020;
  }
  
  .navigation-list li {
    color: #aaaaaa;
  }
  
  /* Profile dropdown */
  .navigation-list li ul {
    background-color: #282828;
  }
  
  img[alt="Profile Picture"] {
    opacity: 0.7;
  }
    
  /*********** https://www.mendeley.com/newsfeed/ ***********/
  
  /* Download App Pane */
  .promo.card.download {
    background-color: #333333;
    color: #aaaaaa;
  }
  
  /* 'Add your first document', 'Add a profile photo', 'Make some connections...' */
  .promo.card.secondary {
    background-color: #333333;
  }
    
  /*********** https://www.mendeley.com/suggest/ ***********/
  html body {
    background-color: #202020;
  }
  
  .headline {
    border-bottom-color: #484848;
  }
  
  .card {
    background-color: #282828;
    color: #aaaaaa;
    border-color: #666;
  }
  
  .category-header {
    border-top-color: #666;
  }
  
  .expandable-content {
    color: #888;
  }
  
  /** Pop-up Pane on 'Upload File' **/
  
  .modal {
    background-color: rgba(40,40,40,.75);
  }
  
  .modal .modal-content {
    background-color: #282828;
    box-shadow: 0 0 0 16px rgba(32,32,32,.5); /* non-color attributes are unchanged */
    color: #aaaaaa;
  }
  
  .upload-wizard .dropfile {
    background-color: #333333;
  }
  
  /** Suggestions List **/
  .publication .ellipsis-expander .see-more {
    background-color: #282828;
  }
  
  /*********** https://www.mendeley.com/research-papers/ ***********/
  
  /* Middle Bkgd Pane */
  .headline {
    background-color: #333333;
    background-image: none;
  }
  
  /* 'Add an article to get suggestions' pane */
  .onboarding-add-document {
    background-color: #333333;
  }
  
  /* Search dropdown */
  .masthead nav.masthead-search {
    background-color: #333333;
  }
  
  .masthead nav.masthead-search .masthead-search-field input {
    background-color: #282828;
  }
  
  /* Current Page No. */
  .pagination a.current span, .pagination a:active span, .pagination a:hover span {
    color: #007398;
  }
  
  /* 'Get full text' button */
  .button-secondary {
    background-color: #333333;
    border-color: #484848;
    color: #007398;
  }
  
  /** Search Results **/
  
  /* Item */
  .publication-list-item {
    border-top-color: #666;
  }
  
  /** Footer **/
  .footer {
    background-color: #202020;
  }
  
  .footer .footer-message {
    background-color: #333333;
  }
  
  /*********** https://www.mendeley.com/scopus-claim/ ***********/
  
  /* List of possible publications */
  .author-search-results-container .author-result {
    background-color: #333333;
    border-top-color: #666;
  }
  
  .author-search-results-container .author-result .author {
    color: #aaaaaa;
  }
  
  /*********** https://www.mendeley.com/community/ ***********/
  
  .card.promo.primary {
    background-color: #333333;
  }
  
  .community-list-welcome p {
    color: #aaaaaa;
  }
  
  /*********** https://data.mendeley.com/ ***********/
  
  /* Header */
  .logged-in .header-home .header-main {
    background-color: #333333;
  }
  
  .header-home .header-main-logo-loggedin {
    filter: invert(80%);
  }
  
  .header-main-loggedin .header-main-right-item {
    color: #aaaaaa;
  }
  
  /*********** https://www.mendeley.com/careers/ ***********/
  
  /* Header */
  .is-signed-in .banner > .wrapper {
    background-color: #333333;
  }
  
  .is-signed-in .editorial-nav__item a, .is-signed-in.home .editorial-nav__item a {
    color: #aaaaaa;
  }
  
  .primary-logo > a > img {
    filter: invert(100%);
  }
  
  .home .home-header h1 {
    color: #aaaaaa;
  }
  
  .home .search__form input, .home .search__form select {
    background-color: #333333;
  }
  
  /* Discipline */
  .home .browse {
    background-color: #333333;
  }
  
  .tabs__tab--active {
    background-color: #333333;
  }
  
  .home .browse .tabs__tab--active a, .home .browse .tabs__tab--active a:hover, .tabs a {
    color: #aaaaaa;
  }
  
  .home .band--primary--second {
    background-image: none;
  }
  
  /* 'Get Jobs', 'Upload your resume' section */
  .home .band--primary--third {
    background-color: #282828;
  }
  
  /* Sections below it */
  .home .band--primary--fourth, .home .band--primary--fifth, .home .band--primary--sixth {
    background-color: #282828;
  }
  
  /* 'Featured Employers' */
  .home .featured-recruiters {
    background-color: #333333;
  }
  
  h2.brick__header {
    color: #aaaaaa;
  }
  
  /* Page No.s */
  .paginator__item--active a {
    background-color: #484848;
  }
  
  /* footer */
  .contentinfo {
    background-color: #333333;
  }
  
  .contentinfo .footer-custom-content-top {
    background-color: #282828;
  }
  
  /**** https://www.mendeley.com/careers/jobs/ ****/
  
  .lister__item {
    border-top-color: #666;
  }
  
  /**** https://www.mendeley.com/careers/searchjobs/ ****/
  
  /* Top bar - 'Find a Job', 'Create Job Alert', etc. */
  .primary-nav__item a {
    color: #aaaaaa;
  }
  
  .container {
    background-color: #333333;
  }
  
  /* 'Email me jobs like this' */
  .highlight {
    background-color: #333333 !important;
  }
  
  /** Items **/
  
  /* 'View details' buttons */
  .button--lister-view-details, .button--lister-view-details:hover, .button--lister-view-details:focus {
    background-color: #333333;
    border-color: #484848;
  }
  
  .rec-logo {
    filter: invert(80%);
  }
  
  /** Details - on clicking 'View details' **/
  h1[itemprop="title"] {
    color: #aaaaaa;
  }
  
  .highlight-lighter {
    background-color: #333333 !important;
  }
  
  dl dt {
    color: #aaaaaa;
  }
  
  .grid-item p, .grid-item div {
    color: #aaaaaa;
  }
  
  /* Right-side Pane */
  .tabs a {
    background-color: #333333;
  }
  
  /* on-arrow-click dropdowns */
  h4.category-header.js-accordion-trigger.accordion-trigger.icon-before {
    color: #aaaaaa;
  }
  
  .scrollable {
    background-color: #282828;
  }
  
  /*********** https://www.mendeley.com/research-funding/ ***********/
  
  .landing-page-user-search {
    background-color: #282828;
  }
  
  /* 'Featured funders in Computer Science' */
  .funder-description-title {
    color: #aaaaaa;
  }
  
  /*********** https://www.mendeley.com/import/iframe ***********/
  
  /* Bkgd */
  .main, #toolbar-primary-container, #toolbar-secondary-container {
    background-color: #282828;
  }
  
  #mendeley-import-panel-iframe {
    background-color: #282828;
  }
  
  /* Dropdown list */
  .selectize-dropdown {
    background: #202020;
  }
  
  .target-location-picker-container .selectize-dropdown .option[data-selectable] {
    color: #aaaaaa;
  }
  
  /* Selected Tags */
  .selectize-input {
    background: #202020;
  }
  
  .selectize-input input {
    color: #aaaaaa;
  }
  
  .target-location-picker-container .selectize-control.multi .selectize-input > div.item {
    background-color: #0086ca;
    color: #f2f2f2;
  }
  
  /* Save button */
  .btn-primary {
    background-color: #3f3f3f;
    border-color: #3f3f3f;
  }
  
  .btn-primary[disabled] { /* while saving */
    background-color: #3f3f3f;
    border-color: #3f3f3f;
  }
  
  /* Title */
  .document-metadata-view .document-title {
    color: #aaaaaa;
    font-size: 100%;
  }
  
  /* Rows */
  .document-metadata-view section {
    border-bottom-color: #666;
  }
  
  .feedback .feedback-intro {
    border-top-color: #666;
  }
  
  /** Related Papers **/
  
  /* Header */
  .related-papers {
    background-color: #333333;
    border-top-color: #484848;
    border-bottom-color: #484848;
  }
  
  .related-papers .related-papers-heading .related-papers-header {
    color: #aaaaaa;
  }
  
  /* Row item */
  .document-item-tick-styled {
    color: #282828;
  }
  
  .document-item.selected {
    background-color: #363636;
  }
  
  .document-item-compact {
    border-bottom-color: #666;
  }
  
  /* Manual Entry forms */
  .document-metadata-edit .document-edit-select, .document-metadata-edit .document-edit-field {
    background-color: #282828;
  }
  
  .document-metadata-edit .document-edit-field {
    border-color: #666;
    color: #aaaaaa;
  }
  
  /* Manual Entry Tags */
  .document-metadata-edit .document-edit-tag-list {
    background-color: #282828;
  }
  
  .document-metadata-edit .document-edit-tag-list .document-edit-tag {
    background-color: #333333;
    border-color: #666;
  }
  
  .document-metadata-edit .document-edit-tag-list .document-edit-tag span, 
  .document-metadata-edit .document-edit-tag-list .document-edit-add-tag .document-edit-add-tag-input {
    color: #aaaaaa;
  }
  
/*   / *********** For any site where the Mendeley Web importer plugin is used i.e. http://, https:// *********** /
  
  / * For changing Bkgd while loading on saving a link * /
  div[style="position: fixed; top: 20px; right: 35px; bottom: 20px; margin: 0px; padding: 0px; z-index: 99999999; box-shadow: 3px 3px 20px rgba(0, 0, 0, 0.3); background: rgb(246, 246, 246) none repeat scroll 0% 0%;"] {
    background: #282828 !important;
  } */
  
  /**** PDF Viewer (Firefox) ****/
  
  .textLayer {
    background-color: black;
  }
  
}
