css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("itunes.apple.com") {

#main {
width: auto;
}
#main #content {
width: auto;
}
#main #content #left-stack {
width: 20%;
}
#main #content .center-stack {
width: 80%;
}
#main #content .center-stack div.track-list table {
width: 100%;
}
#main #content .track-list table tbody tr td {
height: auto;
vertical-align: top;
padding-top: 3px;
padding-bottom: 3px;
}
#main #content .track-list table tbody tr.selected td,
#main #content .track-list table tbody tr.selected td.index,
#main #content .track-list table tbody tr.selected td.view-in-itunes {
background: #e8e8e8 none;
}
tr.borderless + tr td,
tr.borderless + tr td.index,
tr.borderless + tr td.view-in-itunes {
background: #f0f0f0 none !important;
}
#main #content .track-list table.total-flexible-columns-2.total-columns-6 tr th.flexible-col, #main #content .track-list table.total-flexible-columns-2.total-columns-6 tr td.flexible-col {
max-width: none;
}
#main #content .center-stack > div.top-albums, #main #content .center-stack > div.top-songs, #main #content .center-stack > .top-albums-and-songs, #main #content .center-stack > .listeners-also-bought, #main #content .center-stack > div.swoosh, #main #content .center-stack div.movie-review-container, #main #content .center-stack > .track-list, #main #content .center-stack .stack-section .track-list, #main #content .center-stack > .related-radio-stations {
width: auto;
}
.flexible-col .text {
color: black;
overflow: visible;
}
#main #content .track-list table tr td > span > span.text, #main #content .track-list table tr td.name > span, #main #content .track-list table tr td span.itunes-plus, #main #content .track-list table tr td.price > span, #main #content .track-list table tr td > span a, #main #content .track-list table tr td > a span {
white-space: normal;
}
#main #content .center-stack div.track-list table {
width: auto;
}
  
}
