css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.springer.com"), domain("link.springer.com") {
  
  body.cms {
    background-color: #202020;
    background-image: none;
  }
  
  header {
    background-color: #333333;
  }
  
  div#header {
    background-color: #333333;
    background-image: none;
  }
  
  a#headerlogo {
    filter: invert(80%);
  }
  
  .big-search .search-field {
    background-color: #202020;
  }
  
  /* Search dropdown */
  #search-field .search-field--suggestions {
    background-color: #202020;
  }
  
  #search-field .search-field--suggestions li {
    color: #666;
    background-color: #202020;
  }
  
  #search-field .search-field--suggestions li:hover, #search-field .search-field--suggestions li.selected {
    background-color: #333333;
  }
  
  #search-field .search-field--suggestions li mark {
    color: #777;
  }
  
/*   / * Brighter Links * /
  a {
    color: #6a6868;
  }
  
  a:hover {
    color: #047fd5;
  } */
  
  /* div[role=main]{
    background-color: #202020;
  } */
  
  /* Menu Bar */
  div.navwrapper {
    background-color: #202020;
  }
  
  li#mainNavigation0.mainNavigationMark {
    color: #aaaaaa;
  }
  
  a#mainnav.home.mainNavigationLinkMark.noSubnavigation {
  }
  
  nav > ul > li > * > a {
    text-shadow: none;
  }
  
  nav > ul li.open .nav-main a, nav > ul > li .nav-main a:hover {
    background-color: #202020;
  }
  
  nav > ul > li .nav-sub {
    background-color: #202020;
  }
  
  nav > ul > li a {
    color: #aaaaaa;
    text-shadow: none;
  }
  
  nav > ul > li a:hover {
    background-color: #2c2c2c;
    text-shadow: none;
  }
  
  /* Table of Contents item */
  .product-toc dl.expander dd li:hover {
    background-color: #3f3f3f;
    border-top: none;
    border-bottom: none;
  }
  
  .product-toc dl.expander dd li {
    border-bottom-color: #666;
  }
  
  /* Heading Bars */
  .facet-header {
    background-image: none;
    background-color: #202020;
  }
  
  .facet-header .all {
    color: #aaaaaa;
  }
  
  /* Buttons */
  .btn {
    background-color: #202020;
    background-image: none;
    box-shadow: none;
  }
  
  /* Dropdown lists e.g. { Home, Subjects, Services, Products,.. } { Subdisciplines, Journals, Books, Series, Textbooks,.. } etc. */
  a.secNavHover {
    background-color: #3f3f3f;
  }
  
  div.secSubNav {
    background-color: #202020;
  }
  
  div.secSubNavTitle {
    background-color: #202020;
  }
  
  .dGrey {
    color: #aaaaaa !important;
  }
  
  div.secSubNav ul li a {
    color: #6a6868;
  }
  
  div.secSubNav ul li a:hover {
    color: #047fd5;
  }
  
  /* Middle Panes */
  .marketing-message {
    background-color: #202020;
  }
  
  .entitlement {
    background-color: #202020;
    box-shadow: inset 0 1px 0 #202020; /* Non-color part kept same */
    border-top-color: #202020;
    border-bottom-color: #202020;
  }
    
  .navwrapper {
    background-color: rgb(166,166,166);
  }
  
  .page-wrapper {
    background-color: #333333;
  }
  
  .product-page {
    background-color: #131313;
    color: #aaaaaa;
  }
  
  /* remove shadows */
  .product-page .product-title, .product-about, .extra-materials, .product-recommendation {
    box-shadow: none;
    border-bottom-color: #666;
  }
  
  .product-page .product-tabs a {
    text-shadow: none;
    box-shadow: none;
    color: #aaaaaa;
    border-color: #666;
  }
  
  .product-page .product-tabs li.active a {
    background-color: #131313;
    border-color: #666;
  }
  
  .box-primary {
    background-color: #333333;
    
  }
  
  .col-aside {
    background-color: #131313;
  }
  
  /*
  * Right column
  */
  .product-buy {
    background-image: none;
    background-color: #333333;
  }
  
  .product-buy .box-primary {
    background-color: #131313;
    color: #aaaaaa;
  }
  
  .expander {
    background-color: #131313;
    color: #aaaaaa;
  }
  
  .product-buy .expander dd ul li {
    color: #aaaaaa;
  }
  
  .buy-footer {
    background-color: #131313;
  }
  
  /** Book Metrics **/
  .book-metrics h2 {
    box-shadow: none;
    border-bottom-color: #666;
  }
  
  .book-metrics-items a {
    border-color: #666;
  }
  
  /* Bottom Pane */
  #footer {
    background-color: #202020;
    background-image: none;
    box-shadow: none;
  }
  
  /*** link.springer.com ***/
  
  body {
    background-color: #202020;
    background-image: none;
    color: #aaaaaa;
  }
  
  #logo {
    filter: invert(80%);
  }
  
  div#wrapper {
    background-color: #131313;
  }
  
  .expander-title {
    background-color: #262626;
    background-image: none;
    box-shadow: none;
  }
  
  /*** link.springer.com Book, Search Results ***/
  
  /* 'Chapter' header */
  .toc h2 {
    box-shadow: none;
  }
  
  .toc li {
    border-top-color: #666;
  }
  
  .content-item-list .no-access-message, .toc .no-access-message {
    background-color: #202020;
  }
  
  .content-item-list .content-type, .toc .content-type, .toc h3.content-type {
    color: #aaaaaa;
  }
  
  .content-item-list .no-access, .toc .no-access {
    background-color: #131313;
  }
  
  .content-item-list .snippet {
    color: #aaaaaa;
  }
  
  .content-item-list .no-access:hover, .toc .no-access:hover {
    background-color: #333333;
  }
  
  .content-item-list li .authors a, .content-item-list li .editors a, .document-main .authors a {
    color: #aaaaaa;
  }
  
  .content-item-list .meta a {
    color: #0470bb;
  }
  
  /*** Search Results ***/
  .wrapper {
    background-color: #202020;
    background-image: none;
  }
  
  .search-page .sub-search__result-list .result-item {
    box-shadow: none;
    border-bottom-color: #666;
  }
  
  /* list or results */
  .componentList, .whiteBox {
    background-color: #202020;
    color: #aaaaaa;
  }
  
  /* results row Shopping and 'ALL' pop-up pane */
  .shoppingActions .shoppingActionContainer, .shoppingActions div.btnContainer {
    background-color: #202020;
  }
  
  .shoppingActions td.leftCell, .shoppingActions td.rightCell {
    background-color: #202020;
  }
  
  .shoppingActions td.leftCell:hover, .shoppingActions td.rightCell:hover {
    background-color: #2c2c2c;
  }
  
  /* 'Sort listing by' */
  .searchSortBox {
    background-image: none;
  }
  
  ul.listBooks li h2 a {
    color: #0470bb;
  }
  
  /* Search bar */
  header .big-search .text {
    background-color: #202020;
  }
  
  .ui-autocomplete {
    background-color: #202020;
  }
  
  .ui-autocomplete li a:focus, .ui-autocomplete li a.ui-state-focus, .ui-autocomplete li a:hover, .ui-autocomplete li a.ui-state-hover {
    background-color: #333333;
  }
  
  .ui-autocomplete strong {
    color: #777;
  }
  
  /* Share bar */
  .moreActions {
    background-color: #202020;
  }
  
  a.btnFb, a.btnTwitter, a.btnWeibo, a.btnShare, a.btnRecommend {
    background-image: none;
  }
  
  ul.moreActionsList {
    background-image: none;
  }
  
  /* 'Results' */
  h2.currentCategory {
    color: #aaaaaa;
  }
  
  /* 'Additional Search Results on...' */
  .portlet {
    background-color: #202020;
  }
  
  .portlet li a.linkToOpenLayer {
    background-color: #262626;
    background-image: none;
  }
  
  .portletHeadline > h3 {
    color: #aaaaaa;
  }
  
  /* 'Recent Searches' */
  .portlet .accordionHead {
    background-image: none;
  }
  
  .accordionBody {
    background-color: #202020;
  }
  
  /* 'Feedback' */
  .portlet .portletBoxGrey {
    background-color: #262626;
    background-image: none;
  }
  
  /* Bottom */
  footer #footer {
    background-image: none;
    background-color: #202020;
  }
  
  /* 'Series in' results (on selecting from top dropdown lists) */
  
  .colLeftContent {
    background-color: #202020;
  }
  
  .flapHead, .flapHead:hover, .accordionHead, .flapHeadDummy {
    color: #aaaaaa;
    background-image: none;
  }
  
  /* Textbook results - Highlights */
  .teaser {
    background-color: #262626;
  }
  
  /* Refine Search */  
  .search-page .sub-search__filtering .facet-header, .search-page .sub-search__filtering .filter-header {
    background-color: #202020;
  }
  
  .facet-header, .search-page .sub-search__filtering .filter-header {
    border-bottom-color: #202020;
    box-shadow: inset 0 1px 0 0 #202020;
  }
  
  .facet ol li a, .search-page .sub-search__filtering .filter ol li a {
    border-color: #202020;
    color: #aaaaaa;
  }
  
  .facet-percentage {
    background-color: #202020;
    background-image: none;
  }
  
}
