@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("sofifa.com"), domain("disqus.com") {
/*-------text colors---------*/
body
	{color: #909090!important;}
.form-select
	{color: #808080;}
a
	{color: #24846A;}
.p
	{color: #000000;}
.toast
	{color: #579261;}
.table th>a
	{color: #46b1a3;}
h2
	{color: #CFB926;}
.content .header
	{color: #096A9F;}
.meta a
	{color: #118F5D}
.post-content .post-message p
	{color: #d85151;}
.tab-menu .item:hover
	{color: #7F7F7F;}
.btn,.button
    {color: #FDDB00;}
.btn:hover,.button:hover
	{color: #44CE80;}
.form-autocomplete .form-autocomplete-item:hover
	{color: #5C989E;}
/*------------borrar--------------*/
div.banner,footer
	{display: none !important;}
/*-----------bgfondos----------*/
.page-header,.sub,.toast,.table .colspan th,.table th,.table td,aside .table th, aside .table td,tr.starting td,tr.sub td,.sub-header
	{background: #25262D;} 
body,.tab-content,.form-input,input[type="checkbox"]:after
	{background: #161616;}
.form-autocomplete .form-autocomplete-input .form-input,.floating-header
	{background: transparent;}
.table.table-striped tbody tr:nth-of-type(odd),.chosen-container .chosen-drop,section.columns
	{background: #161616;}
.avatar-40,.avatar-60,.type-ahead,.avatar
	{background: #000;}
.real-trophies tr:nth-child(2n+1)
	{background: #1B1B1B;}
.real-trophies tr:nth-child(2n)
	{background: #202020 !important;}
.chosen-container-multi .chosen-choices
	{background-color: transparent;}
select,.card,.form-autocomplete .form-autocomplete-input,.form-autocomplete .form-autocomplete-list,.player .teams,.player .operation,#compare,table.pin
	{background-color: #161616;}
div.block,svg
	{background: rgb(50, 50, 50) none repeat scroll 0% 0% !important;}
/*-----hover----------*/
.nav a.item:hover,.tab-menu .item:hover,.form-autocomplete .form-autocomplete-item:hover
	{background: #000;}
.table.table-hover tbody tr:hover
	{background: #212121 !important;}
.btn,.button,.btn:hover,.button:hover
	{background: #3d4045;}
/*----------NO BORDER-----------*/
.btn,.button,.btn:hover,.button:hover
	{border: 0px solid red;}
/*--------BORDERSSSSSS-----------*/
.floating-header
	{border-bottom: 3px solid #3b3d48;}
.table th
	{border-color: #3b3d48;}
.gap
	{border-left: .1rem solid #3b3d48;}
 .type-ahead
	{border-left: 1px solid #444444; border-bottom: 1px solid #444444; border-right: 1px solid #444444;}
.chosen-container-multi .chosen-choices,.chosen-container .chosen-drop,input[type="number"],select,.card,.form-autocomplete .form-autocomplete-input,.form-autocomplete .form-autocomplete-list,.player .teams,.player .operation,#compare,table.pin
	{border: 1px solid #262626;}   
.table td, .table th
	{border-bottom: .1rem solid #3b3d48;}
table thead td, table thead th, table tbody td, table tbody th, table tfoot td, table tfoot th
	{border-bottom: 1px solid #262626;}
.form-input,.form-select
	{border: .1rem solid #262626;}
input[type="checkbox"]:before
	{border: 1px solid #047d72;}    
/*---------------------asdasdasd--------------------*/
input[type="checkbox"]:after
	{height: 15px;}
img[src$="http://cdn.sofifa.org/img/logo.png"]{
height:38px!important; /*width of new image*/
padding-left:113px!important; /*height of new image*/
width:0!important;
background-image:url("https://k60.kn3.net/F/C/C/6/2/F/A8B.png")!important
}
div#radardiv
{
  background: rgb(255, 255, 255) none repeat scroll 0% 0%;
  opacity: 0.5;
  border-radius: 50%;
}
.meta a
	{margin-right: 10px;}
/*--comments--*/
.post-content footer .voting .count
	{color: green;}
.post-content footer .voting .vote-up
	{color: red!important;}
.post-content footer .voting .vote-down
	{color: red!important;}
.post-content footer .voting .vote-down, .post-content footer .voting .vote-up,span.control
	{color: green !important;}
p[color*="808080"]
	{color: red !important;}
p[color*="000"]
	{color: red !important;}
}
