css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("delicious.com") {

  /*
  * Main Page
  */
  .content-wrapper {
    background-color:rgb(48,48,48) !important;
    /* background-color: #171717 !important; */
  }

  /* Link Tags */
  /* Default */
  .item .meta a {
    color: #cccccc;
  }
  /* On hovering */
  .item .meta a:hover {
    color: #ffffff;
  }
  
  /* Link Comment */
  /* Default */
  .item .note {
    color: #999999;
  }
  /* On hovering */
  .item:hover .note {
    color: #cccccc;
  }
  
  /* No Links found */
  .no-link-found {
    background-color: #444444;
    color: #c0c0c0;
  }
  
  /* Top Msg */
  .news .alert {
    background-color: #444444;
    color: #c0c0c0;
  }
  
  .news .alert a, .notification .alert a {
    color: #dddddd;
  }
  
  /* Left-side Pane */
  .site-nav-links li a {
    background-color: #444444;
  }
  
  .site-nav-links li.on a {
    background-color: #333333;
  }
  
  /* Search box */
  .site-nav-search.on input, .site-nav-search:focus input {
    background-color: #333333;
  }
  
  /* Current Filter Row */
  .filter {
    background-color: #2d2d2d;
  }
  
  .relatedtags-wrapper {
    background-color: #363636;
  }
  
  /* Top-Left User name */
  .sidebar {
    background-color: #444444;
  }
  
  /* Top-Middle User Name and details */
  .username {
    color: #c0c0c0;
  }
  .hero-unit .name .handle {
    color: #888888;
  }
  
  .hero-unit .info {
    text-shadow: none;
  }
  
  /* Tag Cloud */
  .tagcloud-wrapper, .tagbundles-wrapper, .datetable-wrapper { /* Tags, Tag bundles, Date bkgd. */
    background-color: #363636;
  }
  
  .tagcloud a { /* Tag Names default */
    color: #c0c0c0;
  }
  
  .tagcloud a:hover { /* Tag Names on hover */
    color: #ffffff;
  }
  
  .tag-filter-label { /* Filter tags by _ */
    color: #c0c0c0;
  }
  
  /* Ads */
  #logoImage {
    height: 0px;
    width: 0 px;
    max-height: 0px;
    max-width: 0px;
  }
  
  iframe#google_ads_frame1 {
    height: 0px;
    width: 0 px;
  }
  
  .c {
    background-color: #333333;
  }
  
  /*
  * Pop-up Window for Add/Edit Link
  */
  
  /* Whole Background */
  .modal-content {
    background-color: #333333;
  }
  
  /* Private links */
  .item.private {
    background-color: #363636;
  }
  
  /* Date */
  .modal-content .item-status {
    background-color: #333333;
  }
  
  /* URL */
  input#inputLinkUrl.form-control {
    background-color: #333333;
    color: #cccccc;
  }
  
  /* Title */
  input#inputLinkTitle.form-control {
    background-color: #333333;
    color: #cccccc;
  }
  
  /* Tags */
  input#tags.form-control.tag-input {
    background-color: #333333;
    color: #cccccc;
  }
  
  .inline-help {
    background-color: #333333;
    color: #cccccc;
  }
  
  /* Tags Dropdown */
  .tags-input-container .tag-input-candidates {
    background-color: #333333;
    color: #cccccc;
  }
  
  /* Tag suggestions */
  /* on hover */
  .tag-suggestion .tag-suggestion-item:hover {
    color: #cccccc;
  }
  
  /* Comment */
  .form-control {
    background-color: #333333;
    color: #cccccc;
  }
  
  /* buttons */
  .modal-content .modal-footer {
    background-color: #333333;
  }
  
  /*
  * Discover Links
  */
  input#tag-query.form-control {
    background-color: #333333;
    color: #cccccc;
  }
  
  body {
    background-color: #333333;
  }
  
  /*
  * Network Links
  */
  /* Default */
  .item.item-rich .note {
    color: #999999;
  }
  /* On hovering */
  .item.item-rich .note:hover {
    color: #cccccc;
  }
  
  /*
  * Add Link
  */
  input#url-input.form-control {
    background-color: #333333;
    color: #cccccc;
  }
  
  /*
  * Profile Page
  */
  input#display_name.form-control {
    background-color: #333333;
    color: #cccccc;
  }
  
  input#url.form-control {
    background-color: #333333;
    color: #cccccc;
  }
  
  input#email.form-control {
    background-color: #333333;
    color: #cccccc;
  }
  
  input#original_password.form-control {
    background-color: #333333;
    color: #cccccc;
  }
  
  input#new_password.form-control {
    background-color: #333333;
    color: #cccccc;
  }
  
  input#new_password_confirm.form-control {
    background-color: #333333;
    color: #cccccc;
  }
  
  div.load-more {
    background-color: #333333;
    color: #cccccc;
  }
  
  /*
  * About, Help, Apps, Tools, etc.
  */
  div#delicious {
    color: #c0c0c0;
  }
}
