css:
@-moz-document url-prefix("http://nosue.me/"), url-prefix("https://nosueip.azurewebsites.net/"), url-prefix("https://nosue.me") {
a {
   /* color: #267BC5; */
    color: #0CC7AF;
    text-shadow: 0 1px 3px rgba(0,0,0,.15);
}

a:hover {
    text-decoration: none;
    color: #B73375;
}
body {
    font-family: Exo\ 2,Arial Grande,Tahoma,Helvetica,Arial,sans-serif;;
    font-size: 14px;
    line-height: 1.42857143;
    background-image: url("https://new.ppy.sh/images/backgrounds/page-ddd.png")!important;
    background-repeat: repeat;
    padding: 60px;
}
.navbar-custom .navbar-brand {
    color: #fff !Important;
}
.navbar-custom .navbar-nav > .dropdown > a .caret {
    border-top-color: #E0E0E0;
    border-bottom-color: #c0c0c0;
}
.dropdown-menu > li > a {
    background-color: transparent;
    color: #000000;
}
.navbar-custom {
    background-image: url(https://new.ppy.sh/images/headers/forum-category.png);
    background-color: #D41788;
    border-color: #B01872;
}
.navbar-custom .navbar-nav > .active > a, .navbar-custom .navbar-nav > .active > a:hover, .navbar-custom .navbar-nav > .active > a:focus {
    background-color: transparent;
}
.navbar-custom .navbar-nav > li > a {
    color: #fff; 
}
.jumbotron {
    margin-bottom: 0px;
    color: #fff;
    background-image: url(http://new.ppy.sh/images/layout/forum-top-pippy.png), url(https://new.ppy.sh/images/headers/forum-top.png);
    background-repeat: no-repeat;
    padding-left: 100px;
    padding-top: 48px;
    padding-bottom: 48px;
    height: 210px;
    background-position: calc(55% - 0px) 0px;
    background-color: transparent;
}
.jumbotron p {
    margin-bottom: 15px;
    font-size: 21px;
    font-weight: 200;
    color: #fff;
    background-color: transparent;
    box-shadow: none !Important;
} 
.h1, h1 {
    color: #fff;
    text-shadow: 0 1px 3px rgba(0,0,0,.75);
}
.alert {
    background-image: url(https://new.ppy.sh/images/headers/forum-category.png) !Important;
}
.alert-danger {
    color: #fff;
    background-color: #a94442;
    border-color: #8E3C3A;;
}
.alert-success {
    color: #fff;
    background-color: #3c763d;
    border-color: #376436;
}
.alert-info {
    color: #fff;
    background-color: #3CA9E0;
    border-color: #378DB9;
}
.h2, h2 {
    color: #fff;
    font-size: 30px;
    padding: 5px;
    text-shadow: 0 1px 3px rgba(0,0,0,.75);
    background-color: #f6a;
    background-image: url(https://new.ppy.sh/images/headers/forum-category.png);
    text-align: center;
}
.container {
    width: 1100px;
}
.navbar-nav>li>.dropdown-menu {
    margin-top: 10px;
    width: 300px !Important;
    padding: 5px !Important;
    background-image: url(https://new.ppy.sh/images/headers/forum-category.png);
    background-color: #fff;
    border-radius: 5px;
    background-size: cover;
}
h2.form-signin-heading {
    background-image: url(https://new.ppy.sh/images/headers/login-box.jpg);
    background-size: cover;
    background-position: 0%;
}
.btn-primary {
    background-image: url(https://new.ppy.sh/images/backgrounds/button.svg);
    background-color: #4ad;
    background-size: 200px;
    background-position: 50% 50%;
}
.table-responsive {
    background-color: rgba(255, 255, 255, 0.60) !Important;
    /* background-image: url(https://new.ppy.sh/images/backgrounds/button.svg); */
    border-radius: 7px;
    box-shadow: 0 1px 2px rgba(0, 0, 0, 0.2);
}
.panel {
    background-color: inherit;
}
.nav-tabs {
    border-bottom: 2px solid #ddd;
}
img {
    border-radius: 5px;
}
.panel-body {
    background-color: #fff;
    box-shadow: 0 1px 2px rgba(0, 0, 0, 0.2);
}
.panel-heading { 
    background-image: url(https://new.ppy.sh/images/headers/forum-category.png) !Important;
    background-color: #4175D0 !Important;
    color: #fff !Important;
    text-shadow: 0 1px 3px rgba(0,0,0,.75);
    font-size: 20px;
    height: 50px;
}
p {
    background-color: #fff;
    padding: 10px;
    border-radius: 5px;
    box-shadow: 0 1px 2px rgba(0, 0, 0, 0.2);
}
}

@-moz-document url-prefix("http://nosue.me/u/"), url-prefix("https://nosue.me/u/"), url-prefix("http://nosue.me/manage"), url-prefix("http://nosue.me/clan/") {
a {
    color: #86e;
}
.row.no-vert-pad.panel.panel-default {
    background-image: url(https://new.ppy.sh/images/headers/forum-category.png);
    margin-top: 20px;
    border-color: #ddd;
    background-color: #86e !Important;
}
.h1, h1 {
    color: #fff;
}
h1#rank {
    color: #8DFF14;
}
.panel-heading { 
    background-image: url(https://new.ppy.sh/images/headers/forum-category.png) !Important;
    background-color: #86e !Important;
    color: #fff !Important;
    text-shadow: 0 1px 3px rgba(0,0,0,.75);
    font-size: 20px;
    height: 50px;
}
.panel-body {
    background-color: #fff;
}
.btn-default, .btn-default:hover {
    background-image: url(https://new.ppy.sh/images/backgrounds/button.svg) !Important;
    background-color: #4ad !Important;
    background-size: 200px;
    background-position: 50% 50%;
    color: #fff;
    border-color: #3E94C0;
    text-shadow: none !Important;
}
p {
    background-color: rgb(239, 239, 239);
}
.col-md-9 {
    background-color: #EFEFEF !Important;
    height: 306px;
    border-radius: 5px;
    box-shadow: 0 1px 2px rgba(0, 0, 0, 0.2);
}
}

@-moz-document url-prefix("http://nosue.me/register") {
.panel-heading {
    background-image: url(https://new.ppy.sh/images/headers/login-box.jpg) !Important;
    height: 60px;
    color: #fff !Important;
    font-size: 30px;
    text-shadow: 0 1px 3px rgba(0,0,0,.75);
}
button#registerButton,.btn-default.btn-default:hover {
    background-image: url(https://new.ppy.sh/images/backgrounds/button.svg) !Important;
    background-color: #4ad !Important;
    background-size: 200px;
    background-position: 50% 50%;
    border-color: #3E94C0;
    color: #fff;
    text-shadow: none;
}
.panel-body {
    background-color: #fff;
}
.text-danger, .text-success {
    box-shadow: none !Important;
}
}

@-moz-document url-prefix("http://nosue.me/leaderboard"), url-prefix("http://nosue.me/clans"), url-prefix("http://nosue.me/clan/") {
.panel-heading {
    background-image: url(https://new.ppy.sh/images/headers/forum-category.png) !important;
    background-color: #fc2 !Important;
    color: #fff !Important;
    height: 60px;
    font-size: 25px;
    text-shadow: 0 1px 3px rgba(0,0,0,.75);
}
a {
    color: #BF9300;
}
.row.no-vert-pad.panel.panel-default {
    background-image: url(https://new.ppy.sh/images/headers/forum-category.png) !important;
    background-color: #fc2 !Important;
}
.btn-success {
    background-image: url(https://new.ppy.sh/images/backgrounds/button.svg) !Important;
}
}

@-moz-document url-prefix("http://nosue.me/u/621"), url-prefix("http://nosue.me/u/3"), url-prefix("http://nosue.me/u/608"), url-prefix("http://nosue.me/u/604"), url-prefix("http://nosue.me/u/4"), url-prefix("http://nosue.me/u/551"), url-prefix("http://nosue.me/u/1733"), url-prefix("http://nosue.me/u/2061"), url-prefix("http://nosue.me/u/5"), url-prefix("http://nosue.me/u/556") {
/* For Nosue! Stuff Only! */
.row.no-vert-pad.panel.panel-default {
    background-image: url(https://new.ppy.sh/images/headers/forum-category.png);
    background-color: #040404 !Important;
}
h1#rank {
    color: red;
}
a {
    color: red;
}
}

@-moz-document url-prefix("http://nosue.me/team"), url-prefix("http://nosue.me/changelog") {
a {
    color: red;
}
.panel-heading {
    background-image: url(https://new.ppy.sh/images/headers/forum-category.png) !important;
    background-color: #040404 !Important;
    color: #fff !Important;
    height: 60px;
    font-size: 25px;
    text-shadow: 0 1px 3px rgba(0,0,0,.75);
}
.panel-body {
    padding: 15px;
    background-color: #fff;
}
}

@-moz-document url-prefix("http://nosue.me/forum/"), url-prefix("http://nosue.me/forum/category/"), url-prefix("http://nosue.me/forum/topic/") {
a {
    color: #fff;
    text-shadow: 0 1px 3px rgba(0,0,0,.45);
}
a:hover {
    color: #B9BCFF;
}
.forumItem {
    background-color: #26B300;
    background-image: url(https://new.ppy.sh/images/headers/forum-category.png);
    border-radius: 5px;
    height: 120px;
    padding: 10px;
}
.forumItem:hover {
    box-shadow: inset 0 0 0 5px #1A7302;
}
h3 {
    color: #fff;
    text-shadow: 0 1px 3px rgba(0,0,0,.75)
}
p {
    color: #fff;
    padding: 0px !Important;
    background-color: transparent !Important;
    box-shadow: none !Important;
}
.col-xs-10 {
    color: #F7F7F7;
}
}

@-moz-document url-prefix("http://nosue.me/supporter"), url-prefix("http://nosue.me/beatmaps") {
.panel-heading {
    text-align: center;
    height: 80px;
    background-color: #EE6165 !Important;
}
input.vcenter {
    color: #000;
    border-radius: 10px;
    text-align:	center;
    top: 80%;
    width: 750px;
}
}
