css:
/*

Firefox OS X El Capitan theme by devegadev from userstyles.org
Modified by Orson Wang 
1. left align URL text and tab align. 
2. fixed forward and backward button icon display.
3. Hide close button on each tab

Version 1.2 

*/

@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

* {
  font-family: -apple-system !important;
}  /* Update for 10.12 */

#titlebar-buttonbox-container {
	margin-left: 5px !important;
}

#nav-bar {
	background: -moz-linear-gradient(rgb(230, 230, 230), rgb(220, 220, 220)) !important;
	border: 0 !important;
	border-bottom: 1px solid rgba(0, 0, 0, .2) !important;
	height: 38px !important;
	margin-top: -12px !important;
}

#nav-bar:-moz-window-inactive {
	background: rgb(246, 246, 246) !important;
	border-bottom: 1px solid rgba(0, 0, 0, .1) !important;
}

#nav-bar[inFullscreen] {
	margin-left: -72px !important;
	margin-top: 0 !important;
}

#nav-bar-customization-target {
	margin-left: 72px !important;
}

#nav-bar-customization-target[inFullscreen] {
	margin-left: 0 !important;
}

#back-button {
	background: -moz-linear-gradient(rgb(255, 255, 255), rgb(242, 242, 242)) !important;
	border: 0 !important;
	border-radius: 4px !important;
	border-right: 1px solid rgba(0, 0, 0, .1) !important;
	box-shadow: 0 0 1px rgba(0, 0, 0, .33) !important;
	height: 22px !important;
	line-height: 14px !important;
	max-width: 27px !important;
	min-width: 27px !important;
}

#back-button .toolbarbutton-icon {
	/*list-style-image: url(back.png) !important;*/
/*	-moz-image-region: rect(0px 7px 12px 0px) !important;*/ /* Remove to fix button image display */
	opacity: 1 !important;
}

#forward-button {
	background: -moz-linear-gradient(rgb(255, 255, 255), rgb(242, 242, 242)) !important;
	border: 0 !important;
	border-left: 1px solid rgba(0, 0, 0, .1) !important;
	border-radius: 4px !important;
	box-shadow: 0 0 1px rgba(0, 0, 0, .33) !important;
	height: 22px !important;
	margin-left: 5px !important;
	max-width: 27px !important;
	min-width: 27px !important;
}

#forward-button .toolbarbutton-icon {
	/*list-style-image: url(forward.png) !important;*/
/*	-moz-image-region: rect(0px 7px 12px 0px) !important;*/ /* Remove to fix button image display */
	margin-left: -2px !important;
	opacity: 1 !important;
}

#back-button:-moz-window-inactive,
#forward-button:-moz-window-inactive,
#urlbar:-moz-window-inactive,
.searchbar-textbox:-moz-window-inactive,
.toolbarbutton-1[cui-areatype="toolbar"]:not(#new-tab-button):-moz-window-inactive,
.toolbarbutton-1[cui-areatype="toolbar"]:not(#new-tab-button):hover:-moz-window-inactive,
#PanelUI-menu-button:-moz-window-inactive,
#PanelUI-menu-button:hover:-moz-window-inactive {
	background: rgb(246, 246, 246) !important;
	border: 1px solid rgba(0, 0, 0, .11) !important;
	box-shadow: none !important;
}

#back-button[disabled] .toolbarbutton-icon,
#forward-button[disabled] .toolbarbutton-icon {
	opacity: .3 !important;
}

#back-button:-moz-window-inactive .toolbarbutton-icon,
#forward-button:-moz-window-inactive .toolbarbutton-icon,
.toolbarbutton-1[cui-areatype="toolbar"]:not(#new-tab-button):-moz-window-inactive .toolbarbutton-icon,
.toolbarbutton-1[cui-areatype="toolbar"]:not(#new-tab-button):hover:-moz-window-inactive .toolbarbutton-icon,
#bookmarks-menu-button:-moz-window-inactive .toolbarbutton-menubutton-dropmarker .dropmarker-icon,
#bookmarks-menu-button:hover:-moz-window-inactive .toolbarbutton-menubutton-dropmarker .dropmarker-icon,
#PanelUI-menu-button:-moz-window-inactive .toolbarbutton-icon,
#PanelUI-menu-button:hover:-moz-window-inactive .toolbarbutton-icon {
	opacity: .5 !important;
}

#urlbar {
	background: -moz-linear-gradient(rgb(255, 255, 255), rgb(242, 242, 242)) !important;
	border: 0 !important;
	border-radius: 4px !important;
	box-shadow: 0 0 1px rgba(0, 0, 0, .33) !important;
	height: 22px !important;
	margin: 0 13px 0 17px !important;
/*	text-align: center !important;*/ /* I used to left align the url text, which is system default */
}

#urlbar-reload-button:-moz-window-inactive .toolbarbutton-icon,
#urlbar-stop-button:-moz-window-inactive .toolbarbutton-icon {
	-moz-image-region: rect(1px 12px 13px 0px) !important;
	opacity: .5 !important;
}

#page-proxy-favicon:-moz-window-inactive,
.searchbar-search-button-container:-moz-window-inactive .searchbar-search-button {
	opacity: .6 !important;
}

.searchbar-textbox {
	box-shadow: 0 2px 0 -1px rgba(0, 0, 0, .1) !important;
	height: 22px !important;
	text-align: center !important;
}

.searchbar-textbox[focused="true"] {
	border-color: #FFF !important;
}

.toolbarbutton-1[cui-areatype="toolbar"]:not(#new-tab-button) {
	background: -moz-linear-gradient(rgb(255, 255, 255), rgb(242, 242, 242)) !important;
	border: none !important;
	border-color: rgba(0, 0, 0, .11) !important;
	margin-left: 4px !important;
	margin-right: 4px !important;
	border-radius: 4px !important;
	box-shadow: 0 0 1px rgba(0, 0, 0, .33) !important;
	max-height: 22px !important;
	min-height: 22px !important;
}

#PanelUI-button,
.toolbarbutton-1[cui-areatype="toolbar"]:not(#new-tab-button):hover {
	border: none !important;
}

#bookmarks-menu-button,
#bookmarks-menu-button .box-inherit {
	border: none !important;
	border-right: 1px solid rgb(226, 225, 226) !important;
	box-shadow: none !important;
}

#bookmarks-menu-button .toolbarbutton-menubutton-dropmarker {
	background: transparent !important;
	border: none !important;
	box-shadow: none !important;
	transition: color 0s !important;
}

#bookmarks-menu-button .toolbarbutton-menubutton-dropmarker:before {
	background: transparent !important;
}

#PanelUI-menu-button {
	background: -moz-linear-gradient(rgb(255, 255, 255), rgb(242, 242, 242)) !important;
	border: none !important;
	border-color: rgba(0, 0, 0, .11) !important;
	border-radius: 4px !important;
	box-shadow: 0 0 1px rgba(0, 0, 0, .33) !important;
	margin: 8px 14px 8px -2px !important;
	max-height: 22px !important;
	min-height: 22px !important;
}

#PanelUI-menu-button:hover {
	border: none !important;
}

.toolbarbutton-1[cui-areatype="toolbar"]:not(#new-tab-button) .toolbarbutton-icon,
#bookmarks-menu-button .toolbarbutton-menubutton-dropmarker .dropmarker-icon,
#PanelUI-menu-button .toolbarbutton-icon,
#urlbar-reload-button .toolbarbutton-icon,
#urlbar-stop-button .toolbarbutton-icon {
	opacity: .8 !important;
}

#nav-bar .toolbarbutton-menubutton-button-dropmarker,
#nav-bar .toolbarbutton-1 {
	transition: background 0s !important;
}

#nav-bar .toolbarbutton-menubutton-button-dropmarker[open],
#nav-bar .toolbarbutton-1[open] {
	background: rgb(108, 107, 108) !important;
	box-shadow: none !important;
}

.toolbarbutton-menubutton-button[open] .toolbarbutton-icon,
.toolbarbutton-1[open] .toolbarbutton-icon,
.toolbarbutton-1[open] .dropmarker-icon {
	filter: invert(100%) brightness(200%) grayscale(100%) !important;
	opacity: 1 !important;
}

#PersonalToolbar {
	background: -moz-linear-gradient(rgb(219, 219, 219), rgb(213, 213, 213)) !important;
	border-bottom: 1px solid rgba(0, 0, 0, .15) !important;
	-moz-box-ordinal-group: 3 !important;
	height: 24px !important;
	margin-top: -1px !important;
	margin-bottom: 0 !important;
}

#PersonalToolbar:-moz-window-inactive {
	background: rgb(246, 246, 246) !important;
	border-bottom: 1px solid rgba(0, 0, 0, .1) !important;
}

#PersonalToolbar #personal-bookmarks {
	margin-top: 3px !important;
}

#PersonalToolbar #personal-bookmarks .toolbarbutton-icon {
	display: none !important;
}

#PersonalToolbar #personal-bookmarks .toolbarbutton-text {
	color: rgba(0, 0, 0, .7) !important;
}

#PersonalToolbar #personal-bookmarks .bookmark-item:hover {
	background: rgb(166, 166, 166) !important;
	border-radius: 4px !important;
}

#PersonalToolbar #personal-bookmarks .bookmark-item:hover .toolbarbutton-text {
	color: rgb(255, 255, 255) !important;
	text-shadow: none !important;
}

#PersonalToolbar #personal-bookmarks:-moz-window-inactive .bookmark-item:hover {
	background: rgb(209, 209, 209) !important;
}

#PersonalToolbar #personal-bookmarks:-moz-window-inactive .toolbarbutton-text {
	color: rgb(168, 168, 168) !important;
}

#PersonalToolbar #personal-bookmarks:-moz-window-inactive .bookmark-item:hover .toolbarbutton-text {
	color: rgb(84, 84, 84) !important;
}

#PersonalToolbar #PlacesToolbarItems {
   -moz-box-pack: center !important;
}

#TabsToolbar {
	background: -moz-linear-gradient(rgb(203, 202, 203), rgb(196, 195, 196)) !important;
	box-shadow: none !important;
	-moz-box-ordinal-group: 4 !important;
	height: 23px !important;
	margin-left: -69px !important;
	margin-right: -15px !important;
	margin-bottom: 0 !important;
	overflow: hidden !important;
}

#TabsToolbar[inFullscreen] {
	height: 24px !important;
	margin-bottom: -1px !important;
	margin-left: -1px !important;
	margin-right: -1px !important;
	margin-top: 0 !important;
}

#TabsToolbar:-moz-window-inactive {
	background: rgb(246, 246, 246) !important;
}

#TabsToolbar .tabbrowser-tabs {
	background: -moz-linear-gradient(rgb(219, 219, 219), rgb(213, 213, 213)) !important;
	margin-top: -1px !important;
	max-height: 22px !important;
	min-height: 22px !important;
}

#TabsToolbar[inFullscreen] .tabbrowser-tabs {
	margin-top: -10px !important;
	max-height: 22px !important;
	min-height: 22px !important;
	padding-top: 1px !important;
}

#TabsToolbar .tabbrowser-tab {
    border: none !important;
    border-left: 1px solid rgba(0, 0, 0, .15) !important;
    border-right: 1px solid rgba(0, 0, 0, .15) !important;
    padding: 0 !important;
}

#TabsToolbar[inFullscreen] .tabbrowser-tab {
    margin-top: -2px !important;
}

#TabsToolbar .tabbrowser-tab:not([pinned]) {
	margin-left: -1px !important;
/*	max-width: 100% !important;*/ /* This operation can centerize new tab, but it will cause many strange side effect when close tab which was no rightmost. So I disable it. */
	padding-right: 1px !important;
}

#TabsToolbar .tabbrowser-tabs[positionpinnedtabs] {
	border-top: 1px solid rgba(0, 0, 0, .25) !important;
	margin-left: 1px !important;
}

#TabsToolbar .tabbrowser-tab[pinned] {
	border: none !important;
	border-right: 1px solid rgba(0, 0, 0, .15) !important;
	padding-top: 3px !important;
	padding-bottom: 3px !important;
}

#TabsToolbar .tabbrowser-tab:-moz-window-inactive {
	background: rgb(236, 236, 236) !important;
	border-right: 1px solid rgba(0, 0, 0, .1) !important;
}

#TabsToolbar .tabbrowser-tab[selected] {
	background: -moz-linear-gradient(rgb(219, 219, 219), rgb(213, 213, 213)) !important;
}

#TabsToolbar .tabbrowser-tab[selected]:-moz-window-inactive {
	background: rgb(246, 246, 246) !important;
}

#TabsToolbar .tabbrowser-tab:not([selected]) {
	background: -moz-linear-gradient(rgb(184, 184, 184), rgb(179, 179, 179)) !important;
}

#TabsToolbar .tabbrowser-tab:hover:not([selected]):-moz-window-inactive {
	background: rgb(227, 227, 227) !important;
}

#TabsToolbar .tabs-newtab-button:hover,
#TabsToolbar .tabbrowser-tab:hover:not([selected]) {
	background: -moz-linear-gradient(rgb(175, 175, 175), rgb(170, 170, 170)) !important;
}

#TabsToolbar .tabbrowser-tab .tab-label {
	color: rgba(0, 0, 0, .75) !important;
}

#TabsToolbar .tabbrowser-tab:-moz-window-inactive .tab-label {
	color: rgba(0, 0, 0, .5) !important;
}

#TabsToolbar .tabbrowser-tab[selected] .tab-label {
	color: rgba(0, 0, 0, .8) !important;
}

#TabsToolbar .tabbrowser-tab[selected]:-moz-window-inactive .tab-label {
	color: rgba(0, 0, 0, .6) !important;
}

#TabsToolbar .tabbrowser-tab:after,
#TabsToolbar .tabbrowser-tab:before,
#TabsToolbar .tabbrowser-tab[pinned]:after,
#TabsToolbar .tabbrowser-tab[pinned]:before {
	display: none !important;
}

#TabsToolbar .tab-background {
	margin: 0 !important;
	background: transparent !important;
}

#TabsToolbar .tab-background-start,
#TabsToolbar .tab-background-end {
    display: none !important;
}

#TabsToolbar .tab-background-middle {
	background: transparent !important;
	margin: -4px -2px !important;
}

#TabsToolbar .tabs-newtab-button {
    /*background: url(newtab.png) center no-repeat, -moz-linear-gradient(rgb(184, 184, 184), rgb(179, 179, 179)) !important;*/
	border-left: 1px solid rgba(0, 0, 0, .1) !important;
	margin: 0 !important;
	margin-left: -1px !important;
	width: 24px !important;
}

#TabsToolbar #new-tab-button {
    /*background: url(newtab.png) center no-repeat, -moz-linear-gradient(rgb(184, 184, 184), rgb(179, 179, 179)) !important;*/
	border-left: 1px solid rgba(0, 0, 0, .1) !important;
	border-right: 1px solid rgba(0, 0, 0, .1) !important;
	margin-left: -1px !important;
	width: 24px !important;
}

#TabsToolbar[inFullscreen] #new-tab-button {
    margin-top: -8px !important;
}

#TabsToolbar .tabs-newtab-button .toolbarbutton-icon,
#TabsToolbar #new-tab-button .toolbarbutton-icon {
	list-style-image: none !important;
	width: 24px !important;
}

#TabsToolbar .tabs-newtab-button:hover,
#TabsToolbar #new-tab-button:hover {
	/*background: url(newtab.png) center center no-repeat, -moz-linear-gradient(rgb(175, 175, 175), rgb(170, 170, 170)) !important;*/
}

#TabsToolbar:-moz-window-inactive .tabs-newtab-button,
#TabsToolbar:-moz-window-inactive #new-tab-button {
	/*background: url(newtab.png) center center no-repeat, rgb(240, 240, 240) !important;*/
	opacity: .7;
}

#TabsToolbar:-moz-window-inactive .tabs-newtab-button:hover,
#TabsToolbar:-moz-window-inactive #new-tab-button:hover {
	/*background: url(newtab.png) center center no-repeat, rgb(231, 231, 231) !important;*/
	opacity: .7;
}

#TabsToolbar .tabbrowser-arrowscrollbox {
	border-right: 1px solid rgba(0, 0, 0, .1) !important;
	margin-left: 1px !important;
	margin-bottom: -3px !important;
}

#TabsToolbar[inFullscreen] .tabbrowser-arrowscrollbox {
	border-right: 1px solid rgba(0, 0, 0, .1) !important;
	margin-left: 1px !important;
	margin-bottom: -1px !important;
}

#TabsToolbar .arrowscrollbox-scrollbox {
	padding: 0 !important;
}

#TabsToolbar .scrollbutton-up {
	/*background: url(back.png) center no-repeat !important;*/
	border-right: 1px solid rgba(0, 0, 0, .2) !important;
	margin-top: 0 !important;
	transition: none !important;
	width: 16px !important;
}

#TabsToolbar .scrollbutton-down {
	/*background: url(forward.png) 8px center no-repeat !important;*/
	margin-top: 0 !important;
	transition: none !important;
	width: 16px !important;
}

#TabsToolbar .scrollbutton-up .toolbarbutton-icon,
#TabsToolbar .scrollbutton-down .toolbarbutton-icon {
	list-style-image: none !important;
	width: 16px !important;
}

#TabsToolbar .scrollbutton-up[disabled],
#TabsToolbar .scrollbutton-down[disabled] {
	opacity: .5 !important;
}

#TabsToolbar .scrollbutton-up:-moz-window-inactive {
	/*background: url(back.png) center no-repeat, rgb(255, 255, 255) !important;*/
	opacity: .5;
}

#TabsToolbar .scrollbutton-down:-moz-window-inactive {
	/*background: url(forward.png) center no-repeat, rgb(255, 255, 255) !important;*/
	opacity: .5;
}

#TabsToolbar #alltabs-button {
	background: -moz-linear-gradient(rgb(184, 184, 184), rgb(179, 179, 179)) !important;
	border: none !important;
	border-right: 1px solid rgba(0, 0, 0, .1) !important;
	margin-bottom: -1px !important;
}

#TabsToolbar[inFullscreen] #alltabs-button {
	background: -moz-linear-gradient(rgb(184, 184, 184), rgb(179, 179, 179)) !important;
	border: none !important;
	border-right: 1px solid rgba(0, 0, 0, .1) !important;
	margin-bottom: 0px !important;
	margin-top: -9px !important;
}

#TabsToolbar #alltabs-button:hover {
	background: -moz-linear-gradient(rgb(175, 175, 175), rgb(170, 170, 170)) !important;
}

#TabsToolbar #alltabs-button:-moz-window-inactive {
	background: rgb(237, 237, 237) !important;
}

#TabsToolbar #alltabs-button:-moz-window-inactive .toolbarbutton-icon,
#TabsToolbar #alltabs-button:hover:-moz-window-inactive .toolbarbutton-icon {
	opacity: .5 !important;
}

#TabsToolbar .tabbrowser-tab .tab-icon-image,
#TabsToolbar .tabbrowser-tab .tab-throbber {
	-moz-box-ordinal-group: 2 !important;
	margin-left: -4px !important;
	margin-right: 2px !important;
}

#TabsToolbar .tabbrowser-tab .tab-icon-sound {
	-moz-box-ordinal-group: 2 !important;
	margin-left: 0 !important;
}

#TabsToolbar:not([inFullscreen]) .tabbrowser-tab .tab-icon-image,
#TabsToolbar:not([inFullscreen]) .tabbrowser-tab .tab-throbber {
	margin-top: 0 !important;
}

#TabsToolbar .tabbrowser-tab .tab-label {
	-moz-box-ordinal-group: 3 !important;
}

#TabsToolbar .tabbrowser-tab .tab-close-button,
#TabsToolbar .tabbrowser-tab:hover .tab-icon-image,
#TabsToolbar .tabbrowser-tab:hover .tab-throbber,
#TabsToolbar .tabbrowser-tab[pinned]:hover .tab-close-button,
#TabsToolbar .tabbrowser-tab[pinned] .tab-icon-overlay {
	display: none !important;
}

#TabsToolbar:not([inFullscreen]) .tabbrowser-tab .tab-stack {
	margin-top: -2px !important;
}

#TabsToolbar .tabbrowser-tab:hover .tab-close-button {
	display: block !important;
	margin-left: -4px !important;
	margin-right: 2px !important;
	margin-top: 0 !important;
}

#TabsToolbar[inFullscreen] .tabbrowser-tab:hover .tab-close-button {
	display: block !important;
	margin-left: -4px !important;
	margin-right: 2px !important;
	margin-top: 2px !important;
}

#TabsToolbar .tabbrowser-tab:hover .tab-close-button .toolbarbutton-icon {
	/*list-style-image: url(closetab.png) !important;*/
	-moz-image-region: rect(0px 16px 16px 0px) !important;
	display: none !important; /* Hide close button on each tab */
}

#TabsToolbar .tabbrowser-tab:hover .tab-close-button:hover .toolbarbutton-icon {
	background: rgba(90, 90, 90, .2) !important;
	border-radius: 2px !important;
}

#TabsToolbar:-moz-window-inactive .tabbrowser-tab:hover .tab-close-button {
	opacity: .45 !important;
}

#TabsToolbar .tabbrowser-tab[pinned]:hover .tab-icon-image {
	display: block !important;
}
