css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("linux.org.ru") {
html,body, .ui-menu-item {
background-color:#D9DAC6 !important;
}

#hd .menu a {
color: #155AFF !important;
}
body, #bd h1, #bd h2, .hideon-tablet, #bd .forum table th,
pre code, #bd .forum table thead, #bd .forum table tfoot, #bd fieldset legend {
color:#00000d !important;
}

a, #bd h2 *, #bd .sign a, #bd .sign a:visited, #bd .forum table tbody td *,
#bd .forum table tbody td a.secondary:visited,
.message-table .tag, .group-label  {
color: #0000AD !important;
}
#hd h1 {
color:#fff !important;
}

#bd .tags a, #bd .tags a:visited, #bd a.tag, #bd a.tag:visited {
color:#306AF9 !important;
}

#bd .sign a, #bd .sign a:visited {
text-decoration: none !important;
}

#bd .sign a:hover {color:#0080C0 !important;
text-decoration: underline !important;
}


#bd .forum table thead, #bd .forum table thead, #bd .forum table tfoot {
background-color:#C7CBB1 !important;
}

#bd .forum table thead, #bd .forum table tfoot {
border: 1px solid #555753 !important;
}

#bd .forum table tbody td {
border-color:#555753 !important;
}

#bd .forum table th {
border-color:#555753 !important;
}

.messages .msg {
background:#C7CBB1 !important;
}

#bd a:visited {
color:#606090 !important;
}

#bd .forum table tbody td a:visited {
color:#babdb6 !important;
}

#navPath a:visited {
color:#729FCF !important;
}

#whois_userpic, #bd .forum table tbody tr:hover {
background:#C7CBB1 !important;
}

.dw-feed-items {
background-color: #D9DAC6 !important;
}
.dw-feed-title-box {
color: #00000D !important;
background-color: #D9DAC6 !important;
}


#bd .forum table tbody tr td a:visited {
color: #606090 !important;
}


.code, .page-number, .news, .pre, pre, code , .code, pre code {
background-color: #C7CBB1 !important;
}

pre .shebang, pre .comment, pre .template_comment, pre .javadoc{
color: #95FF8F !important;
}

.infoblock, .boxlet, article, #bd .news, #tag-page-forum, #tag-page-news, #related-topics-list {
background-color: #C7CBB1 !important;
border-radius: 10px !important;
}

article {
padding: 15px !important;
}

#related-topics-list {
padding-top: 7px !important;
padding-bottom: 7px !important;
}

#tag-page-forum, #tag-page-news {
padding: 7px !important;
}

fieldset {
 border: 1px solid white !important;
 padding: 5px !important;
}

fieldset legend {
 padding-left: 5px !important;
 padding-right: 5px !important;
 color:#babdb6 !important;
}

/*#bd .nav li a, #bd .nav li a:visited {
    color: #3F77FF !important;
}*/

#bd .nav li a.current {
    color: black !important;
    background-color: #3F77FF;
}

.message-table .tag, .group-label {
background-color: #C7CBB1 !important;
}

#bd .forum table tbody tr:hover .tag, #related-topics-list .group-label, #tag-page-news .group-label , #tag-page-forum .group-label {
background-color: #B1B59A !important;

}

.btn-default, .btn-selected, #bd .nav li a {
color:#606090 !important;
}

.btn-default, #bd .nav li a, #bd .nav li a.current {
background-color: #C7CBB1 !important;
}

.btn-selected, .boxlet .btn-default, .infoblock .btn-default, .msg-container .btn, #bd .nav li a.current {
background-color: #B1B59A !important;
}

.btn-danger {
background-color: #784949 !important;
}

body #bd .btn-primary, body #bd .btn-danger {
color: #D9DAC6 !important;
}

body #bd .btn-primary {
background-color: #606090 !important;
}

.fav-buttons .selected [class^="icon-"] {
color: #3F77FF !important;
}
.user-remark {
color: #464D4F !important;
}
.stars {
color: #5BB705 !important;
}
.ui-widget-content, .ui-widget-content .ui-state-focus, .ui-state-focus {
background: none !important;
background-color: #C7CBB1 !important;
border: 1px solid #606090 !important;
}
pre .comment,pre .template_comment,pre .diff .header,pre .doctype,pre .pi,pre .lisp .string,pre .javadoc,pre .pragma {
color: #606090 !important;
font-style: italic !important;
}
pre .keyword,pre .winutils,pre .method,pre .addition,pre .request,pre .status,pre .nginx .title {
color: #859900 !important;
}
pre .number,pre .command,pre .string,pre .tag .value,pre .rules .value,pre .phpdoc,pre .tex .formula,pre .regexp,pre .hexcolor {
color: #20867E !important;
}
pre .title,pre .localvars,pre .chunk,pre .decorator,pre .built_in,pre .identifier,pre .vhdl .literal,pre .id,pre .css .function {
color: #268bd2 !important;
}
pre .attribute,pre .variable,pre .lisp .body,pre .smalltalk .number,pre .constant,pre .class .title,pre .parent,pre .haskell .type, pre .css .class {
color: #7C620F !important;
}
pre .preprocessor,pre .preprocessor .keyword,pre .shebang,pre .symbol,pre .symbol .string,pre .diff .change,pre .special,pre .attr_selector,pre .important,pre .subst,pre .cdata,pre .clojure .title,pre .css .pseudo {
color: #cb4b16 !important;
}
pre .preprocessor, pre .preprocessor .keyword, pre .symbol, pre .number, pre .variable, pre .vbscript, pre .literal {
color: #4D4C7D;
}
pre .keyword, pre .tag,  pre .ruby .function .keyword ,pre .css .tag {
color: #155AFF !important;
}
pre .deletion {
color: #dc322f !important;
}
pre .tex .formula {
background: #eee8d5 !important;
}

}
