	}
	*/

	div.content.clearfix:hover div.kd_ns_box:not(:hover),
	div.content.clearfix:hover td.col-1.col-first:not(:hover)
	{
		transition: 0.25s ease-in;
		-webkit-filter: saturate(0.1);
		filter: saturate(0.1);
		opacity: .75;		
	}

