css:
@-moz-document domain("jeuxvideo.com"), domain("forumjv.com") {
[id^="prospect"],
[id*="prospect"],
[id*="wbdds_insertion"],
[id^="wbdds_insertion"],
.part-boutique, 
.bonus-track, 
.bonus-sponso,
.mdd-card-suggest,
#jv-feedback .btn-feedback,
#cookies_notifier,
.bloc-menu-liste-machine,
#nuggadLayer {
  display:none !important;
}/*Supprime les pubs */
}

@-moz-document regexp("http://www.jeuxvideo.com/(?!profil)([^\"]*)") {
#content {
  	background:url(http://image.noelshack.com/fichiers/2014/50/1418128222-body-bg-jvshack.png) repeat-x 0 0 rgb(215, 215, 215);
}/* Change le fond du site */

.container-content {
	background: white !important;
}/* Changement de couleur du fond principal du site. */
}

@-moz-document url-prefix("http://www.jeuxvideo.com/profil/") {
#content {
	margin-top: 47px !important;
}
}

@-moz-document url("http://www.jeuxvideo.com/forums/0-50-0-1-0-1-0-blabla-15-18-ans.htm") {
#content {
  	background:none;
}/* Change le fond du site */
}

@-moz-document domain("jeuxvideo.com"), domain("forumjv.com") {
[class*="text-modo"],
[class^="text-modo"] {
	color: rgb(204, 0, 0) !important;
}/* Change la couleur des modos */

li > a.lien-jv > span,
.panel-jv-forum .titre-info-fofo,
.panel-jv-forum .bloc-info-forum .lien-jv, 
.panel-jv-forum .bloc-forums-preferes .lien-jv, 
.panel-jv-forum .bloc-sous-forums .lien-jv,
.panel-jv-forum .liste-modo-fofo,
.panel-jv-forum .bloc-modo-fofo {
  	color:black !important;
}/* Couleur des liens dans les forums préférés */

[class*="panel-jv-forum"]:hover,
[class*="panel-jv-forum"]:focus,
[class*="panel-jv-forum"]:active,
[class^="panel-jv-forum"]:hover,
[class^="panel-jv-forum"]:focus,
[class^="panel-jv-forum"]:active {
  color: rgb(255, 64, 0) !important;
}/* Change la couleur des différents liens au passage de la souris */
}

@-moz-document domain("jeuxvideo.com"), domain("forumjv.com") {
#navbar-jv {
	background: url(http://imagizer.imageshack.us/a/img537/2082/QxC19L.png) left top no-repeat !important;
	background-position: 0px 1px !important;
	width: 100% !important;
	text-align: center !important;
}/* Change le fond, et agrandissement du menu */

#navbar-jv .liste-rubrique {
	background: url(http://imagizer.imageshack.us/a/img633/8499/6plLuR.png) right top no-repeat !important;
	background-position: 381px 1px !important;
}/* Change le fond du menu */

#navbar-jv .liste-rubrique>li.open {
	background: none !important;
}/* Retire la couleur bleu à l'ouverture du lien */

#navbar-jv .content-menu-jv {
	border-top: none !important;
	border-bottom: none !important;
}/* Plus de bordure haute-basse */

.bloc-menu-navbar {
        border-bottom: none !important;
}

#content:not([style]) {
    padding-top: 46px !important;
}/* Espacement du menu */

#header-top {
	background: url(http://image.jeuxvideo.com/css_img/defaut/header/logo_bg.png) !important;
}/* Change le fond du header */

#header-logo:after {
	content: none !important;
}/* Retire le logo bêta */

#header-bottom, #header-bottom .bloc-sticker {
	position: relative !important;
	height: 0px !important;
}/* Retire le menu fixé en haut. */

#header-search-jv input[type="text"] {
	width: 210px !important;
}/* Agrandissement de la barre de recherche */

.affix #header-search-jv {
	left: 23px !important;
}/* Décale la barre de recherche */

.panel-jv-forum .bloc-forums-preferes .lien-jv {
	background: url(http://image.noelshack.com/fichiers/2015/01/1420292570-sommaire-puces-jvshack.png) no-repeat -3px 3px !important;
	padding-left: 7px !important;
}/* Rajoute une petite flèche dans la liste des topics préférés. */

.panel-jv-forum .liste-forums-preferes span.picto-suppression, .panel-jv-forum .liste-sujets-nomiss span.picto-suppression {
	margin-right: 16px !important;
}/* Remets à sa place le bouton pour supprimer un topic favoris etc.. */

.panel-jv-forum {
  	background: rgb(237,237,237) !important;
}/* Changement de couleur des menus forums préférés etc... */

.panel-jv-forum .panel-heading {
	font-size: 11px !important;
}/* On réduit la taille du titre des blocs sur le côté des topics */

.panel-jv-forum .liste-forums-preferes, 
.panel-jv-forum .liste-sujets-nomiss, 
.panel-jv-forum .liste-sous-forums {
	width: 200px !important;
}/* On agrandit les blocs */

.panel-jv-forum {
	width: 166px !important;
	font-size: 36px !important;
}/* Taille du texte et largeur des blocs changés. */

.panel-jv-forum:nth-child(4) {
	position: absolute !important;
	left: 757px !important;
	top: 0 !important;
	width: 200px !important;
}/* On prend le quatrième enfant du parent et on lui mets les mêmes attributs que le parent. */

.panel-jv-forum .panel-scroll-forum > .wrapper-forum-nicescroll {
    padding-right: 0 !important;
    overflow: visible !important;
}/* Rends visible les sous-forums */
	
.panel-jv-forum .liste-sous-forums li {
    padding: 0 !important;
}
.bloc-message-forum .bloc-date-msg {
	line-height: 34px !important;
}/* Mets au même niveau la date avec les boutons ddb etc.. */

.bloc-message-forum .bloc-options-msg span, .bloc-message-forum .bloc-options-msg a {
	margin-left: 5px !important;
}/*Décale la date */
}

@-moz-document domain("jeuxvideo.com"), domain("forumjv.com") {
.picto-msg-tronche {
	background: url("http://image.jeuxvideo.com/pics/messages_prives/ico_black_on.png") 1px 0 no-repeat !important;
}/* Bouton Blacklist "Tronche" :hap: */

.picto-msg-quote {
	background: url("http://image.noelshack.com/fichiers/2014/50/1418298773-quote-jvshack.png") 4px 1px no-repeat;
}/* Bouton citer */

.picto-msg-crayon {
	background: url("http://image.noelshack.com/fichiers/2014/50/1418296609-edit2-jvshack.gif") 3px 0 no-repeat !important;
	padding-left: 16px !important;
}/* Bouton éditer */

.picto-msg-exclam {
	background: url("http://image.jeuxvideo.com/pics/forums/bt_forum_avertirmod.gif") no-repeat !important;
}/* Bouton ddb */
.bloc-message-forum .bloc-pseudo-msg {
	background: url("http://image.jeuxvideo.com/pics/forums/bt_forum_profil.gif") no-repeat right 1px !important;
	padding-right: 15px !important;
}/* Petit icone verte à côté des pseudos. */
}

@-moz-document url-prefix("http://www.jeuxvideo.com/forums/"), url-prefix("http://www.jeuxvideo.com/recherche/"), url-prefix("http://www.forumjv.com/") {
*[src="/img/forums/topic-dossier1.png"] {
	height: 10px !important;
	width: 16px !important;
	padding-bottom: 13px !important;
	background:url(http://image.noelshack.com/fichiers/2014/50/1418128632-dossierjaune-jvshack.gif) !important;
}/* Change les dossiers jaunes des topics */

*[src="/img/forums/topic-dossier2.png"] {
	height: 10px !important;
	width: 16px !important;
	padding-bottom: 13px !important;
	background:url(http://image.noelshack.com/fichiers/2014/50/1418129151-dossierrouge-jvshack.gif) !important;
}/* Change les dossiers rouges des topics */

*[src="/img/forums/topic-marque-on.png"] {
	height: 10px !important;
	width: 16px !important;
	padding-bottom: 14px !important;
	background:url(http://image.noelshack.com/fichiers/2014/50/1418129272-marque-on-jvshack.gif) !important;
}/* Change l'épingle verte des topics */

*[src="/img/forums/topic-marque-off.png"] {
  	height: 10px !important;
	width: 16px !important;
	padding-bottom: 14px !important;
	background:url(http://image.noelshack.com/fichiers/2014/50/1418129274-marque-off-jvshack.gif) !important;
}/* Change l'épingle rouge des topics */

*[src="/img/forums/topic-lock.png"] {
  	height: 10px !important;
	width: 16px !important;
	padding-bottom: 14px !important;
	background:url(http://image.noelshack.com/fichiers/2014/50/1418303551-cadenas-jvshack.gif) !important;
}/* Change le cadenas des topics */

.btn {
  border: none !important;
}/* Retire les bordures des boutons */

.btn-poster-msg {
    background: url("http://image.jeuxvideo.com/pics/forums/bt_forum_poster.gif") !important;
	height: 17px !important;
	width: 80px !important;
}/* Changement du background du bouton poster */

.btn-topic-resolu {
  	background: url("http://image.noelshack.com/fichiers/2014/50/1418154045-rafraichir-jvshack.gif") !important;
	height: 17px !important;
	width: 80px !important;
}/* Changement de background du bouton résolu */

.btn-repondre-msg, .btn-repondre-msg:hover  {
    background: url("http://image.jeuxvideo.com/pics/forums/bt_forum_repondre.gif") !important;
	height: 17px !important;
	width: 80px !important;
}/* On garde le même background au passage de la souris */

div.bloc-pre-left > a > span, div.group-two > a:nth-child(1) > span, div.group-two > a:nth-child(1) > span:hover,div.bloc-pre-left > a > span:hover {
    background: url("http://image.jeuxvideo.com/pics/forums/bt_forum_nouveau_sujet.gif") !important;
	height: 17px !important;
	width: 140px !important;
}/* On garde le même background au passage de la souris */

div.group-two > a:nth-child(2) > span, div.group-two > a:nth-child(2) > span:hover {
  	background: url("http://image.jeuxvideo.com/pics/forums/bt_forum_liste_sujets.gif") !important;
	height: 17px !important;
	width: 140px !important;
}/* On garde le même background au passage de la souris */

div.bloc-pre-right > button, div.bloc-pre-right > button, div.bloc-pre-pagi-forum > div.bloc-pre-right > a > span {
    background: url("http://image.jeuxvideo.com/pics/forums/bt_forum_rafraichir.gif") !important;
	height: 17px !important;
	width: 80px !important;
}/* Changement du background du bouton rafraichir */

div.bloc-pre-left > div.group-one > span, div.bloc-pre-left > div.group-one > a > span, div.row > div:nth-child(2) > button, div.group-two > a:nth-child(2) > span, div.group-two > a:nth-child(2) > span:hover,div.bloc-pre-left > a > span, div.group-two > a:nth-child(1) > span, div.group-two > a:nth-child(1) > span:hover,div.bloc-pre-left > a > span:hover,div.bloc-pre-right > button, div.bloc-pre-right > button, div.bloc-pre-pagi-forum > div.bloc-pre-right > a > span {
	text-transform: none;
	font-weight: normal;
	padding: 0;
	font-size: 0;
	height: 0;
	min-width: 0;
	line-height: 0;
	border-radius: 0;
	text-shadow: none;
}/* On retire la transformation, le style, la taille du texte mais aussi les marges, la hauteur et la hauteur minimale, la hauteur du texte, et les bords des textes. :hap: */
}

@-moz-document url-prefix("http://www.jeuxvideo.com/forums/"), url-prefix("http://www.jeuxvideo.com/recherche/"), url-prefix("http://www.forumjv.com/") {
.titre-head-bloc-forum {
    border-top:none !important;
}/* Plus de bordure haute. */

.titre-head-bloc .titre-bloc {
	background: rgb(153, 204, 0) url(http://image.jeuxvideo.com/css_img/defaut/bloc_forum_h3.png) right top no-repeat !important;
  	color:white !important;
  	width: 619px !important;
  	padding: 5px !important;
}/* Couleur du texte pour le titre du forum */

#forum-main-col {
	float: right !important;
}/* On fait flotter le bloc des topics à droite */

#forum-right-col {
	width: 179px !important;
}/* On agrandis le bloc de droite */

#forum-right-col .panel-jv-forum:not(:nth-child(5)) > .panel-heading,
#forum-right-col .panel-jv-forum:nth-child(5) > .panel-heading {
	border-radius: 0px 5px 0px 0px;
}/* Ajout de bords arrondis haut-droit */

#forum-right-col .panel-jv-forum:not(:nth-child(5)) > .panel-heading  {
	background: #0DA0CA linear-gradient(#0EBBED 0px, #0DA0CA 50%, #0382A6 50%, #0382A6 100%);
}/* Ajout du background bleu dans le sommaire gauche */

#forum-right-col .panel-jv-forum:nth-child(5) > .panel-heading {
	background: #0DA0CA linear-gradient(#34495e 0px, #34495e 50%, #2c3e50 50%, #2c3e50 100%);
}/* Ajout du background bleu dans le sommaire droit */

.titre-head-bloc-forum .titre-bloc-forum {
  	color:black !important;
}/* Couleur des blocs */

.titre-head-bloc-forum .titre-bloc-forum {
  	background:none !important;
  	text-align: center !important;
}/* On retire le fond et on aligne le texte au milieu */

#table-liste-topic-forum thead {
	background: #999 !important;
}/* Changement du fond de la partie haute du forum */

#table-liste-topic-forum thead th {
	text-align: center !important;
	color: #FFF;
}/* Texte centré, changement de couleur */


#forum-right-col > div:nth-child(4),
#forum-right-col > div:nth-child(5) {
	position: absolute;
	left: 758px !important;
	top: 62px !important;
	width: 200px !important;
}/* Même chose qu'au dessus */

#forum-right-col > div:nth-child(4),
#forum-right-col > div:nth-child(5) {
	top: 0px !important;
}/* Plus de hauteur haute. ( C'est pas très français mais osela) */

.bloc-liste-num-page a:visited {
	color:silver !important;
}/*Dans la liste des pages sur un topic, les pages déjà visités deviennent de couleur grise. */

#forum-main-col {
	float: left !important;
	width: 574px !important;
	margin-top: -11px;
}/* Partie très importante pour ce skin. :noel: / On mets les blocs sur le côté des topics */

.fil-ariane-crumb h1:after {
	content:" - SKIN BY GUKLAM";
	color:black;
}/* Pseudo après le fil d'ariane. */

#forum-right-col div:nth-child(5) {
	position: absolute !important;
	left: 757px !important;
	top: 0 !important;
	width: 200px !important;
}/* Décale le menu forums préférés qui était buggé et qui se mettait sous le bloc info */

html body #forum-right-col > div:nth-child(1),
html body #forum-right-col > div:nth-child(2),
html body #forum-right-col > div:nth-child(3),
html body #forum-right-col > div:nth-child(4),
html body #forum-right-col > div:nth-child(5),
html body #forum-right-col > div{
    position: static!important;
    width: 100%!important;
}
.panel-jv-forum .liste-forums-preferes, .panel-jv-forum .liste-sujets-nomiss, .panel-jv-forum .liste-sous-forums{
    width: auto!important;
}
html body #forum-right-col{
    width: 386px!important;
    max-width: none;
}
.panel-jv-forum .liste-forums-preferes li, .panel-jv-forum .liste-sujets-nomiss li{
    color: black;
}
.btn.btn-poster-msg{
    font-size: 0;
}
}
