css:
@-moz-document domain(workflowy.com) {

span.contentTag {
    /* Hide the # and @ symbol preceeding tags */
    
    color: transparent !important
}
.contentTag[title="Filter #Now"] span,
.contentTag[title="Filter #Next"] span,
.contentTag[title="Filter #Soon"] span,
.contentTag[title="Filter #Later"] span,
.contentTag[title="Filter #Someday"] span,
.contentTag[title="Filter #ThisWeek"] span,
.contentTag[title="Filter #NextWeek"] span,
.contentTag[title="Filter #ThisMonth"] span,
.contentTag[title="Filter #NextMonth"] span,
.contentTag[title="Filter #ThisYear"] span,
.contentTag[title="Filter #NextYear"] span,
.contentTag[title="Filter #Monday"] span,
.contentTag[title="Filter #Tuesday"] span,
.contentTag[title="Filter #Wednesday"] span,
.contentTag[title="Filter #Thursday"] span,
.contentTag[title="Filter #Friday"] span,
.contentTag[title="Filter #Saturday"] span,
.contentTag[title="Filter #Sunday"] span {
    /* Customise calendar keyword tags */
    
    width: 90% !important;
    background: #eee !important;
    color: #555 !important;
    font-weight: bold
}


/* Customise keyword tags */
.contentTag[title="Filter #Important"] span, .contentTag[title="Filter #important"] span { background: red !important; }
.contentTag[title="Filter #Due"] span, .contentTag[title="Filter #due"] span { background: orange !important; }
.contentTag[title="Filter #Event"] span, .contentTag[title="Filter #event"] span { background: lightGreen !important; }
.contentTag[title="Filter #Note"] span, .contentTag[title="Filter #note"] span { background: yellow !important; color: black !important; }



.content .contentTag, .nameAnimated .contentTag {
    /* Fix from better workflowys tags */
	left: initial !important;
    margin-right: initial !important;
    position: initial !important;
}

}
