css:
@-moz-document domain("erlang.org") {
body {
    font-family: Roboto, Helvetica, Arial, sans-serif !important;
    font-size: 1em !important;
    font-size-adjust: none !important;
    font-style: normal !important;
    font-variant: normal !important;
    font-weight: normal !important;
    line-height: 1.1 !important;
    color: #222 !important;
}
p {
    font-family: Roboto, Helvetica, Arial, sans-serif !important;
    margin: 0em 0px 0em 0px !important;
    color: #444 !important;
    line-height: 130% !important;
    padding: 0;
}
h1,
h2,
h3,
h4,
h5,
h6 {
    color: #333 !important;
    font-weight: normal !important;
}
.example{
	background: #122 !important;
	border-radius: 0.5em;
	border:1px solid #111;
}
span.bold_code {
    padding-top: 0.2em !important;
    font-family: "SF Mono", "Menlo", "Consolas", "Andale Mono", monospace !important;
    font-weight: bold !important;
    color: darkslateblue !important;
    margin-bottom: 0 !important;
    line-height: 1.4em !important;
}
.code {
    font-family: "SF Mono", "Menlo", "Consolas", "Andale Mono", monospace !important;
    color: royalblue !important;
    font-weight: bold !important;
}
pre code {
    background: none;
    border: none;
    font-family: "SF Mono", "Menlo", "Consolas", "Andale Mono", monospace !important;
}
pre {
    margin: 10px 0px !important;
    padding: 5px !important;
    margin-left: 10px !important;
    color: #ddd !important;
    font-size: 16px !important;
    line-height: normal !important;
    overflow: auto !important;
    font-family: "SF Mono", "Menlo", "Consolas", "Andale Mono", monospace !important;
}
a {
    color: #B22222 !important;
}
a:visited {
    color: #CD5C5C !important;
    border-bottom: 1px dotted #CD5C5C !important;
    text-decoration: none !important;
}
h1 {
    display: inline !important;
    font-size: 5em !important;
    font-weight: bold !important;
}
h2 {
    margin-top: 0px !important;
    font-size: 17px !important;
    font-weight: normal !important;
}
.flipMenu {
    font-family: Roboto, "HelveticaNeue", "Helvetica", 'Open Sans', Arial, sans-serif !important;
    font-style: normal;
    font-weight: normal;
    font-size: 1.0em;
    line-height: 1.4em;
}
.flipFolderClosed,
.flipFolderOpen {
    font-weight: bold !important;
    list-style-image: none !important;
    margin-left: -2.1em !important;
}
li.flipStatic {
    font-family: Roboto, HelveticaNeue;
    font-size: 10pt;
    font-weight: normal !important;
    list-style-image: none !important;
    margin-left: -1.7em !important;
    line-height: 90% !important;
    padding-left: -1.2em;
}
#leftnav {
    width: 230px !important;
    border-right: 1px dotted silver !important;
}
#content{
	margin-left: 240px;
}
.note{
	border-radius: 1em;
	overflow: hidden;
}
.note .label{
	background: royalblue;
}
}
