css:
@-moz-document url-prefix("https://newbinusmaya.binus.ac.id/") {
.background
{

background: url("https://lh4.ggpht.com/WmZvdsJEjByXx2HHRFGhdg0fbbI61LqmVimH9vJAU5lyGinj31e9PshnDxoIeV8NdA=h900") #1C1C1C bottom fixed no-repeat !important;
    background-size: cover !important;
}

img#LogoUniversity, div.ribbon, span.site-description, img#LogoSite
{
    
height: 0px !important;
padding:90px!important;
width: 400px!important;
position: absolute!important;
top: 220px!important;
left: 40%!important;
z-index: -1!important;
margin-left: -18px!important;}


div.logo
{
	width: 875px!important;
	height: 100px !important;
 background: url("http://i.imgur.com/X9bh0G5.jpg") no-repeat !important;   
}


div.logo-binusmaya
{
	background: url("http://i.imgur.com/WuS7ICX.png") no-repeat !important;
}

div.login-body
{
	background-color:#d4ebf2 !important;
}

div.login-footer, span.item
{
	background-color:#4eadc9 !important;
	
	color: #000000 !important;
}

div.slide-wrap
{
	height: 450px !important;
}

div.login.login-binusmaya
{
	top: 50%;
	
}
}

@-moz-document url-prefix("https://fs.binusian.org/adfs/") {
div#page.main-container, section.wrapper
{
	background: url("https://lh4.ggpht.com/WmZvdsJEjByXx2HHRFGhdg0fbbI61LqmVimH9vJAU5lyGinj31e9PshnDxoIeV8NdA=h900") #1C1C1C bottom fixed no-repeat !important;
    background-size: cover !important;
}

div.login
{
	background-color:#d4ebf2 !important;
}
div.login-head
{
 background: url("http://i.imgur.com/X9bh0G5.jpg") no-repeat !important;
background-color:#d8d8d8 !important;
}

div.ribbon, div.logo
{
    visibility: hidden!important;
}
}
